css:
@-moz-document domain("tweetdeck.twitter.com") {
/*=== 選択領域の文字色と背景色 (TODO: ダークテーマ用を別途用意) ===*/
.app-columns ::selection {background:black; color: white;}
.app-columns ::-moz-selection {background:black; color: white;}

/*=== 引用サイトのタイトル行短縮の無効化 ===*/
div[style*="display: -webkit-box"] {display: block !important; max-height: none !important;}

}
