@-moz-document domain("4chan.org") {
/* Source: https://userstyles.org/styles/124929/dark4chan */

	* { font: 10pt "Calibri"; }

/* scrollbar */
::-webkit-scrollbar {
    width: 12px;
    height: 12px;
}
::-webkit-scrollbar-thumb {
    background: rgba(84, 204, 102, 0.4);
    -webkit-box-shadow: inset 1px 1px 2px rgba(0, 0, 0, 0.2);
}
::-webkit-scrollbar-track {
    background: #2c2c2c;
    -webkit-box-shadow: inset 1px 1px 2px rgba(0, 0, 0, 0.1);
}
::-webkit-scrollbar-corner {
    background-color: #3a3939;
}

/* Header */
body.hasDropDownNav {
    margin-top: 0;
}
div#boardNavDesktop {
    position: fixed;
    top: 0px;
    left: 0px;
    right: 0px;
    z-index: 9001;
}
.boardList {
    width: calc(100% - 110px);
    display: inline-block;
    color: transparent;
}
.boardList a {
    position: relative;
    z-index: 9002;
    font-size: 9pt!important;
}
.boardList a:after {
    content: "Â·";
    margin-right: -10px;
    padding-left: 2px;
    cursor: pointer;
    display: inline-block;
    color: #747474!important;
    pointer-events: none!important;
}
.boardList a:last-child:after {
    display: none;
}
#navtopright,
#navbotright {
    display: inline-block;
    color: transparent;
}
#boardNavMobile .boardSelect:hover .custom-menu-ctrl {
    color: transparent;
}
#boardNavMobile .boardSelect:hover .custom-menu-ctrl a {
    -webkit-appearance: none;
    background: #444a81;
    padding: 1px 4px!important;
    color: #fff!important;
    font-size: 10px;
    text-align: center;
    border-radius: 2px;
    display: inline-block;
    cursor: pointer;
    outline: 0;
    border: 0;
    letter-spacing: .5px;
    font-weight: bold;
}
#boardNavMobile .boardSelect:hover .custom-menu-ctrl a:hover {
    -webkit-appearance: none;
    background-color: #393939!important;
    cursor: pointer;
    color: #d1d1d1!important;
}
.persistentNav,
div#boardNavMobile,
div#boardNavDesktop,
:root.fixed:not(.centered-links) #header-bar {
    padding: 6px!important;
    color: #ccc!important;
    background-color: #202020!important;
    box-shadow: 0px 1px 3px 1px rgba(0, 0, 0, 0.3)!important;
    border-top: 3px solid #444a81!important;
    border-bottom: none!important;
    font-size: 9pt!important;
}
#custom-board-list {
    color: #747474!important;
}

/******************************/
