css:
/**********************************/
/* DEG Metro Stars   v1.0.1 skin  */
/* Date: 20/03/2009               */
/* Made by: Gizzly                */
/**********************************/

@-moz-document domain("hattrick.org") {

#top
{
	height: 0px !important;
	position: relative !important;
	z-index: 50 !important;
        background: #000000 !important;
}
#footer a
{
        color: #ffea00 !important;
        font-weight: bold !important;
}
#ticker
{
	position: absolute !important;
        width: 350px !important;
	top: 30px !important;
	left: 360px !important;
}
#ticker div a
{
	padding-bottom: 2px !important;
	color: black !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}
#shortcuts
{
	margin-top: 20px !important;
	margin-left:0px !important;
}
#teamLinks
{
        color: #ffffff !important;
        margin-top: 15px !important;
	margin-left: 120px !important;
}
div#teamLinks a
{
        color: black !important;
}
/*--Users online - week - date - time ----------------------------*/
div[id="online"] {color: black !important; margin-top: 15px !important;}
div[id="time"] {color: black !important; margin-top: 15px !important;}

/*--Page background-----------------------------------------------*/
body {background: #D11E1A !important;}

/*----centre title-------("Offizielle Hattrick-Mitteilungen")--------------*/
h1 
{
        color: #D11E1A !important;
        border-bottom: solid thin #D11E1A!important;
}
/*-----all boxHead--------------------*/
h2 
{
        color: black !important;
        background: url(http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/DEGMetroStars/box.jpg) !important;
}
/*-----centre news--------------------*/
.boxBody h2 
{ 
        color: #D11E1A !important;
        background: none !important;
        border-bottom: solid 1px #D11E1A !important;
}
/*-----------boxHeads----------------------------------------------------------------------------------------*/
/*Left menu*/
.subMenuBox .boxHead
{
        color: black !important;
	background: url(http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/DEGMetroStars/box.jpg) !important;
}
.subMenuBox .boxHead .boxLeft
{
        color: black !important;
        background: url(http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/DEGMetroStars/box.jpg) !important;
}
/*Centre box*/
.main .boxHead h2 
{
        color: black !important;
	background: url(http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/DEGMetroStars/box.jpg) !important;
}
/*Right menu*/
.sidebarBox h2 
{
        color: black !important;
	background: url(http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/DEGMetroStars/box.jpg) !important;
}
/*----- Press, GB, Team events ------------------------------------------------------------------------------*/
.main .mainBox h2 
{
	border-bottom: solid 1px #D11E1A !important;
	color: #D11E1A !important;
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;
}
/*Central header paths*/
h2 a 
{
        color: black !important;
}
h2 a:hover 
{
        color: #D11E1A !important;
}
/*News and team events date*/
.date
{
	color: #808080 !important;
	padding-right: 8px;
	font-weight: 400;
}
/*Left Menu titles*/
h3 
{
        color: #D11E1A !important;
        border-bottom: solid thin #D11E1A !important;
}
/*General Links*/
a
{
        color: #D11E1A !important;
	text-decoration: none !important;
}
/*----subMenu and sidebar links------------------------------------------------------------------------------*/
.subMenuBox a, .subMenuBox a
{
        text-decoration: none !important;
	color: black !important;
}
.sidebarBox a 
{
        text-decoration: underline !important;
	color: black !important;
}
/*Bot teams link*/
div a[class="shy"] 
{
        color: gray !important;
}
/*Hide Hattrick logo*/
#logo
{
        background: none !important;
}
/*Hide ticker's stars*/
#ticker div
{
        /*background: none !important;*/
}
/* central page background */
#content
{
        background-image: none !important;
        background-color: #ffea00 !important;
}
/*Image header (banner)*/
#header 
{
        background: url(http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/DEGMetroStars/header-2.jpg) !important;
	height: 102px !important;
	padding-top: 40px !important;
}
/*--------Top menu settings----------------------------------------------------------------------------------*/
#menu
{
	height: 20px !important;
	margin-top: 5px !important;
        background: none !important;
}
#menu a
{
	line-height: 20px !important;  
        color: black !important;
}
div#menu a:hover 
{
        color: #ffea00 !important; 
        background: #D11E1A !important;
}
a:hover
{
	color: black !important;
}
/*bottom white line*/
div[id="bottom"]
{
        background: #ffea00 !important;
}

/*---Aufstellung---------------------------------------------------------*/
div[class="name"] a       {color: #000000 !important;}
div[class="name"] a:hover {color: #D11E1A !important;}

/*---Forum---------------------------------------------------------------*/
div[class="boxBody"]   {background: white !important;}
div[class="message"] a {color: #D11E1A !important;}
div[class="signature"] a {color: #D11E1A !important;}

#cfPollHeader
{
	background: black !important;
	padding: 5px 10px;
	font-weight: bold;
        color: #ffffff;
}
.cfWrapper
{
	border: solid 1px #c70000 !important;
	margin: 0 0 12px 0;
}
.cfWrapper a
{
        color: #D11E1A !important;
}
.cfUserInfo a
{
        color: #D11E1A !important;
}
.folderHeader a:link 
{
	color: #D11E1A !important;
	font-weight: bold !important;
}
.cfFooter a
{ 
        color: black !important;
}
.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore:hover
{

}
/*Ads section*/
div[class="insertHeader"]
{

}
div[class="insertFooter"]
{
        background: #ffffff !important;
}
/*-------------Formatting buttons----------------------------------------------------------------------------*/



/*shortcuts*/
img.scNewMail 
{

}
}
