css:
@-moz-document domain("moodle.uowplatform.edu.au") {
.block .header h2 {
    color: #CCC;
    text-shadow: 0 1px 0 rgba(0, 0, 0, .5);
}
.block,
.topbar,
.coursebox {
    transition: all 0.3s cubic-bezier(.25, .8, .25, 1);
    box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
}
.block:hover,
.topbar:hover,
.coursebox:hover {
    box-shadow: 0 10px 28px rgba(0, 0, 0, 0.25), 0 6px 10px rgba(0, 0, 0, 0.22);
}
.block {
    border: 0px;
}
.navbar-inner {
    border: 0px;
}
.navbar .btn-navbar {
    background-color: #3f3f3f !important;
    background-image: linear-gradient(to bottom, #444, #333) !important;
    float: left;
}
.navbar .btn-navbar:hover,
.navbar .btn-navbar:focus,
.navbar .btn-navbar:active,
.navbar .btn-navbar.active,
.navbar .btn-navbar.disabled,
.navbar .btn-navbar[disabled] {
    background-color: #333 !important;
}
.topbar {
    background: #024079 !important;
    border-radius: 4px;
    margin-bottom: 12px !important;
    width: calc(100% - 10px) !important;
}
.coursebox {
    border: 1px solid #444;
    background-color: #373737;
}
.navbar .nav > li > a {
    text-shadow: 0 1px 0 #000 !important;
}
#page-footer .footer-right {
    color: #CCC;
}
a:visited {
    color: #1A99CC;
    text-decoration: none;
}
body {
    background-color: #444;
    color: #c0c0c0;
}
.breadcrumb {
    background-color: #333333;
}
.breadcrumb > li {
    text-shadow: none;
}
input,
input[type="text"],
input[type="password"] {
    background-color: #333333;
    color: #c0c0c0;
}
input[type="submit"] {
    background-color: #333333;
    color: #c0c0c0;
    background-image: none;
    text-shadow: none;
}
.navbar-inner {
    background-color: #2A2A2A;
    background-image: none;
}
.navbar .brand {
    text-shadow: none;
}
.block {
    background-color: #333333;
}
.block {
    background-color: #333333;
}
.courses .coursebox,
.courses .coursebox.even {
    background-color: #333333;
}
.courses .coursebox:hover,
.courses .coursebox.even:hover {
    background-color: #4c4c4c;
}
a {
    color: #0070a8;
}
a:hover {
    color: #0092db;
}
.text_to_html > p:nth-child(8) > span:nth-child(2) {
    color: #c0c0c0;
}
.course-content .current {
    background-color: #4c4c4c;
}
.generaltable tbody > tr:nth-child(2n+1) > td {
    background-color: #404040;
}
.generaltable tbody tr:hover > td {
    background-color: #4c4c4c;
}
select {
    background-color: #333333;
    color: #c0c0c0;
}
.path-mod-assign .gradingtable tr.unselectedrow td {
    background-color: #404040;
}
#fitem_id_submitbutton {
    background-color: #404040;
}
.path-mod-assign .gradingtable tr.unselectedrow td a {
    color: #0092db;
}
.path-mod-assign .gradingtable tr.unselectedrow td a:hover {
    color: #29b8ff;
}
.moodle-actionmenu.show[data-enhanced] .menu {
    background-color: #404040;
}
.moodle-actionmenu.show[data-enhanced] .menu a {
    color: #c0c0c0;
}
textarea {
    background-color: #333333;
    color: #c0c0c0;
}
.forumpost {
    background-color: #404040;
}
div.editor_atto_toolbar,
div.editor_atto_toolbar div.atto_group {
    background-color: #404040;
}
.editor_atto_content_wrap {
    background-color: #333333;
    color: #c0c0c0;
}
.filemanager .fp-navbar,
.fp-viewbar,
.fp-toolbar .fp-btn-add,
.filemanager-container {
    background-color: #404040;
}
.dndupload-arrow {
    filter: invert(75%);
}
.filemanager-container .dndupload-target {
    background-color: #333333;
}
.filemanager-container {
    background: #333333;
}
#fgroup_id_buttonar {
    background-color: #404040;
}
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd,
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd.yui3-widget-hd {
    background: #333333;
    color: #c0c0c0;
    text-shadow: none;
}
.moodle-dialogue-base .moodle-dialogue-wrap {
    background-color: #333333;
}
.file-picker a {
    color: #c0c0c0;
}
.file-picker .fp-list .fp-repo.active {
    background-color: #404040;
    color: #c0c0c0;
}
.file-picker .fp-navbar {
    background-color: #333333;
}
.file-picker .fp-content,
.fp-iconview .fp-filename-field .fp-filename {
    background-color: #404040;
}
.fp-iconview .fp-filename-field .fp-filename:hover {
    color: #e6e6e6;
}
.yui3-skin-sam .yui3-datatable-header {
    background-color: #404040;
    color: #c0c0c0;
    text-shadow: none;
}
.moodle-dialogue-base .moodle-dialogue .moodle-dialogue-bd {
    color: #e6e6e6;
}
.yui3-skin-sam .yui3-datatable-even .yui3-datatable-cell {
    background-color: #404040;
}
.yui3-skin-sam .yui3-datatable-odd .yui3-datatable-cell {
    background-color: #333333;
}
.fp-content-center form + div {
    background-color: #404040;
}
}
