css:
/* @namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul); */
@-moz-document regexp("^.*adnmb\\d?\\.com/(Forum|f|t).*$") {
/* 滚动条相关 */
::-webkit-scrollbar {
    width: 10px !important;
}
::-webkit-scrollbar-track {
    box-shadow: inset 0 0 5px #444 !important; 
    border-radius: 10px !important;
}
::-webkit-scrollbar-thumb{
    background: #555 !important; 
    border-radius: 2px !important;
}

::-webkit-file-upload-button{
	background-color: #444 !important;
	color: #ccc !important;
	border: none !important;
	padding: 2px 6px !important;
	height: 26px !important;
	border-radius: 3px !important;
}

/* 背景色 */
html, body, #h-menu{
	background-color: #111 !important;
}

/* Footer */
#h-bottom-nav{
	background-color: #222 !important;
}
#h-bottom-nav div{
	color: #aaa !important;
}

/* 侧边 */
.uk-nav-sub li{
	display: block !important;
	background-color: #333 !important;
	padding: 2px 6px !important;
	margin-bottom: 4px !important;
	box-shadow: 0px 3px 0px 0px #000 !important;
	border-radius: 3px 0px 0px 3px !important;
}

.h-active a{
	color: #8d9 !important;
}

/* 正文 */
.h-title, .h-threads-content, .h-forum-header{
	color: #ccc !important;
}

/* 分割线 */
hr{
	display: none !important;
}

/* 链接 */
a, label, input[type="file"]{
	color: #798 !important;
}
a:hover, .uk-icon-refresh:hover{
	text-decoration: none !important;
	color: #df7 !important;
}

/* 输入框 */
input[type="text"]{
	height: 20px !important;
}
input[type="text"], #h-post-form textarea{
	background-color: #222 !important;
	color: #ccc !important;
	border: 2px #444 solid !important;
	border-radius: 3px !important;
}

/* 按钮 */
input[type="submit"]{
	background-color: #444 !important;
	color: #ccc !important;
	border: none !important;
	height: 24px !important;
	padding: 2px 6px !important;
	border-radius: 3px !important;
}

/* 下拉框 */
select, .h-emot-select{
	background-color: #222 !important;
	color: #ccc !important;
	border: 2px #444 solid !important;
	height: 26px !important;
	border-radius: 3px !important;
	width: 100% !important;
}

/* 发送 */
input[type="submit"]:hover{
	cursor: pointer !important;
	background-color: #666 !important;
	color: #ddd !important;
}

/* 链接 */
.h-threads-info-title{
	color: #8a9 !important;
}

/* 用户名 */
.h-threads-info-email{
	color: #9a8 !important;
}

/* 时间戳等 */
span, .h-threads-info-createdat, .h-threads-info{
	color: #aaa !important;
}

/* 带底色的正文 */
.h-threads-item-reply, .h-threads-item-reply-main, .h-threads-item{
	background-color: #444 !important;
	padding: 5px !important;
	margin-top: 14px !important;
}

/* 边框 */
.h-threads-item-reply{
	border-radius: 3px !important;
	box-shadow: 0px 3px 0px 0px #222 !important;
}

/* 串 */
.h-threads-item{
	background-color: #333 !important;
	padding: 10px !important;
	border-radius: 6px !important;
	box-shadow: 0px 5px 0px 0px #000 !important;
}

/* 红色醒目 */
font[color="red"], font[style="color:#f00"], font[color="#CC0000"], strong[style="color:red"]{
	color: #e88 !important;
}

.uk-nav-header{
	color: #9ba !important;
}

/* 绿色 */
font[color="green"]{
	color: #8a8 !important;
}

/* 绿色 */
font[color="#789922"]{
	color: #9b9 !important;
}

/* 隐藏字 */
font[style="color:#fff"]{
	color: #444 !important;
}

/* 头衔 */
sub[style="color:#444"]{
	background-color: #444 !important;
	color: #888 !important;
	padding: 2px !important;
	margin-left: 5px !important;
	border-radius: 2px !important;
	line-height: 12px !important;
	position: relative !important;
	top: 0 !important;
}

/* PO主标识 */
.uk-text-primary{
	color: #8a9 !important;
}

.uk-text-danger{
	color: #777 !important;
}

.h-post-form-title{
	background: none !important;
	color:#aaa !important;
	line-height: 25px !important;
}

img{
	margin: 10px !important;
}

/* 版规图片底色，AC娘是黑的，没底色看不清 */
.h-forum-header img{
	background-color: #aaa !important;
}

div[style="text-align:left;font-size:12px;color:#CC0000;margin:10px auto 0px;"]{
	color: #ccc !important;
}

div[style="text-align:center;color:#CC0000;margin:10px auto 0px;"] img{
	background-color: white !important;
}

.uk-button-success{
	background-color: #587 !important;
	color: #acb !important;
}

.h-nav-parent-header:hover{
	text-shadow: none !important;
}

.h-tool-btn, .uk-icon-refresh{
	color: #465 !important;
	background-color: #798 !important;
}

.uk-pagination li span, .uk-pagination li a{
	background-color: #354 !important;
	text-shadow: none !important;
	color: #7a9 !important;
}

.uk-pagination .uk-active span{
	background-color: #476 !important;
	text-shadow: none !important;
	color: #7a9 !important;
}

.uk-pagination .uk-disabled span{
	background-color: #444 !important;
	text-shadow: none !important;
	color: #7a9 !important;
}

.h-post-form-input{
	color: #7a9 !important;
}

.h-threads-item-reply-icon{
	color: #576 !important;
}

/* 动画相关 */
.h-threads-item-reply:hover, .h-threads-item-reply-main:hover{
	transition: background .2s linear, box-shadow .2s linear;
    background: #555 !important;
    box-shadow: #333 !important;
}

.h-threads-item-reply, .h-threads-item-reply-main{
	transition: background .2s linear, box-shadow .2s linear;
}
}
