css:
@-moz-document domain("rockpapershotgun.com") {
.breaking {
  background-color: #c1272c;
}

.ads-enabled .page, .page {
  width: auto;
}
#page {
  max-width: 760px;
}

.ads-enabled #page {
  max-width: 1124px;
}

a.logo img,
.login h1 a {
  filter: sepia() saturate(10000%) hue-rotate(40deg);
}
body {
  background-color: #1d1d1d;
}
.header-desktop,
.footer {
  background-color: #1d1d1d;
  color: #999;
  border: 0;
}
.header-desktop .primary a,
.header-desktop .secondary a {
  color: #999;
}
.footer .section-title,
.account {
  color: #999;
}
.account .actions {
  text-align: right;
}
.spotlight-container {
  display: none;
}
#page-wrapper .container,
#page-wrapper [class^=container-] {
  margin: 0 auto 0 0;
  max-width: 760px;
}
.above > [class^=container-] {
  padding-right: 1.5rem;
  border-right: 1px solid #cecece;
}
.header-desktop .support-us .button,
.alert {
  color: #c1272c;
  background-color: transparent;
  border: 1px solid #c1272c;
}
a,
.blog-post a,
.blog-post .tags a,
.blog-post.feature a,
.blog-post p.label {
  color: #c1272c;
}
.blog-post main a {
  font-weight: bold;
}
.title a {
  color: #2d2d2d;
}
.title a:hover {
  text-decoration: none;
}
a:hover {
  color: #82a8c8 !important;
}
.button {
  color: white;
  background-color: #c1272c;
}
.button.outline,
.have-you-played-widget .button.outline,
.button outline personalised {
  color: #2d2d2d;
  border-color: #2d2d2d;
}
.blog-post .button:not(.outline),
.blog-post.feature .button:not(.outline),
.blog-post.premium .button:not(.outline),
.cookie-gdpr .button.personalised {
  color: #c1272c;
  background-color: transparent;
  padding: 0;
}
.blog-post .button:not(.outline):after {
  content: ' Â»'
}
.spotlight-item .details {
  position: absolute;
  left: 0;
  right: 1rem;
  background: none;
  min-height: 5rem;
  bottom: 0;
  padding: 0.25rem 1rem;
}
.spotlight-item .details .label,
.spotlight-item .details .comment-count {
  display: none;
}
.spotlight-item p {
  display: inline;
  background-color: #c1272c;
  color: white;
  box-shadow: 3px 3px 0px rgba(0, 0, 0, 0.8);
  line-height: 2rem;
  -webkit-box-decoration-break: clone;
  font-size: 1rem;
}
.spotlight-item .title {
  font-size: 1rem;
  padding: 3px 6px;
}
.blog-post .icon {
  display: none;
}
.blog-post .tags:after,
.blog-post.feature .tags:after {
  width: 100%;
  border: 0;
}
.blog-post.premium .label,
.blog-post.premium a {
  color: #c1272c;
}
.blog-post {
  margin: 2rem 0 0 0;
  padding: 2rem 0 0 0;
  border-top: 1px solid #cecece;
}
.blog-post.feature {
  border-color: #cecece;
}
.blog-post .excerpt {
  font-family: Roboto, Helvetica, Arial, sans-serif;
}
article header {
  display: flex;
  flex-wrap: wrap;
}
article header div {
  width: 100%;
}
article header div:nth-child(1) {
  order: 2;
}
article header div:nth-child(2) {
  order: 1;
  padding-right: 2rem;
}
#right-rail .section-title {
  color: #c1272c;
}
}
