@-moz-document domain("youtube.com") {
/* General Parts */
.ytp-chrome-bottom {
	bottom: -21px !important;
	padding-left: 25px !important;
	margin: 0px -37px !important;
}

.ytp-chrome-top,
.ytp-chrome-bottom {
    text-shadow: 0 0 0px rgba(0, 0, 0, .5) !important;
}

.ytp-chrome-controls {
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAdCAYAAABrAQZpAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAABIAAAASABGyWs+AAAACXZwQWcAAAABAAAAHQAh4gKwAAAAMUlEQVQI12OQlJD4x2Sgr8/I9OTZs5dM//79Y2T6//8/IyqLAcLClEBjYahDKCHSKADN5VNQpdTxzgAAAABJRU5ErkJggg==) !important;
    height: 25px !important;
}

.ytp-right-controls {
    height: 100% !important;
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAdCAYAAABrAQZpAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAABIAAAASABGyWs+AAAACXZwQWcAAAABAAAAHQAh4gKwAAAAMUlEQVQI12OQlJD4x2Sgr8/I9OTZs5dM//79Y2T6//8/IyqLAcLClEBjYahDKCHSKADN5VNQpdTxzgAAAABJRU5ErkJggg==) !important;
    margin: 0 -24px !important;
}

.ytp-left-controls {
	height: 157% !important;
}

.ytp-volume-slider {
	top: -6px !important;
}

.ytp-time-display {
    font-size: 10.4px !important;
    font-family: Roboto, Helvicta, Arial, sans-serif !important;
    padding: 0 0px !important;
    width: 57px !important;
    margin-left: 8px !important;
    line-height: 27px !important;
}

.ytp-time-current {
    color: #fff!important;
}

.ytp-time-separator,
.ytp-time-duration {
    color: #999 !important;
}

.ytp-gradient-bottom {
    height: 0px !important;
    padding-top: 0px !important;
}

.ytp-settings-button.ytp-hd-quality-badge::after,
.ytp-settings-button.ytp-4k-quality-badge::after,
.ytp-settings-button.ytp-5k-quality-badge::after,
.ytp-settings-button.ytp-8k-quality-badge::after {
    top: 2px !important;
    right: 2px !important;
    background-color: #b50324 !important;
}

.ytp-settings-button.ytp-hd-quality-badge::after {
    background: url(https://s.ytimg.com/yts/imgbin/player-common-vflbJjZA5.png) !important;
    background-position: 26px 0px !important;
}

.ytp-big-mode .ytp-settings-button.ytp-hd-quality-badge::after {
    content: '___' !important;
    background: url(https://s.ytimg.com/yts/imgbin/player-common-vflbJjZA5.png) !important;
    background-position: 26px 0px !important;
    margin: -12px -16px !important;
    color: transparent !important;
    height: 7px !important;
}

.ytp-chrome-controls .ytp-button {
    height: 100% !important;
    border-right: 1px solid #222 !important;
}

.ytp-bezel {
    border-radius: 10px !important;
}

/* Scrubber */
.ytp-scrubber-button.ytp-swatch-background-color {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-common-vflXVjbFd.png) -158px -19px !important;
}

.ytp-scrubber-button.ytp-swatch-background-color:hover {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-common-vflXVjbFd.png) -158px 0 !important;
}

.ytp-scrubber-button {
    width: 13px !important;
    padding: 1.5px !important;
    margin: 0px 0px 0px 0 !important;
    border-radius: 36.5px !important;
}

/* Progress-Bar */
.ytp-progress-bar-container:hover:not([aria-disabled=true]) {
    cursor: pointer !important;
    height: 8px !important;
}

.ytp-swatch-background-color {
    background-image: linear-gradient(to bottom, #c00 0, #600 100%) !important;
}

.ytp-progress-list {
    background: #1e1f1f !important;
    background-image: linear-gradient(to top, rgba(30, 31, 31, .75) 89%, #2f2f2f 90%) !important;
}

.ytp-load-progress {
    background-image: linear-gradient(to top, #5a5a5a 89%, #666 90%) !important;
}

.ytp-hover-progress-light {
    opacity: 0 !important;
}
.ytp-big-mode .ytp-scrubber-button {
    height: 16px !important;
}
.ytp-progress-list {
    transform: scaleY(0.6) scaleX(1.037) translateX(12.0px) !important;
}
.ytp-progress-bar-container:hover:not([aria-disabled="true"]) .ytp-progress-list {
    transform: scaleX(1.037) translateX(12.0px) !important;
}


/* Play */
.ytp-chrome-controls .ytp-play-button {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -96px -231px !important;
    padding: 0px 27.5px !important;
}

.ytp-chrome-controls .ytp-play-button:hover {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -121px -42px !important;
    padding: 0px 27.5px !important;
}


/* Pause */
button.ytp-play-button.ytp-button[title="Pause (k)"] {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -64px -177px !important;
}

button.ytp-play-button.ytp-button[aria-label="Pause (k)"]:hover {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -64px -258px !important;
}


/* Vol */
button.ytp-mute-button.ytp-button {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -153px -258px !important;
    width: 0px !important;
    margin-left: 2px !important;
    padding: 0 33px 0 2px !important;
}

button.ytp-mute-button.ytp-button:hover {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -121px -123px !important;
    width: 0px !important;
    margin-left: 2px !important;
    padding: 0 33px 0 2px !important;
}


/* Mute */
button.ytp-mute-button.ytp-button[title="Unmute (m)"] {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -121px -15px !important;
}

button.ytp-mute-button.ytp-button[title="Unmute (m)"]:hover {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) 0 -42px !important;
}


/* Next + Previous */
a.ytp-next-button.ytp-button {
    display: none !important;
}

a.ytp-prev-button.ytp-button {
    display: none !important;
}



/* Fullscreen */
button.ytp-fullscreen-button.ytp-button {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -89px -123px !important;
    padding: 0px 15px !important;
}

button.ytp-fullscreen-button.ytp-button:hover {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -96px -96px !important;
    padding: 0px 15px !important;
}


/* Exit Fullscreen */
button.ytp-fullscreen-button.ytp-button[title="Exit full screen (f)"] {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -1px -68px !important;
    padding: 0px 15px !important;
}

button.ytp-fullscreen-button.ytp-button[title="Exit full screen (f)"]:hover {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -65px -149px !important;
    padding: 0px 15px !important;
}


/* Theater Mode */
button.ytp-size-button.ytp-button[title="Theater mode (t)"] {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -89px -15px !important;
    background-size: auto !important;
    padding: 0 14.5px !important;
}

button.ytp-size-button.ytp-button:hover {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -32px -204px !important;
    background-size: auto !important;
    padding: 0 14.5px !important;
}


/* Default Mode */
button.ytp-size-button.ytp-button[title="Default view (t)"] {
    width: 0px !important;
    padding: 0 14.5px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -57px -123px !important;
    background-size: auto !important;
}

button.ytp-size-button.ytp-button[title="Default view (t)"]:focus:hover {
    width: 0px !important;
    padding: 0 14.5px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -32px -231px !important;
    background-size: auto !important;
}


/* Settings */
button.ytp-button.ytp-settings-button {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -153px -123px !important;
    padding: 0 14.5px !important;
    background-size: auto !important;
}

button.ytp-button.ytp-settings-button:hover {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -128px -204px !important;
    padding: 0 14.5px !important;
    background-size: auto !important;
}

button.ytp-button.ytp-settings-button:active {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -128px -204px !important;
    padding: 0 14.5px !important;
    background-size: auto !important;
}


/* Captions */
button.ytp-subtitles-button.ytp-button {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -65px -204px !important;
    width: 0px !important;
    padding: 0 14.5px !important;
    background-size: auto !important;
}

button.ytp-subtitles-button.ytp-button:hover {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -65px -285px !important;
    width: 0px !important;
    padding: 0 14.5px !important;
    background-size: auto !important;
}

.ytp-chrome-controls .ytp-button[aria-pressed=true]:after {
    width: 0px !important;
    left: 9px !important;
    -moz-transition: left .25s cubic-bezier(0.0, 0.0, 0.2, 1), width .25s cubic-bezier(0.0, 0.0, 0.2, 1) !important;
    -webkit-transition: left .25s cubic-bezier(0.0, 0.0, 0.2, 1), width .25s cubic-bezier(0.0, 0.0, 0.2, 1) !important;
    transition: left .25s cubic-bezier(0.0, 0.0, 0.2, 1), width .25s cubic-bezier(0.0, 0.0, 0.2, 1) !important;
}

button.ytp-subtitles-button.ytp-button[aria-pressed="true"] {
    background: url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -33px -150px !important;
    width: 0px !important;
    padding: 0 14.5px !important;
    background-size: auto !important;
}

button.ytp-subtitles-button.ytp-button[aria-pressed="true"]:hover {
    background: url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -33px -150px !important;
    width: 0px !important;
    padding: 0 14.5px !important;
    background-size: auto !important;
}

/* MiniPlayer (for users of the material design) */
button.ytp-miniplayer-button.ytp-button {
    width: 0px !important;
    padding: 0 14.5px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -57px -123px !important;
    background-size: auto !important;
}

button.ytp-miniplayer-button.ytp-button:hover {
    width: 0px !important;
    padding: 0 14.5px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -32px -231px !important;
    background-size: auto !important;
}


/* Volume Slider */
.ytp-volume-slider-handle:before {
    left: -64px !important;
    background: url(data:image/png;base64,) !important;
    background-position: 16px !important;
    height: 32px !important;
}

.ytp-volume-slider-handle:after {
    left: 5px !important;
    background: url(data:image/png;base64,) !important;
    background-position: 151px !important;
    height: 32px !important;
}

.ytp-volume-slider-handle {
    position: absolute !important;
    top: 50% !important;
    width: 5px !important;
    height: 14px !important;
    border-radius: 0px !important;
    margin-top: -5.9px !important;
    background: url(data:image/png;base64,) 258px 0px !important;
}

.ytp-volume-slider-handle[style="left: 0px;"] {
    left: -9px !important;
}

.ytp-volume-slider-active .ytp-volume-panel {
    width: 54px !important;
    margin-right: 3px !important;
}

.ytp-volume-slider-handle {
    margin-left: 9px !important;
}


/* Settings Box */
.ytp-settings-menu {
    right: 12px !important;
    background: rgba(31, 31, 31, .9) !important;
    bottom: 36px !important;
    z-index: 71 !important;
    will-change: width, height !important;
}

.ytp-menuitem[aria-haspopup="true"] .ytp-menuitem-content {
	padding-right: 10px !important;
	background: #e6e6e6 !important;
	color: #555 !important;
	height: 20px;
}

.ytp-menuitem {
	height: 53px !important;
	display: grid;
	margin: 0 0 0 70px;
	width: 100px;
}
.ytp-menuitem[aria-haspopup="true"]:not([aria-disabled=true]):hover {
    background-color: rgba(255, 255, 255, 0) !important;
}

.ytp-menuitem[role="menuitemcheckbox"]:not([aria-disabled=true]):hover {
    background-color: rgba(255, 255, 255, 0) !important;
}

.ytp-menuitem-toggle-checkbox {
    border-radius: 0px !important;
}

.ytp-menuitem-toggle-checkbox:after {
    height: 13.91px !important;
    width: 16.91px !important;
    border-radius: 0px !important;
    margin-top: 0.4px !important;
    background-color: #bdbdbd !important;
    box-shadow: 0 0px 0px 0 rgba(0, 0, 0, 0.6) !important;
    transition: all 0s cubic-bezier(0.4, 0.0, 1, 1) !important;
}

.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox:after {
    background-color: #636263 !important;
    margin: 0px 3px !important;
    -moz-transform: translateX(16px) !important;
    -ms-transform: translateX(16px) !important;
    -webkit-transform: translateX(16px) !important;
    transform: translateX(16px) !important;
}

.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
    background: #bdbdbd !important;
}

.ytp-popup {
    text-shadow: 0 0 2px rgba(255, 255, 255, 0) !important;
}

.ytp-menu-label-secondary {
    font-size: 91% !important;
    color: #555 !important;
}

.ytp-panel[style="width: 296px; height: 183px;"] {
    position: absolute !important;
    bottom: -5px !important;
    right: 0px !important;
    width: 257px !important;
    height: 110px !important;
    overflow-y: auto !important;
    overflow-x: hidden !important;
}

.ytp-menuitem-icon svg {
	display: block;
	width: 0;
	height: 0;
}
.ytp-panel-menu {
	padding: 15px 0;
	width: 80%;
}

.ytp-menuitem {
	height: 53px !important;
	display: grid;
	margin: 0 0 0 130px;
	width: 100px;
}

.ytp-settings-menu {
	bottom: 30px !important;
	width: 150px !important;
}


/* Right-Click Menu */
.ytp-panel-menu[style="height: 174px;"] {
    color: #333 !important;
    margin-left: -32px !important;
}

.ytp-popup {
    border-radius: 0px !important;
    background: #ffffff;
}

.ytp-menuitem-label {
    font-size: 118% !important;
    font-weight: 100 !important;
}

.ytp-menuitem:not([aria-disabled=true]):hover {
    background-color: #333 !important;
    color: #fff !important;
}

.ytp-popup[class="ytp-popup ytp-contextmenu"] {
    border-radius: 2px !important;
    width: 223px !important;
    height: 177px !important;
    background: #ffffff !important;
    border: 1px solid #ccc !important;
}

.ytp-menuitem[data-visual-id="9"] {
    height: 25px !important;
}

.ytp-panel[style="width: 295px; height: 174px;"] {
    bottom: -4px !important;
    right: 0px !important;
    width: 295px !important;
    height: 180px !important;
}

.ytp-menuitem-label, 
.ytp-menuitem-content {
	padding: 2px 0px !important;
	text-align: left !important;
}

.ytp-panel-menu[style="height: 177px;"] {
    color: #333 !important;
}

.ytp-panel-menu[style="height: 178px;"] {
	color: #333 !important;
	margin: 0px 150px;
	display: block;
	width: 350px;
	padding: 40px 60px;
}

/* Fullscreen Mode */
.ytp-big-mode .ytp-chrome-top .ytp-button,
.ytp-big-mode .ytp-chrome-controls .ytp-button {
    width: 0px !important;
}

.ytp-big-mode .ytp-time-display {
    line-height: 27px !important;
}

.ytp-big-mode .ytp-chrome-controls {
    height: 27px !important;
    line-height: 27px !important;
}

.ytp-big-mode .ytp-chrome-bottom {
	height: 54px !important;
	padding-top: 4px !important;
	margin-bottom: -5px !important;
}
.ytp-big-mode .ytp-progress-list {
    transform: scaleY(0.625) scaleX(1.018) translateX(12px) !important;
}
.ytp-big-mode .ytp-progress-bar-container:hover:not([aria-disabled="true"]) .ytp-progress-list {
	transform: translateY(-2.5px) scaleX(1.018) translateX(12px) !important;
}

.ytp-big-mode .ytp-chrome-controls .ytp-play-button {
	padding: 27px !important;
}

.ytp-big-mode {
	font-size: 11px;
}

.ytp-big-mode .ytp-menuitem-icon svg {
	width: 0px;
	height: 0px;
}

.ytp-big-mode .ytp-volume-slider {
	min-height: 24px;
}


/* Play on TV Button */
button.ytp-button[title="Play on TV"] {
    display: none !important;
}

/* Watch on YouTube Button */
.ytp-chrome-controls .ytp-button.ytp-youtube-button {
    width: 0px !important;
    padding-right: 52px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-common-vflXVjbFd.png) -302px -34px !important;
}

.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover {
    width: 0px !important;
    padding-right: 52px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-common-vflXVjbFd.png) -15px 1px !important;
}

/* Spinner */
.ytp-spinner-rotator {
    width: 31px !important;
    height: 31px !important;
    background: url(data:image/png;base64,) !important;
}

.ytp-spinner-container {
    width: 31px !important;
    padding-bottom: 31px !important;
    margin-top: -15px !important;
    margin-left: -15px !important;
    transform: rotate(617688888888grad) !important;
}

.ytp-spinner-left {
    right: 49% !important;
    opacity: 0 !important;
}

.ytp-spinner-right {
    left: 49% !important;
    opacity: 0 !important;
}

/* Fullscreen Mode Volume Slider */
.ytp-big-mode .ytp-volume-slider-handle:before,
.ytp-big-mode .ytp-volume-slider-handle:after {
    height: 14px !important;
    margin-top: -5px !important;
    width: 85px !important;
}

.ytp-big-mode .ytp-volume-slider-handle {
    width: 0px !important;
    height: 18px !important;
    border-radius: 9px !important;
    margin-top: -9px !important;
}
.ytp-big-mode .ytp-volume-slider-handle:before {
    left: -64px !important;
}
.ytp-big-mode .ytp-volume-slider-active .ytp-volume-panel {
    width: 55px !important;
    margin-right: 0px !important;
}
.ytp-big-mode .ytp-volume-slider-handle[style="left: 0px;"] {
    left: 11px !important;
}
.ytp-big-mode .ytp-volume-slider-handle {
    margin-left: -11px !important;
}

/* Replay */
.ytp-chrome-controls .ytp-play-button[title="Replay"] {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -96px -285px !important;
    padding: 0px 27.5px !important;
}

.ytp-chrome-controls .ytp-play-button[title="Replay"]:hover {
    width: 0px !important;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -32px -15px !important;
    padding: 0px 27.5px !important;
}

/* Final Fixes */
.ytp-chrome-controls .ytp-button.ytp-mute-button svg, .ytp-right-controls .ytp-button svg {
	width: 0px;
	height: 0px;
}
body:not([v3notplaylist]) button.ytp-mute-button.ytp-button {
	padding-right: 34px !important;
}
}
