css:
@-moz-document domain("emojipedia.org") {
html,
body {
    background: #333742;
    color: #ccc;
}
/* head*/
.sponsor {
    background-color: #1C212D;
}
.sponsor a,
.sponsor a:hover {
    border-bottom: unset;
}
.sponsor > p:nth-child(1) > a:nth-child(1) > span:nth-child(1) {
    color: #fff;
}
body > header {
    background-color: #333742;
}
/* search  */
.home-content .search-form input {
    background-color: #495163;
    border: 2px solid #636A7B;
}
input,
textarea,
select,
[type="button"],
[type="submit"],
[type="reset"],
button {
    border: 1px solid #636A7B;
}
body > .home-content .search-form input {
    color: #ccc;
}
a {
    color: #1E90FF
}
a:hover {
    color: #49A6FF;
    font-style: italic;
    text-decoration: none;
}
.emoji {
    color: #1E90FF;
}
.emoji:hover {
    color: #49A6FF;
}
body > header .search-form input {
    background-color: #495163;
    color: #ccc;
    border: 1px solid #636A7B;
}
/*    block   */
.sidebar .block {
    background-color: #2f3642;
    border: 2px solid #636A7B;
    border-radius: 8px;
}
/* emo page */
.vendor-list,
form.stacked,
.vendor-list li .vendor-info,
.vendor-list li .vendor-image {
    border-bottom: 1px solid #636A7B;
    border-top: 1px solid #636A7B;
}
.vendor-list li .vendor-rollout-container .vendor-rollout .vendor-info,
.vendor-list li .vendor-rollout-container .vendor-rollout .vendor-image {
    border-bottom: 1px solid #636A7B;
    border-top: 1px solid #636A7B;
    background-color: #2f3642;
}
.vendor-list li .vendor-rollout-container .vendor-rollout {
    border-bottom: unset;
}
.content .boxout {
    background-color: #2f3642;
    border: 2px solid #636A7B;
    border-radius: 8px;
}
#trends-widget-1 {
    filter: brightness(85%);
}
/* New Emojis*/
.site-header {
    background: #2f3642 no-repeat 50%;
}
.post-full-content,
.page-template .site-main,
.post-template .site-main {
    background: #333742;
    color: #ccc;
}
.post-full-content h1,
.post-full-content h2,
.post-full-content h3,
.post-full-content h4,
.post-full-content h5,
.post-full-content h6,
.post-full-title {
    color: #aaa;
}
.post-full-content em,
.post-full-content strong {
    color: #ccc;
}
.post-full-content a {
    color: #eee;
}
.author-card-name a,
.post-card-content-link {
    color: #ccc;
}
.post-card {
    background-color: #2f3642;
    border: 2px solid #636A7B;
    border-radius: 8px;
}
/* foot */
body > footer nav {
    color: #e2e2e2;
    background-color: #2f3642;
    border-bottom: 2px solid #636A7B;
    border-top: 2px solid #636A7B;
}
body > footer nav a {
    color: #679BE2;
}
body > footer {
    color: #d1d1d1;
    background-color: #121827;
}
}
