css:
@-moz-document domain("app.glip.com") {
body[theme=rc] .plugintab, .calendar-events-header .calendar-events-date-range-block, .calendar-events-list-block {
    color: #34a0e6 !important;
}
body[theme=rc] .grouptab, .plugin-content h1, .contentpad h1, h1.page-header, .phone-row .name, table.ganttchart tr.tr_sep label, .calendar-event-row.calendar-event-row-default .calendar-event-row__time, .calendar-event-row .calendar-event-row__title, .calendar-event-row .calendar-event-row__time {
    color: #bbb !important;
}
body[theme=rc] .plugintab.selected, body[theme=rc] .grouptab.selected, h1#popup-title {
    background: #09638e!important;
    color: #ccc!important;
}
.rightrail .sep label {
    border-radius: .5em;
    background: #09638e!important;
    color: #ccc!important;
}
.loading {
    background-color: inherit;
}
body[theme=rc] a {
    color: #34a0e6 !important;
}
body[theme="rc"] a.at_mention_post.no-person {
    border-radius: .5em;
    color: #34a0e6 !important;
    background-color: #2d4a50;
}
div.select td.label {
    background-color: #ddd !important;
    color: #333 !important;
}
div.select, input, textarea, .quickdial-bar-container, input.quickdial-bar, input.quickdial-bar:focus, input.quickdial-bar:focus:hover, .compose-popup .body {
    background-color: #183e56 !important;
    color: #bbb !important;
}
input.error, textarea.error, .error.warn, .perm_error.warn {
    background-color: #183e56 !important;
}
#topbar, .topbar-search .topbar-search-input-container input:focus, .topbar-search .topbar-search-input-container input:hover:focus, .topbar-search-results-container {
    background-color: #172936 !important;
}
.topbar-results-display-list-wrapper, .result-display-list-section-title-wrapper, .results-display-list-item-icon, .actions, .stream-action {
    background-color: transparent !important;
}
.leftrail-bottom-navigation-update-plugins .plugintab-bottom-navigation.selected {
    background-color: transparent !important;
}
.leftrail-bottom-navigation-update-plugins .plugintab-bottom-navigation.selected .ico {
    color: #34a0e6;
}
.leftrail-bottom-navigation-update-plugins .plugintab-bottom-navigation .ico {
    color: #bbb;
}
menu.dropdown, .hint, .compose-popup {
    background: rgba(75, 172, 234, .85);
}
.arrow, .arrow2 {
    background-color: rgba(75, 172, 234, .85);
}

.phone-holder .quickdial-results {
    background-color: #183e56;
}
.phone-holder .quickdial-results-container {
    background-color: #183e56;
}
div.widget-outbound-caller-id {
    color: inherit;
}
.outbound-caller-id-list-widget {
    background-color: rgba(153, 206, 230, .88);
}
.phone-people-row {
    background-color: rgba(153, 206, 230, .88);
}
div.person_info {
    color: #ddd !important;
}
div.person_name {
    /*color: #5dd !important;*/
    color: #54b2dc !important;
}
.person_row:hover {
    background-color: #666 !important;
}
div.content, .post, .post .post_text, .postslist, h1.page-header, h1.stream-header, div.stream-compose, .compose_type, .emptybox, input, input:focus, input:hover:focus, .mimic_input, .input-holder, textarea, select, .results-display-list-item, .tasktext {
    background-color: #172935 !important;
    color: #bbb !important;
}
.post .markdown b, .post .integration_item b {
    color: inherit !important;
}
.compose label {
    color: #bbb !important;
}
td.fc-state-highlight {
    background: #183e56 !important;
}
.first-post, .postslist .sep label, .post .sep label, .rightrail, div.item-omni, table.ganttchart tr.focus td, table.ganttchart tr.edit td, div.replies .post, .calendar-event-row:not(.calendar-no-events-row):hover {
    background-color: #233d4e !important;
    color: #bbb !important;
}
table.ganttchart tr:hover textarea, table.ganttchart tr:hover input {
    background-color: inherit !important;
}
table.ganttchart tr:hover td, tr.ui-sortable-helper td {
    background-color: #444 !important;
}
.fc-widget-header {
    background-color: #09638e !important;
}
tr.taskrow.black.focus td textarea, tr.taskrow.black.focus td input {
    background-color: transparent !important;
}
.emptybox, .settings_category .title {
    color: #ddd !important;
}
.headshot img {
    background-color: transparent !important;
}
pre.codesnippet, .CodeMirror, .CodeMirror-gutters {
    background-color: #8997a1 !important;
}
pre.codesnippet, .CodeMirror-linenumber {
    color: #083f58 !important;
}
/* link preview in post*/
div.post:nth-child(2n) .link {background-color: transparent !important;}
/* link to an attached file in a post */
.post .filename {
    color: #589ecc !important;
}
/* team search */
.guest, h1.stream-header.guest {
    background-image: none !important;
    background: rgba(255,255,255,.1) !important;  
}
.team_name, .person_name, .model_name {
    color: inherit !important;  
}
.chat-textarea, .post textarea {
    color: #3eb8ff !important;
}
}
