@-moz-document regexp(".*anilist.co.*") {
/*Updates here: https://userstyles.world/style/5243*/
/*anilist*/
.container {
     padding-left: 0px;
     padding-right: 0px;
}
*,
div,
html,
canvas {
     font-family: monospace, monospace!important;
     letter-spacing: -0.5px!important;
     word-spacing: -2px!important;
     border-radius: 0px!important;
     box-shadow: none!important;
}
.line-chart-wrap/*graphs get wonky, because of border-radius: 0 above*/
{
     background: none!important;
}
:after,
:before {
     border-radius: 0px!important;
     box-shadow: none!important;
}
::-webkit-scrollbar {
     width: 4px!important;
}
::-webkit-scrollbar-thumb {
     cursor: pointer!important;
     border-radius: 3px;
}
::-webkit-scrollbar-thumb {
     background: rgba(var(--color-blue))!important;
}
.text ::-webkit-scrollbar,
.about ::-webkit-scrollbar,
.activity-text ::-webkit-scrollbar,
.input.el-textarea ::-webkit-scrollbar,
.reply ::-webkit-scrollbar {
     width: 0px!important;
}
.shadow {
     display: none;
}
.banner {
     margin-top: 0!important;
}
.activity-entry .details .status:first-letter {
     text-transform: lowercase!important;
}
#app > div.wrap > div {
     background: rgba(var(--color-foreground-grey));
}
input::placeholder,
textarea::placeholder/*for some reason placeholders in inputs and textareas don't change color when modding --color-text in site-theme-contrast*/
{
     color: rgba(var(--color-text))!important;
}

/*dropdowns*/
.el-select-dropdown,
.el-dropdown-menu,
.el-dropdown-menu__item--divided::before {
     background: rgba(var(--color-foreground-grey))!important;
}
.el-select-dropdown__item.hover,
.el-select-dropdown__item:hover {
     background: rgba(var(--color-foreground-grey-light));
}
.el-dropdown-menu__item--divided {
     border-color: rgba(var(--color-background));
}
.el-select-group__wrap:not(:last-of-type)::after {
     background: rgba(var(--color-foreground));
}
.el-popper[x-placement^="bottom"] .popper__arrow,
.el-popper[x-placement^="bottom"] .popper__arrow::after {
     border-bottom-color: transparent;
}
.el-popper[x-placement^="top"] .popper__arrow,
.el-popper[x-placement^="top"] .popper__arrow::after {
     border-top-color: transparent;
}
.el-select-dropdown.is-multiple .el-select-dropdown__item.selected,
.el-select-dropdown.is-multiple .el-select-dropdown__item.selected.hover {
     background: rgba(var(--color-foreground-grey-light));
}

/*markdown editor*/
.markdown-editor {
     background: rgba(var(--color-foreground-grey));
}
.markdown-editor > div {
     color: rgba(var(--color-text))!important;
}
.markdown-editor > div:hover {
     color: rgba(var(--color-blue))!important;
}
.markdown-editor > [title="Youtube Video"]:hover {
     color: rgba(255, 0, 0)!important;
}

/*donator badge*/
.details > .donator-badge {
     left: 109px!important;
     top: 100%!important;
     transform: translate(0px, -35px);
}

/*like button */
/*this mod is by Reina
https://raw.githubusercontent.com/Reinachan/AniList-High-Contrast-Dark-Theme/main/LICENSE*/
.action.likes .button .fa-heart:hover {
     stroke: rgba(var(--color-blue))!important;
     transition: 0.2s!important;
}
.action.likes .button .fa-heart {
     color: transparent;
     stroke: rgba(var(--color-blue-dim));
     stroke-width: 70;
     font-size: .87em;
     padding-bottom: 0.08em;
     padding-top: 0.05em;
}
.like-wrap.thread_comment .button .fa-heart {
     color: transparent;
     stroke: rgba(var(--color-text-lighter));
     stroke-width: 70;
     font-size: 1em;
     padding-bottom: 0.0em;
     padding-top: 0.05em;
}
.action.likes .button.liked .fa-heart {
     color: var(--color-red);
     stroke: rgb(0 0 0 / 0%);
     stroke-width: 0;
     font-size: 0.875em;
     padding-bottom: 0;
     padding-top: 0;
}
.like-wrap.thread_comment .button.liked .fa-heart {
     color: var(--color-red);
     stroke: rgb(0 0 0 / 0%);
     stroke-width: 0;
     font-size: 1em;
     padding-bottom: 0;
     padding-top: 0;
}

/*activities*/
.activity-entry,
.activity-edit .input,
.reply.preview {
     margin-bottom: 10px!important;
}
.activity-text .text {
     border-left: solid 2px rgba(var(--color-text));
}
.user .activity-entry.activity-message {
     border-left: solid 2px rgba(var(--color-blue));
}
.activity-manga_list > div > div > div > div > .title {
     color: rgba(var(--color-manga))!important;
}
.activity-entry .status {
     display: inline-block!important;
     line-height: 1.6rem;
     padding-right: 115px;
}
.reply {
     margin-bottom: 2px!important;
}
.home .activity-entry .list {
     min-height: 80px!important;
     grid-template-columns: 60px auto!important;
}
.activity-replies {
     margin-top: 2px!important;
}
.list .name {
     padding-bottom: 0!important;
}
.activity-entry .name,
.activity-entry .title {
     font-weight: 600;
}

/*list editor*/
.list-editor-wrap .header .title {
     letter-spacing: 2px!important;
}
.list-editor-wrap .list-editor .body .checkbox {
     margin-bottom: 5px;
}
.list-editor-wrap .list-editor .body .custom-lists {
     overflow: scroll!important;
     height: 280px;
}
.custom-lists .el-checkbox__label {
     display: inline !important;
     white-space: normal;
     word-wrap: anywhere;
     word-break: break-word;
}
.list-editor-wrap .list-editor .body .custom-lists::-webkit-scrollbar {
     width: 0px!important;
     height: 0px!important;
}

/*calender in list editor*/
body > div.el-picker-panel.el-date-picker.el-popper,
.el-date-picker__header-label {
     background: rgba(var(--color-foreground-grey));
     border: none;
     color: rgba(var(--color-text));
}
.el-date-table td {
     padding: 0;
}
.el-date-table th,
.el-date-picker__header--bordered {
     padding: 0;
     border-color: rgba(var(--color-foreground-grey-light))!important;
}
.el-date-picker__header {
     margin: 0;
}
.el-picker-panel__content {
     margin-top: 0;
     margin-bottom: 0;
}
.el-popper[x-placement^=bottom] {
     margin-top: 0;
}
.el-date-table td.next-month,
.el-date-table td.prev-month {
     color: rgba(var(--color-text), .3);
}
.el-picker-panel__icon-btn {
     color: rgba(var(--color-text));
}

/*anilist offline front page*/
#app > div.page-content > div > div.landing {
     background: rgba(var(--color-foreground))!important;
}
}

