@-moz-document regexp(".*anilist.co/home"), regexp(".*anilist.co/activity/.*"), regexp(".*anilist.co/social") {
/*home*/
.home {
     grid-template-columns: auto 470px!important;
     overflow: visible!important;
}
.section-header,
h2 {
     padding-left: 0px!important;
     font-weight: 600;
}

/*media preview*/
.list-preview {
     grid-gap: 10px!important;
     padding: 0px!important;
     background: rgba(0, 0, 0, 0)!important;
}
.media-preview {
     grid-gap: 10px!important;
     padding: 0px!important;
     background: rgba(0, 0, 0, 0)!important;
}
.small .content {
     background: rgba(var(--color-background), .9)!important;
}
.media-preview-card {
     display: inline-grid!important;
}

/*following/global*/
.feed-type-toggle {
     margin-left: 10px;
}

/*activities*/
.list .avatar {
     display: block!important;
}
}

@-moz-document regexp(".*anilist.co/user/.*") {
/*user*/
.header-wrap .banner .name {
     background: rgba(0, 0, 0, .7);
     padding: 5px 15px;
}
.section-header {
     padding-left: 0px!important;
     font-weight: 600!important;
}

/*filters*/
.filter-group a,
.filter-group span,
.filter-group span.active,
.filter-group > span,
.filter-group .active,
.group-header {
     font-size: 1.3rem!important;
     font-weight: 600!important;
}

/*sticky nav bar*/
#app > div.page-content > div > div.header-wrap {
     position: sticky!important;
     top: -331px!important;
     z-index: 998;
}
#app {
     overflow: unset!important;
}
.nav.container a.link {
     font-size: 1.2rem;
     padding: 10px;
     margin: 0 20px;
}
.nav-wrap {
     background: rgba(var(--color-foreground-grey))!important;
}

/*genre overview*/
.genres {
     grid-gap: 3px;
}
.genre .name{
     padding: 6px 8px!important;
}
.overview .genre-overview .genre:last-child{
     display: unset!important;
}
.percentage-bar {
     height: 3px;
}
.genre .name {
     font-size: 1.3rem;
}
.genre .amount {
     font-size: 1.2rem;
     font-weight: 500;
}
.genre:nth-of-type(1) .name,
.percentage-bar .percentage:nth-child(1){
     background: rgba(112, 121, 207)!important;
}
.genre:nth-of-type(2) .name,
.percentage-bar .percentage:nth-child(2){
     background: rgb(137, 177, 195)!important;
}
.genre:nth-of-type(3) .name,
.percentage-bar .percentage:nth-child(3){
     background: rgb(137, 137, 137)!important;
}
.genre:nth-of-type(4) .name,
.percentage-bar .percentage:nth-child(4){
     background: rgba(172, 137, 213)!important;
}
.genre:nth-of-type(5) .name,
.percentage-bar .percentage:nth-child(5){
     background: rgb(113, 179, 217)!important;
}
.percentage-bar .percentage:nth-child(6){
     background: rgb(79, 97, 133)!important;
}
.genre:nth-of-type(1) .amount {
     color: rgba(112, 121, 207)!important;
}
.genre:nth-of-type(2) .amount {
     color: rgba(137, 177, 195)!important;
}
.genre:nth-of-type(3) .amount {
     color: rgba(137, 137, 137)!important;
}
.genre:nth-of-type(4) .amount {
     color: rgb(172, 137, 213)!important;
}
.genre:nth-of-type(5) .amount {
     color: rgba(113, 179, 217)!important;
}
.overview .content-wrap {
     border-radius: 0!important;
}

/*stats overview*/
.stats-wrap .milestones {
     font-size: 1rem;
     padding: 5px 0;
}
.stats-wrap .progress {
     height: 3px;
}
.stats-wrap .milestones .milestone:after {
     padding-top: 2px;
}
.stat:not(:first-of-type) {
     border-left: 1px solid rgba(var(--color-foreground));
}
#app > div.page-content > div > div.content.container > div > div:nth-child(n) > div.stats-wrap > div:nth-child(n) > div.stats-wrap {
     padding-top: 5px!important;
     padding-bottom: 5px!important;
}
.progress .bar {
     background: linear-gradient(270deg, rgba(var(--color-blue)), rgba(var(--color-blue)))!important;
}
.list-stats {
     margin-bottom: 0px!important;
}
.activity-feed-wrap,
.activity-feed-wrap + div {
     margin-top: 25px;
}

/*favorites*/
.favourites-wrap {
     padding: 0px!important;
     display: grid!important;
     grid-gap: 10px!important;
     column-gap: 0!important;
     grid-template-columns: repeat(auto-fill, 85px)!important;
     grid-template-rows: repeat(auto-fill, 115px)!important;
     background: transparent!important;
     margin-left: -5px!important;
}
.favourite.studio {
     display: grid!important;
     grid-template-columns: repeat(auto-fill, 85px)!important;
     grid-template-rows: repeat(auto-fill, 115px)!important;
     text-align: center!important;
     align-items: center!important;
}
.overview .favourites > .favourites-wrap > div,
.overview .favourites > .favourites-wrap > a {
     margin-bottom: 0px!important;
     margin-right: 0px!important;
}
.favourite.studio {
     background: rgba(var(--color-foreground))!important;
     color: rgba(var(--color-text))!important;
}
.favourite.studio:hover {
     background: rgba(var(--color-blue))!important;
     color: rgba(var(--color-text))!important;
}
.favourite.studio:hover {
     background: rgba(var(--color-blue))!important;
     color: rgba(0, 0, 0)!important;
}
.favourite.studio {
     background: rgba(var(--color-foreground))!important;
}
}

@-moz-document regexp(".*anilist.co/user/.*/(animelist|mangalist).*") {
/*anime/mangalist*/

/*mediacards*/
.medialist.cards .list-section .list-entries {
    grid-gap: 4px;
}
.medialist.cards .entry-card .title {
     padding: 10px 10px 30px 10px;
     font-size: 1.2rem;
     white-space: nowrap;
     overflow: hidden;
     text-overflow: ellipsis;
     transition: .2s;
}
.medialist.cards .entry-card .title:hover {
     white-space: normal;
     transition: .2s;
}
.medialist.cards .entry-card .score,
.medialist.cards .entry-card .progress {
     font-weight: 600;
     font-size: 1.3rem;
     padding: 5px 10px;
}
.medialist.cards .entry-card .notes,
.medialist.cards .entry-card .repeat {
     z-index: 997;
}
.medialist.table .section-name {
     padding-left: 0!important;
}
.medialist.cards .entry-card .edit {
     right: 0;
     top: 0;
     height: 210px;
     width: 150px;
     background: rgba(var(--color-overlay), .4)!important;
}
}

@-moz-document regexp(".*anilist.co/user/.*/(favorites|stats|social|submissions).*") {
/*user faves/stats/social/submissions*/
/*favorites*/
.favourites-wrap {
     column-gap: 4px!important;
     overflow: visible!important;
}

/*submissions*/
.submissions {
     min-width: 1140px;
}
.submissions-wrap {
     padding-left: 20px;
}

/*stats*/
#app > div.page-content > div > div.content.container > div > div.stats-wrap > div.overview > section.highlights {
     grid-gap: 10px 5px;
     grid-template-columns: repeat(6, 1fr);
     margin-left: 0;
     width: 100%;
     margin-top: 0px;
     margin-bottom: 20px;
}
div.highlight div.value {
     font-size: 1.7rem!important;
}
div.highlight div.label {
     font-size: 1.1rem!important;
}
#app > div.page-content > div > div.content.container > div > div.stats-wrap > div.overview > section.highlights > div:nth-child(n) > div.wrap {
     padding-left: 5px;
}

/*staff stats*/
.header.has-images {
     min-width: 940px;
}
.stat-cards.has-images {
     grid-template-columns: repeat(2, 1fr)!important;
     min-width: 940px!important;
}
.stat-cards.has-images .details .value {
     font-size: 1.5rem!important;
}
.stat-cards.has-images .details .label {
     font-size: 1.1rem!important;
}

/*forum*/
.user-threads,
.user-comments {
     margin-left: 20px;
}
.comment-wrap {
     margin-bottom: 0!important;
}
#app > div.page-content > div > div.content.container > div > div.user-comments > div.wrap > a:nth-child(n) {
     padding-left: 0;
     padding-bottom: 5px;
}
}

@-moz-document regexp(".*anilist.co/(anime|manga).*") {
/*anime/manga pages*/
/*add button*/
.media-manga .actions .list {
     background: rgba(var(--color-manga));
}

/*relations*/
.relations.small,
.overview {
     overflow: visible!important;
}
.relations.small > div {
     display: grid;
     column-gap: 15px;
     grid-template-columns: repeat(auto-fill, 85px)!important;
}
.relations.small > div > div:nth-child(n) {
     height: 115px;
     margin-bottom: 15px;
}
.small .cover + .content {
     background: rgba(var(--color-foreground), .9)
}

/*status distribution*/
.status-distribution .statuses .status:last-child {
     display: unset;
}
.status .name {
     background: rgba(var(--color-foreground-grey-dark))!important;
     font-size: 1.2rem;
     font-weight: 600;
}
.status .amount {
     font-size: 1.2rem;
     font-weight: 600;
}
.percentage-bar .percentage {
     height: 3px;
}
.status:nth-of-type(1) .name,
.percentage-bar .percentage:nth-child(1) {
     background: rgba(112, 121, 207)!important;
}
.status:nth-of-type(2) .name,
.percentage-bar .percentage:nth-child(2) {
     background: rgb(137, 177, 195)!important;
}
.status:nth-of-type(3) .name,
.percentage-bar .percentage:nth-child(3) {
     background: rgb(137, 137, 137)!important;
}
.status:nth-of-type(4) .name,
.percentage-bar .percentage:nth-child(4) {
     background: rgba(172, 137, 213)!important;
}
.status:nth-of-type(5) .name,
.percentage-bar .percentage:nth-child(5) {
     background: rgb(113, 179, 217)!important;
}
.percentage-bar .percentage:nth-child(6) {
     background: rgb(79, 97, 133)!important;
}
.status:nth-of-type(1) .amount {
     color: rgba(112, 121, 207)!important;
}
.status:nth-of-type(2) .amount {
     color: rgba(137, 177, 195)!important;
}
.status:nth-of-type(3) .amount {
     color: rgba(137, 137, 137)!important;
}
.status:nth-of-type(4) .amount {
     color: rgb(172, 137, 213)!important;
}
.status:nth-of-type(5) .amount {
     color: rgba(113, 179, 217)!important;
}
.media-score-distribution .ct-bar {
     stroke-width: 7px;
}

/*tags*/
.sidebar .tags .tag {
     min-height: 32px!important;
}

/*recommendations*/
.recommendations > .wrap {
    grid-gap: 3%;
}

/*stats*/
.ct-grid {
     stroke: rgba(var(--color-text), .4)!important;
}
}

@-moz-document regexp(".*anilist.co/edit.*") {
/*edit anime/manga*/
/*filters*/
.page-group .page-header,
.page-group > span,
