/* Spin */
 .cover-art-image.cover-art-image-loaded, .cover-art.shadow.actionable.cover-art--with-auto-height, .cover-art-image.cover-art-image-loaded, .cover-art-image:before, .cover-art-image-loaded, .cover-art.shadow, .actionable.cover-art--with-auto-height{
     -webkit-animation: spin .6s linear 1;
     -moz-animation: spin .6s linear 1;
     animation: spin .6s linear 1;
     border-radius: 25px;
}
 @-moz-keyframes spin {
     100% {
         -moz-transform: rotate(360deg);
    }
}
 @-webkit-keyframes spin {
     100% {
         -webkit-transform: rotate(360deg);
    }
}
 @keyframes spin {
     100% {
         -webkit-transform: rotate(360deg);
         transform: rotate(360deg);
    }
}
/* Round albums */
 .cover-art.shadow.actionable.cover-art--with-auto-height, .cover-art.shadow {
     border-radius: 100000px;
}
/* Transparent rainbow background */
 @keyframes rainbow-background {
     0% {
         background-color: #ff000042;
    }
     10% {
         background-color: #ff800042;
    }
     20% {
         background-color: #ffff0042;
    }
     30% {
         background-color: #80ff0042;
    }
     40% {
         background-color: #00ff0042;
    }
     50% {
         background-color: #00ff8042;
    }
     60% {
         background-color: #00ffff42;
    }
     70% {
         background-color: #0080ff42;
    }
     80% {
         background-color: #0000ff42;
    }
     90% {
         background-color: #8000ff42;
    }
     100% {
         background-color: #ff008042;
    }
}
/* Text Link Color */
 a:link, a:visited, a:hover, a:active {
     color: rgb(255, 255, 255);
     opacity: 1;
}
/* Feeback rainbow background */
 .feedbackBar-animation-enter-done, .feedbackBar-animation-exit, .feedbackBar-animation-exit-active, .feedbackBar-animation-exit.feedbackBar-animation-exit-active{
     background: linear-gradient(180deg, #ff0000, #ff8000, #ffff00, #80ff00, #00ff00, #00ff80, #00ffff, #0080ff, #0000ff, #8000ff, #ff0080);
     -webkit-animation: rainbow 10s linear infinite;
     -z-animation: rainbow 10s linear infinite;
     -o-animation: rainbow 10s linear infinite;
     animation: rainbow 10s ease infinite;
     font-size: 25px!important;
     font-weight: bold;
     color: #000 
}
/* Nav bar rainbow background */
 .nav-bar-container, .now-playing-bar-container {
     background: linear-gradient(124deg, #ff000036, #ff800036, #ffff0036, #80ff0036, #00ff0036, #00ff8036, #00ffff36, #0080ff36, #0000ff36, #8000ff36, #ff008036);
     -webkit-animation: rainbow 10s linear infinite;
     -z-animation: rainbow 10s linear infinite;
     -o-animation: rainbow 10s linear infinite;
     animation: rainbow 10s ease infinite;
}
 @-webkit-keyframes rainbow {
     0% {
         background-position: 0% 82% 
    }
     0% {
         background-size: 100% 100%;
    }
     50% {
         background-position: 100% 19% 
    }
     50% {
         background-size: 200% 200%;
    }
     100% {
         background-position: 0% 82% 
    }
     100% {
         background-size: 100% 100%;
    }
}
 @-moz-keyframes rainbow {
     0% {
         background-position: 0% 82% 
    }
     0% {
         background-size: 100% 100%;
    }
     50% {
         background-position: 100% 19% 
    }
     50% {
         background-size: 200% 200%;
    }
     100% {
         background-position: 0% 82% 
    }
     100% {
         background-size: 100% 100%;
    }
}
 @-o-keyframes rainbow {
     0% {
         background-position: 0% 82% 
    }
     0% {
         background-size: 100% 100%;
    }
     50% {
         background-position: 100% 19% 
    }
     50% {
         background-size: 200% 200%;
    }
     100% {
         background-position: 0% 82% 
    }
     100% {
         background-size: 100% 100%;
    }
}
 @keyframes rainbow {
     0% {
         background-position: 0% 82% 
    }
     0% {
         background-size: 100% 100%;
    }
     50% {
         background-position: 100% 19% 
    }
     50% {
         background-size: 200% 200%;
    }
     100% {
         background-position: 0% 82% 
    }
     100% {
         background-size: 100% 100%;
    }
}
/* Avatar bigger */
 .avatar.user-avatar {
     width: 70px!important;
     height: 70px!important;
}
/* Now playing font size */
 .react-contextmenu-wrapper {
     font-size: 20px!important;
}
/* Now playing alignment buttons*/
 .player-controls__buttons {
     margin-bottom: 0px;
     cursor: default;
     display: -ms-flexbox;
     display: flex;
     width: 250px;
     -ms-flex-pack: justify;
     justify-content: space-between;
     -ms-flex-flow: row nowrap;
     flex-flow: row nowrap;
}
/* NOWPLAYING PLAY BUTTON */
 .control-button {
     animation: rainbow-text 30s infinite;
     background-color: transparent;
     cursor: pointer;
     width: 50px;
     min-width: 30px;
     height: 30px;
     position: relative;
}
/* Rainbow text - now playing */
 .playback-bar__progress-time{
     animation: rainbow-text 30s infinite;
     font-size: 15px!important;
     font-weight: bold;
}
/* Rainbow text - everything else */
 :before, :after {
     animation: rainbow-text 30s infinite;
     font-size: 32px!important;
     font-weight: bold;
}
/* The Rainbow text */
 @keyframes rainbow-text {
     0% {
         color: #ff0000;
    }
     10% {
         color: #ff8000;
    }
     20% {
         color: #ffff00;
    }
     30% {
         color: #80ff00;
    }
     40% {
         color: #00ff00;
    }
     50% {
         color: #00ff80;
    }
     60% {
         color: #00ffff;
    }
     70% {
         color: #0080ff;
    }
     80% {
         color: #0000ff;
    }
     90% {
         color: #8000ff;
    }
     100% {
         color: #ff0080;
    }
}
/* Import Font */
 @font-face {
     font-family: caviarDreams;
     src: url(https://cyberlapse.github.io/SpotifyStylishExtension/CaviarDreams.woff) ;
}
 @font-face {
     font-family: caviarDreams;
     src: url(https://cyberlapse.github.io/SpotifyStylishExtension/CaviarDreams_Bold.ttf) format('truetype');
     font-weight:bold;
}
/* Album font info */
 .mo-info-name{
     font-family:caviarDreams;
     font-weight:Bold!important;
     font-size: 18px!important;
}
/* Headdings like play list font info and sub title */
 h1, h2, .link-subtle{
     font-family:caviarDreams !important;
     font-weight:normal!important;
     font-size: 18px!important;
}
/* Hide spotify download */
.navBar-item.navBar-item--with-icon-left.download-item{
    display:none;
}
/*track info to fit*/
.track-info.ellipsis-one-line{
    height:58px!important;
}
/*name size*/
.sessionInfo, .user-link{
    font-size:large!important;
    border-top:none!important;
}

/* Hide spotify logo */
.logo path {
	display:none;
}
/* remove space */
 .navBar-header {  
     height:0px;
     padding:0px;
}
/*padding corrected*/
.navBar{
    padding-top:0px;
}


/* ADS BAR IDk if works
 #main .root .top-container .main-view-container--has-ads .ads-container{
     padding-bottom: 200px;
     visibility: hidden !important;
} */
/* Now playing alignment all */
 .now-playing-bar {
     -ms-flex-align: center;
     align-items: center;
     display: -ms-flexbox;
     display: flex;
     -ms-flex-direction: row;
     flex-direction: row;
     -ms-flex-pack: justify;
     justify-content: space-between;
     height: 69px;
     padding: 0px 28px;
}


/* Home slight gray */
 .navBar-link--active, .navBar-link--active:focus, .navBar-link--active:hover {
     color: #d5d5d5;
}
/* Progress bar */
 .progress-bar__fg {
     background: linear-gradient(230deg, #ff00007a, #ff80007a, #ffff007a, #80ff007a, #00ff007a, #00ff807a, #00ffff7a, #0080ff7a, #0000ff7a, #8000ff7a, #ff00807a);
     -webkit-animation: rainbow 10s linear infinite;
     -z-animation: rainbow 10s linear infinite;
     -o-animation: rainbow 10s linear infinite;
     animation: rainbow .5s ease infinite;
     border-radius: 10px;
     height: 10px;
     width: 10px;
}

/* Progress bar hover */
 .progress-bar:hover .progress-bar__fg {
     background-color: #fff!important;
     border-radius: 2px;
     height: 10px;
     width: 10px;
}
/* Progress bar back ground */
 .middle-align.progress-bar__bg{
     background-color: #9797977a!important;
     border-radius: 10px;
     height: 10px;
}
/* small scroll window -- inactive ? */
 .col-xs-12 .col-lg-3 .col-xl-4 .col-sticky{
     top: 70px;
}
/* *****************Scroll bar*********** */
/* Scroll bar - size */
 ::-webkit-scrollbar {
     width: 15px;
     height: 2px;
}
/* Scroll bar - padding */
 ::-webkit-scrollbar-button {
     width: 0px;
     height: 0px;
}
/* Scroll bar - not in use*/
 ::-webkit-scrollbar-thumb {
     background: linear-gradient(230deg, #ff000036, #ff800036, #ffff0036, #80ff0036, #00ff0036, #00ff8036, #00ffff36, #0080ff36, #0000ff36, #8000ff36, #ff008036);
     -webkit-animation: rainbow 10s linear infinite;
     -z-animation: rainbow 10s linear infinite;
     -o-animation: rainbow 10s linear infinite;
     animation: rainbow 10s ease infinite;
     border-radius:50px;
}
/* Scroll bar - hover over */
 ::-webkit-scrollbar-thumb:hover {
     background: linear-gradient(230deg, #ff00007a, #ff80007a, #ffff007a, #80ff007a, #00ff007a, #00ff807a, #00ffff7a, #0080ff7a, #0000ff7a, #8000ff7a, #ff00807a);
     -webkit-animation: rainbow 10s linear infinite;
     -z-animation: rainbow 10s linear infinite;
     -o-animation: rainbow 10s linear infinite;
     animation: rainbow 1s ease infinite;
}
/* Scroll bar - in use active*/
 ::-webkit-scrollbar-thumb:active {
     background: linear-gradient(230deg, #ff0000, #ff8000, #ffff00, #80ff00, #00ff00, #00ff80, #00ffff, #0080ff, #0000ff, #8000ff, #ff0080);
     -webkit-animation: rainbow 10s linear infinite;
     -z-animation: rainbow 10s linear infinite;
     -o-animation: rainbow 10s linear infinite;
     animation: rainbow 1s ease infinite;
}
/* Scroll bar - not in use */
 ::-webkit-scrollbar-track {
     background: #056f572e;
     border: 0px none #ffffff;
     border-radius: 50px;
}
/* Scroll bar - hover over */
 ::-webkit-scrollbar-track:hover {
     background: #0086682e;
}
/* Scroll bar - active */
 ::-webkit-scrollbar-track:active {
     background: #00866873;
}
}
