css:
@-moz-document domain("rawgit.com") {
html,
body {
	background: hsl(0, 0%, 8%);
	color: hsl(0, 0%, 100%);
}
a {
	color: hsl(16, 100%, 60%);
}
.hd {
	background: hsl(0, 0%, 16%);
	border-bottom: 1px solid hsl(0, 0%, 26%);
}
.hd .title {
	color: inherit;
}
.hd .nav a {
	color: hsl(0, 0%, 75%);
}
.hd .nav a:hover {
	background: hsl(0, 0%, 24%);
}
.ft {
	border-top: 1px solid hsl(0, 0%, 26%);
	color: hsl(0, 0%, 75%);
}
.ft a:hover {
	color: hsl(16, 100%, 60%);
}
.url-copy-button:hover {
	background: hsl(0, 0%, 24%);
	border-color: hsl(0, 0%, 32%);
}
.url-copy-button img {
	filter: invert(80%);
}
.url-copy-button:hover img {
	filter: invert(100%);
}
.invalid {
	background: hsl(16, 100%, 87%);
	border-color: hsl(16, 100%, 92%);
}
.valid {
	background: hsl(99, 100%, 96%);
	border-color: hsl(125, 89%, 79%);
}
}
