css:
@-moz-document url-prefix(https://atlas){
.drag.customerPickup,
.drag.completed,
.drag.stationary,
.drag.completedUrgent {
    position: relative;
    top: 2%;
    -webkit-animation: spin 4s linear infinite;
    -moz-animation: spin 4s linear infinite;
    animation: spin .7s linear 1;
    color: #0f001b;
}
@-moz-keyframes spin {
    100% {
        -moz-transform: rotate(360deg);
    }
}
@-webkit-keyframes spin {
    100% {
        -webkit-transform: rotate(360deg);
    }
}
@keyframes spin {
    100% {
        -webkit-transform: rotate(360deg);
        transform: rotate(360deg);
    }
}

.clock1 {
    animation: rainbow-text 600s infinite;
}
@keyframes rainbow-text {
    0% {
        color: #ff0000;
    }
    10% {
        color: #ff8000;
    }
    20% {
        color: #ffff00;
    }
    30% {
        color: #80ff00;
    }
    40% {
        color: #00ff00;
    }
    50% {
        color: #00ff80;
    }
    60% {
        color: #00ffff;
    }
    70% {
        color: #0080ff;
    }
    80% {
        color: #0000ff;
    }
    90% {
        color: #8000ff;
    }
    100% {
        color: #ff0080;
    }
}

.container-fluid,
.gr__atlas,
.row.toolbar,
.panel-body.form-inline,
.mark {
    background-color: #243447!important;
    color: #fff!important;
}

#binCustomerPickup,
#binStationary,
#binTasks,
#binCompleted {
    background-color: #141d26!important;
}
.row.titleBar,
.breadcrumb.pull-left,
.btn.btn-default,
.odd,
.active,
.panel-body {
    background-color: #141d26!important;
    color: #fff!important;
}
a:link {
    color: #909090!important;
    font-weight: bold;
}
.clock {
    color: #2886d6!important;
    box-shadow: 5px 10px!important;
    text-shadow: 0 0 200px #fff, 0 0 90px #526ae2;
    font-size: 20px;
}
.modal-content{
    background-color: #141d26!important;
    color: #fff!important;
    border: 5px solid #295f6e;
}
}
