css:
@-moz-document domain("rust.cc") {
/** 全局 */
body {
    font-family: "Microsoft JhengHei";
    background-color: #dddcd9;
    color: #222;
    font-size: 18px;
    text-shadow: 0 0 1px rgba(0, 0, 0, .2);
}
}

@-moz-document domain("rust.cc") {
/** 顶栏 */
#header {
    position: fixed;
    background-color: #f3f2ee;
    width: 100%;
    left: 0;
    border-radius: 0 0 4px;
    box-shadow: 0 1px 10px 0px rgba(2, 3, 3, .4)
}

#header .header {
    width: 960px;
    margin: auto;
    border-bottom-width: 0;
}

#header .dropdown-content {
    border-radius: 0 0 4px 4px;
    border: none;
    background-color: rgba(240, 240, 240, .9);
    box-shadow: 0 2px 4px 0 rgba(2, 3, 3, .4);
}

#content {
    padding-top: 40px;
}
}

@-moz-document domain("rust.cc") {
/** 内容 */
.detail .border {
    border: none;
}

.detail .underline {
    background-color: #f3f2ee;
    border-radius: 4px;
    border: none;
    box-shadow: 0 0 5px 0 rgba(2, 3, 3, .2);
}

.detail .underline .return {
    background-color: white;
    padding: 4px 8px;
    border-radius: 2px;
    border: 1px solid #dddcd9;
    color: #888;
    transition: color .2s;
}

.detail .underline .return:hover {
    color: #222;
}

.detail .padding-square {
    padding-left: 0;
    padding-right: 0;
}

.detail .detail-body {
    background-color: #f3f2ee;
    padding: 10px;
    border-radius: 2px;
}
}

@-moz-document domain("rust.cc") {
/** 回复 */
#comments {
    padding-left: 0;
    padding-right: 0;
}

#comments button.right {
    padding: 4px 8px;
    color: #888;
    border: 1px solid #dddcd9;
    transition: color .2s;
    cursor: pointer;
}

#comments button.right:hover {
    color: #222;
}

#comments .item {
    background-color: #f3f2ee;
    border-radius: 2px;
    box-shadow: 0 0 4px 0 rgba(2, 3, 3, .2);
}

#comments .item .reply {
    color: #222;
    font-weight: bold;
    float: right;
    text-decoration: underline;
}

#comments blockquote blockquote {
    border-top: 1px solid #aaa;
    border-bottom: 1px solid #aaa;
}
}

@-moz-document domain("rust.cc") {
/** 翻页指示器 */
.paginator.right {
    background-color: #f3f2ee;
    padding: 4px 8px;
    border-radius: 2px;
    box-shadow: 0 0 4px 0 rgba(2, 3, 3, .2);
}
}

@-moz-document domain("rust.cc") {
/** 首页 */
.body-content .planet-space {
    background-color: #f3f2ee;
    border: none;
    border-radius: 4px;
    box-shadow: 0 0 4px 0 rgba(2, 3, 3, .2);
    height: auto;
}

.body-content .planet-space .head {
    font-size: 24px;
    border-bottom-color: #888;
    line-height: unset;
    height: auto;
}

.body-content .one-section {
    width: calc(31% - 13px);
    background-color: #f3f2ee;
    border-radius: 4px;
    border: none;
    box-shadow: 0 0 4px rgba(2, 3, 3, .4);
}

.body-content .one-section:nth-child(3n) {
    margin-right: 0;
}
}
