css:
@-moz-document url-prefix("https://vk.com") {
.top_home_link .top_home_logo {
    background: url(https://gorkarta.ru/i/vk_logo.png) no-repeat 50% / contain;
    width: 190px;
    height: 34px;
    margin: 4px 0px 1px 1px;
    position: fixed !important;
    margin-left: -42px;
    z-index: 99;
}
#side_bar_inner {
	position: fixed !important;
	top: 0;
	margin-top: 57px !important;
	margin-left: -42px;
	z-index: 99
}
.side_bar_inner {
	z-index: 10;
	width: 164px;
	padding: 14px 14px 12px;
	margin-top: 42px;
	background: #222 !important
}
.left_count_wrap,
#side_bar ol li .left_row:hover .left_count_wrap,
.font_medium .left_count_wrap,
.font_medium #side_bar ol li .left_row:hover .left_count_wrap {
	text-align: center;
	background-color: #0251b8;
	padding: 0 !important
}
.left_count_wrap,
#side_bar ol li .left_row:hover .left_count_wrap,
.font_medium .left_count_wrap,
.font_medium #side_bar ol li .left_row:hover .left_count_wrap {
	height: 24px !important;
	min-width: 24px !important;
	width: auto !important;
	padding: 0 5px !important;
	line-height: 24px;
	margin: 2px 2px 0 0 !important;
	border-radius: 15px;
	box-sizing: border-box;
	color: #fff
}
.top_home_link {
	position: relative;
	overflow: hidden
}
.top_home_link:after {
	z-index: 0;
	position: absolute;
	width: 10px;
	height: 10px;
	top: calc(50% - 5px);
	left: calc(50% - 5px);
	content: '';
	visibility: hidden;
	border-radius: 50% !important;
	background: rgba(0, 0, 0, .5);
	opacity: 1
}

.top_home_link:hover:after {
	width: 10px;
	height: 10px;
	visibility: visible;
	animation-name: logo;
	animation-duration: .5s;
	animation-timing-function: ease-in-out;
	animation-fill-mode: both;
	transition-duration: .2s
}
.top_home_link:active:after {
	transform: scale(17) !important
}
@-moz-keyframes logo {
	0% {
		transform: scale(1);
		opacity: 1
	}
	100% {
		transform: scale(20);
		opacity: 0
	}
}
@-webkit-keyframes logo {
	0% {
		transform: scale(1);
		opacity: 1
	}
	100% {
		transform: scale(20);
		opacity: 0
	}
}
@-moz-keyframes logocircle {
	0% {
		transform: scale(0);
		opacity: 1
	}
	100% {
		width: 10px;
		height: 10px;
		transform: scale(10);
		opacity: .2
	}
}
@-webkit-keyframes logocircle {
	0% {
		transform: scale(0);
		opacity: 1
	}
	100% {
		width: 10px;
		height: 10px;
		border-radius: 50%;
		transform: scale(10);
		opacity: .2
	}
}
.top_audio_player .top_audio_player_play>div {
	margin: 0 auto 0
}
.top_audio_player.top_audio_player_playing .top_audio_player_play>div {
	background-position: -15px -78px
}
.top_audio_player .top_audio_player_play {
	width: 30px;
	height: 30px;
	padding: 0 !important;
	text-align: center;
	margin-top: 6px;
	border-radius: 50%
}
.top_audio_player.top_audio_player_playing .top_audio_player_play {
	animation-name: playbtnaudio;
	animation-duration: 2.5s;
	animation-iteration-count: infinite;
	animation-timing-function: linear;
	overflow: visible
}
@-moz-keyframes playbtnaudio {
	0% {
		box-shadow: none
	}
	50% {
		box-shadow: 0 0 0 15px rgba(0, 0, 0, .2);
		background-color: rgba(0, 0, 0, .2)
	}
	100% {
		box-shadow: 0 0 0 30px rgba(0, 0, 0, 0);
		background-color: rgba(0, 0, 0, 0)
	}
}
@-webkit-keyframes playbtnaudio {
	0% {
		box-shadow: none
	}
	50% {
		box-shadow: 0 0 0 15px rgba(0, 0, 0, .2);
		background-color: rgba(0, 0, 0, .2)
	}
	100% {
		box-shadow: 0 0 0 30px rgba(0, 0, 0, 0);
		background-color: rgba(0, 0, 0, 0)
	}
}
