css:
@-moz-document url-prefix("https://sciencepost.fr/") {
body {
    color: #e1e1e1
}
a {
    color: #128C97;
}

/* acceuil */
.g1-delta,
h4 {
    color: #fff;
}
div.g1-row {
    background: linear-gradient(#2c727c, #02727d);
}
img {
    border: 1px #555 solid;
}
.fb-page {
    background: #fff;
    border: 1px #191919 solid;
}
.entry-tpl-grid-fancy,
.entry-tpl-grid {
    background: linear-gradient(to right, #000, #1e1e1e);
    box-shadow: none;
    border: 1px #191919 solid;
}
.entry-title > a {
    color: #e1e1e1;
}
.g1-meta {
    color: #ddd;
}
.entry-tpl-grid-fancy .entry-before-title,
.entry-tpl-grid-fancy .entry-meta {
    background: linear-gradient(to right, #000, #1e1e1e) !important;
}
.entry-tpl-grid .entry-before-title .entry-stats,
.entry-categories .entry-category {
    border-left: none;
    border-top: 1px #e6e6e6 solid;
    border-bottom: 2px #000 solid;
    border-right: 1px #e6e6e6 solid;
    background: linear-gradient(to right, #000, #1e1e1e);
    color: #ddd;
    margin-bottom: -2px;
}
.entry-categories .entry-category:hover {
    border-color: #02727d;
    background-color: #02727d;
    color: #02727d;
}
.g1-pagination {
    background: linear-gradient(to right, #000, #1e1e1e) !important;
}

/* article */
.g1-featured-row {
    background: linear-gradient(to right, #000, #1e1e1e);
    margin-bottom: 0px;
    border-width: 0 0 1px;
    border-style: solid;
    border-color: #000;
}
.g1-meta a,
.g1-mega {
    color: #e1e1e1;
}
.wp-caption-text {
    color: #ddd;
}
header,
.g1-content-narrow {
    background: linear-gradient(to right, #000, #1e1e1e);
    padding: 25px;
}
.entry-tpl-list {
    width: 100%;
    position: relative;
    margin-bottom: 10px;
    background: linear-gradient(to right, #000, #1e1e1e);
    box-shadow: none;
    background-color: #fff;
}
#text-88 > header:nth-child(1) {
    background: transparent;
}
.g1-beta,
h2 {
    color: #e1e1e1;
}

/*  recherche */
.g1-drop-content,
.search-form .search-field {
    background: linear-gradient(to right, #000, #1e1e1e);
}

/* footer*/
.g1-prefooter > .g1-row-background,
.g1-prefooter .g1-current-background {
    border-bottom: 5px #31949D solid;
    border-top: 5px #31949D solid;
}
}

