/* ==UserStyle==
@name           Trello Beautified
@version        1.0.0
@description    Trello Beautified theme for Stylish
@namespace      http://www.w3.org/1999/xhtml
@homepageURL    https://github.com/sirpooya/Trello-Beautified
@supportURL     https://github.com/sirpooya/Trello-Beautified/issues
@author         Pooya Kamel
@license        GPL
==/UserStyle== */

@-moz-document url-prefix("https://trello.com/") {
body, button, html, input, select, textarea {font-family:Segoe UI !important;}

h2, .card-label {
    font-family: Segoe UI !important;
    /*direction: rtl;*/
}
 /*direction: rtl;*/
.list-card-title , p {
    font-family: Segoe UI;
    font-size: 15px;
    line-height: 20px;
}

.attachment-thumbnail p {direction: ltr;}
/* I use the Scrum for Trello extension in addition */

body .list-card-labels {
  float: right;
  top: 2px;
  right: 0px;
  max-width: 200px;
  margin: 0;
  text-align: right;
    padding: 8px 0px;
}

.icon-sm {
    height: 20px;
    width: 20px;
    font-size: 16px;
    line-height: 27px;
}


body .list-card-labels .card-label {
  font-family:roboto,helvetica !important;
  text-transform: uppercase;
  float: right;
  width: auto;
  height: 14px;
  line-height: 14px;
  padding: 0 8px;
  margin: 0 0 2px 2px;
  font-size: 10px;
  font-weight: bold;
  border-radius: 3px;
  overflow: visible;
  text-shadow : none;
  text-align: center;
  vertical-align: middle;
  border-radius:10px;
  min-width:unset;
  max-width:unset;
}


/* display card id */

body .card-short-id.hide {
  position: absolute;
  top: 0;
  left: 0;
  display: block;
  height: 16px;
  line-height: 16px;
  padding: 0 3px;
  font-weight: bold;
  font-size: 11px;
  background: gold;
  border-radius: 3px 0;
}

body .badges .js-plugin-badges {
  position: absolute;
  top: 0;
  left: 30px;
  background: lavender;
  padding: 0 3px;
  display: block;
  height: 16px;
  line-height: 16px;
  border-radius: 0 0 3px 3px;
}

body .badges .js-plugin-badges .badge {
  margin: 0;
  padding: 0;
  color : black;
}



body .badges .js-plugin-badges .badge .badge-text {
  font-size: 10px;
  font-family: interface;
  font-weight: normal;
}

body .has-cover .card-short-id.hide {
  border-radius: 0 0 3px 0;
}

body .list-card-title {
  margin:10px 0px;
}


/* bottom edit link */

body .list-card-operation {
  top: auto;
  right: -2px;
  bottom: -2px;
  padding: 1px;
  border-radius: 3px;
  box-shadow: 1px 1px rgba(0,0,0,.3), -1px -1px white;
}


body .list-card:first-child {
  margin-top: 2px;
}



/* display total cards count */

body .list-header-num-cards.hide {
  display: inline-block;
  font-size:14px;
}
.checklist-new-item.focus .checklist-new-item-text {
    overflow: hidden !important;
    overflow-y: hidden !important;
    /*direction:rtl;*/
    /*text-align : right;*/
    font-family : IRANSans;
}

.window-module-title-icon {
    left : auto;
    right : -38px;
}

.checklist-item {
  clear: both;
  margin-left: 40px;
  padding-left: 10px;
}

.checklist-item-checkbox {
    /* left : auto;
    right : 0; */
    background: #fff;
    border-radius: 3px;
    border: 1px solid #d6dadc;
    border-bottom-color: #c4c9cc;
    box-shadow: 0 2px 3px rgba(0,0,0,.1);
    box-sizing: border-box;
    position: absolute;
    overflow: hidden;
    margin: 5px;
    text-align: center;
    text-indent: 100%;
    height: 20px;
    width: 20px;
    white-space: nowrap;
}
.comment-box-input {
    /*direction:rtl;*/
    /*text-align : right;*/
}
.comment-box-options {
    right : auto;
    left : 0;
}

/* -------------------------------------------------------------------------*/
/* -------------------------------------------------------------------------*/
/* --------------------------------  BY POOYA  -----------------------------*/
/* -------------------------------------------------------------------------*/
/* -------------------------------------------------------------------------*/


.comment-container {zoom: .85;}

.window-module-title.window-module-title-no-divider span {display:none !important;}

.checklist-progress-bar {background: #edeff0;}
.checklist-progress-bar-current {background: #24d202;}
.checklist-item:hover {background: #5f41ff;color: white;}


/* -----  submit button  -----*/
button.primary, input[type=button].primary {
    background: 100% #36cc9b ;border-radius:20px; color:white;}

.list input[type=submit].primary {
    background: #44ac6e ;
    border-radius: 20px !important;
    box-shadow: 0 1px 0 #399852;}

.list input[type=submit].primary:hover {
    background: 100% #399898;}

/* -----  background  -----*/
.board-header-btn {border-radius:16px;}


/*******    header  **********/
#board {padding-top:20px;top:-20px;}
.board-header {zoom:1.2;}

.GAI2GE8czAVHCQ {
    line-height: 40px;}


/* --------- scroolbar -----------*/
.list .u-fancy-scrollbar::-webkit-scrollbar{height:10px;width:3px;display:none;}
.u-fancy-scrollbar::-webkit-scrollbar-track-piece {background: #fff;}
.u-fancy-scrollbar::-webkit-scrollbar-thumb:vertical{background:#ddd;border-radius:2px;height:10px;}

/* --------- list -----------*/
.list-wrapper {width:270px;margin: 0 10px;height: 100%;}

.list-wrapper:first-child {margin-left: 20px;}

/* .list-wrapper:nth-last-child(3),.list-wrapper:nth-last-child(4) {opacity:.8;} */

/* .list-wrapper:nth-child(5) {width:330px !important;} */
/* .list-wrapper:nth-last-child(4) .list-card-title {text-decoration:line-through;} */
/* .list-wrapper:nth-last-child(3) .list-card {background-color:rgba(0,0,0,0);} */

.list-wrapper:nth-last-child(2) .list,.list-wrapper:nth-last-child(2) .list-card {background-color:#ffeed1;}
/* .list-wrapper:nth-last-child(2) .list-card-title {text-decoration:line-through;} */

.list {
    background: #fff;
    border-radius: 8px 8px 1px 1px;
box-shadow:rgba(0, 0, 0, 0.15) 0px 10px 18px 5px;}

.list-header textarea {font-size: 20px;height:30px;}
textarea.mod-list-name {padding:0px;margin:0px;}

.open-card-composer {border-radius: 0 0 1px 1px;
    /*transition:.3s all;*/}

.open-card-composer:hover {background-color: #007eff;
    color: #ffffff;}

.list-header-extras-menu:hover {
    border-radius: 15px;/*transition:.2s all;*/
    background: #f2eefb;}

.list-header { padding: 10px 14px 8px;}



/* -----  small items in card  -----*/
body .card-short-id.hide {
    background:none;
    padding: 2px 0px 0px 5px;
    font-weight: normal;}

body .badges .js-plugin-badges {margin-left: 8px;background:none;top:1px;}


/* -----  cards  -----*/
.list-cards {margin: 0 8px;padding: 0 0px;}
.list-card {
    z-index:auto;
    margin: 0px;
    border: solid 1px rgba(255,255,255,0);
    border-bottom: 1px dashed #ccc;
    border-radius: 0px;
    background-color: #fff/*#f0f0f7*/;}

.list-card:hover .list-card-title {color: #E55D67 ; color: #9139f1  !important;}

.active-card {
/*border:solid 1px #E55D67 !important ;*/
background-color:#feedab;
background-color: #f5f0ff  !important;
/*transition : .3s all;*/}

.hide {color: #00000045;}


/* -----  cards typography  -----*/
.list-card-title {
    font-family: Segoe UI !important;
    font-weight: 600 ;
    font-family: IRANSans medium;
    letter-spacing: -.3px;}

.list-card-details {
    padding: 10px 20px !important;}

.list-card-members > .member,.list-card-members > .member > .member-avatar {width:25px; height:25px;}


/* -----  item - edit  -----*/
.icon-sm.dark-hover {color:#ededed;}
.icon-sm.dark-hover:hover {color:#bdbdbd;}

.list-card-operation {
    left: 0px  !important;
    bottom: -5px  !important;
    border-radius: 13px  !important;
    box-shadow: none  !important;
background-color: #642af2 !important;
z-index:1000;}

.list-card-operation:hover {background-color: #5b2af2 !important;}

.icon-sm.icon-edit {
    height: 25px;
    width: 25px;
    font-size:14px;
    line-height:28px;
}

/* -----  labels colors -----*/

