.list-card-details .card-label.mod-card-front {min-width:8px !important;}

.list-card-details .card-label {padding:0 !important;width:5px !important;height:8px !important;border-radius:10px !important;margin:2px !important;
 text-indent: 100%;
    white-space: nowrap;
    overflow: hidden !important;}





