.body-card-label-text .card-label.mod-card-front
{
    padding-top: 3px;
    border-radius:10px !important;
}


.card-label.mod-card-detail {border-radius: 25px;}
*/




 .card-label-blue.mod-card-front {
    background-color: blue;}

 .card-label-purple.mod-card-front {
    background-color: #b4abff;}

 .card-label-sky.mod-card-front {
    background-color: #ff80ce;}

 .card-label-yellow.mod-card-front {
    background-color: #ffdf42;}

.card-label-orange.mod-card-front {
    background-color: #ff9d57;}

.card-labels-hover .card-label-green.mod-card-front, .card-label-green.mod-card-front {
    background-color: #00d455;}

 .card-label-pink.mod-card-front {
    background-color: #733b26;}


/*******    window  **********/
.window-title {padding-right: 15px;}

.window-header textarea {
    font-size: 24px;
    line-height: 30px;
    margin: 0px 8px 20px 8px;}



.window {border-radius: 14px;background-color: white;}
.window-sidebar .icon-lg,.window-sidebar .icon-sm {color:#000;}
.window .button-link {border-radius: 22px;  background-color: #ffefde;box-shadow: 0 1px 0 0 #ffe2c4 !important;zoom: 1.2;font-size:10px;}
.window .button-link:hover {background: #ffcc70;box-shadow: none !important;color: #fff;}
.window .button-link:hover .icon-sm {color:#fff;}
.window .button-link:hover .on {background: #20dc63;}
.window .button-link .on {border-radius: 18px;}
.window .button-link .on {padding: 2px 1px;color:#fff;}
.window .button-link .on .icon-sm {color:#fff !important;}
.window .button-link:active {background: #fd9f13;}
.button-link.negate {background: #EB5A46;}
.button-link.negate:hover {background: #CF513D;}


.comment-frame {box-shadow:none !important;border-radius: 10px;}
.comment-box-input {background-color: #f2f4f5;border-radius: 10px;}
.comment-box-input:hover {box-shadow:0px 0px 1px 0px rgba(0,0,0,0.3) !important;}


.list-header-num-cards {margin:0px;}


.window-header-inline-content {display:none;}

.card-detail-item-header-edit.hide-on-edit.js-show-with-desc.js-edit-desc {font-size:1px; color: transparent;}
.js-edit-desc:hover {text-decoration:underline !important;}


.card-detail-item-block,.description-content {margin-right:0px !important;margin-top:10px;}

.window-sidebar {
    padding: 8px 2pc 8px 8px;
    width: 140px;}

.current.markeddown {background-color: #fff3dd;border-radius:5px;}

.attachment-thumbnail {
    border-radius: 3px;}

.attachment-thumbnail:hover .attachment-thumbnail-details {
    background-color: #e2f1ff;border-color:#147ce4;}

/*.attachment-thumbnail-details-options-item {float: left;}
tachment-thumbnail-details .attachment-thumbnail-name { float: left;}
*/

.card-detail-item-add-button {
    background: transparent;
    border-radius: 20px;
    color: #838c91;
    border: solid 1px #147ce4;}

.card-detail-item-add-button:hover {
    background: #e2f1ff;border-color:#0062c3;}

.card-detail-item-add-button:hover > .icon-sm.icon-add {color:#0062c3;}

.card-detail-badge {
    background-color: transparent;
    border-radius: 24px;
    color: #147ce4;
    border: solid 1px #147ce4;}

.card-detail-badge.is-clickable:hover,
.card-detail-badge:hover {background: #e2f1ff;border-color:#0062c3;color:#0062c3;}

.quiet-button {
    background: #fff;
    color: #147ce4;}

.quiet-button:active {
    background: #e2f1ff;
    color: #147ce4;}

.quiet-button:hover {
    background: #e2f1ff;
    color: #147ce4;}


.icon-sm.icon-add {color: #147ce4;}



.member,.member-avatar {height:30px;width:30px;}
.card-detail-item.u-clearfix.js-card-detail-members .member {height: 35px;width: 35px;}
.card-detail-item.u-clearfix.js-card-detail-members .member-avatar {height: 35px;width: 35px;}

window-module-title.window-module-title-no-divider {display:none;}

.window-main-col .edit textarea.field {/*direction:rtl;*/background:white;} textarea.field:active {background:white !important;}
.phenom-meta {direction:ltr;}

.markeddown {
    word-wrap: break-word;
    /*background-color: rgb(255, 250, 246);
    background-color: #edeeff;
    border-radius: 7px;*/
    padding: 11px 14px;}

/*.checklist-item-row {direction: rtl;}*/

.action-comment {box-shadow: 0 3px 8px 0px rgba(160, 136, 175, 0.4);}

.atMention.me {color: #137ce4;}
.atMention {font-weight: 500;}


.checklist-new-item-text.js-new-checklist-item-input {overflow-y:none !important;}
.checklist-item:hover {background: #ffe3ca !important;}
.checklist-progress {width:80%;}
.checklist-items-list.js-checklist-items-list js-no-higher-edits.ui-sortable {padding: 0px 40px;}

.list-card.active-card {
    background-color: #edeff0;
    border-bottom-color: #d6dadc;}
}
