css:
@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("http://web.roblox.com"), url-prefix("https://web.roblox.com") {
div.small.dialog-message.dialog-message-content.dialog-triangle.ng-binding,
div.profile-avatar-mask,
.btn-secondary-xs,
span.notification-blue,
a.btn-primary-lg,
a.btn-primary-md,
div.chat-windows-header.dialog-header,
button.btn-secondary-md,
div.avatar-status.chat-friend-status.online,
.btn-large.btn-primary,
.tab.active {
    background: rgb(109, 109, 109) !important;
    border-color: rgb(62, 62, 62) !important;
}

.icon-default-navigation, .icon-nav-menu, .icon-nav-robux, .icon-nav-settings, .icon-nav-search, .icon-nav-home, .icon-nav-profile, .icon-nav-message, .icon-nav-friends, .icon-nav-charactercustomizer, .icon-nav-inventory, .icon-nav-trade, .icon-nav-group, .icon-nav-blog, .icon-nav-search-white, .icon-nav-shop, .icon-nav-notification-stream {
    background-image: url(https://i.imgur.com/Ji2Wdmw.png);
    background-repeat: no-repeat;
    background-size: auto auto;
    width: 28px;
    height: 28px;
    display: inline-block;
    vertical-align: middle;
}

.rbx-header .age-bracket-label {
    display: inline-block;
    size: 0;
    color: transparent;
    padding-top: 13px;
    margin-right: 6px;
}


.input-group-btn .input-dropdown-btn:hover, .input-group-btn .input-dropdown-btn:focus {
    background-color: #ff7d4f;
    box-shadow: 0 1px 3px rgba(150,150,150,0.74);
    border-color: #ff7d4f;
    color: #fff;
    cursor: pointer;
}

.rbx-navbar-search-anchor {
    width: 100%;
    color: #444;
    font-size: 16px;
    border-top: 1px solid #ccc;
    padding: 6px 0;
}

.dropdown-menu {   
    border-radius: 0 0 5px 5px;
    border: 1px solid #ccc;
}

.icon-nav-home {
    text-decoration: none;
    display: block;
    padding: 6px 7px;
    text-shadow: 0 0 1px rgba(255,255,255,0.1);
    font-weight: inherit;
    color: #444;
    position: relative;
}

.rbx-left-col ul {
    padding: 1px 0px 0;
}

body {
    background: white !important;
}

.content {
    max-width: 1338px;
    background-color: white !important;
    padding-top: 12px;
    margin: 0 auto;
}

.games-list-container {
     background: white !important;   
    
}


#navigation {
    background: #e1e2e6;
    
}

.rbx-upgrade-now {
    padding-left: 5px;
}

.section-content {
    background-color: #fff;
    padding: 15px;
    position: relative;
    border-color: #000 !important;
}

.rbx-scrollbar .rbx-upgrade-now {
    margin: 17px 12px 10px;
}

.text-overflow {
  padding-left:10px;  
}

.rbx-header {
    background-color: #2D528F;
    color: #fff;
    text-decoration: none;
}

#chat-header{
    background-color: #2D528F;
    color: #fff;
    text-decoration: none;
}

.menu-item:hover {
   background: rgb(200, 200, 200) !important;
    color: #343434 !important; 
}

.font-bold {
        font-size: 14px;
    color: #999;
    font-weight: bold;
    margin-left: 7px;
}

.rbx-navbar-search .input-field .rbx-navbar-search .input-addon-btn {
    box-sizing: border-box;
    height: 28px;
    box-shadow: none;
    -webkit-appearance: none;
    font: CAPTION;
}

#nav-home {
     padding-left:10px;
}

#nav-home:hover {
    background: rgb(200, 200, 200) !important;
    color: #343434 !important;
}

#nav-profile {
     padding-left:10px;
}

#nav-profile:hover {
    background: rgb(200, 200, 200) !important;
    color: #343434 !important;
}

#nav-character {
     padding-left:10px;
}

#nav-character:hover {
    background: rgb(200, 200, 200) !important;
    color: #343434 !important;
}

#nav-inventory {
     padding-left:10px;
}

#nav-inventory:hover {
    background: rgb(200, 200, 200) !important;
    color: #343434 !important;
}

#nav-shop {
     padding-left:10px;
}

#nav-shop:hover {
    background: rgb(200, 200, 200) !important;
    color: #343434 !important;
}

#nav-friends {
     padding-left:10px;
}

#nav-friends:hover {
    background: rgb(200, 200, 200) !important;
    color: #343434 !important;
}


#nav-message {
     padding-left:10px;
}

#nav-message:hover {
    background: rgb(200, 200, 200) !important;
    color: #343434 !important;
}

#nav-trade {
     padding-left:10px;
}

#nav-trade:hover {
    background: rgb(200, 200, 200) !important;
    color: #343434 !important;
}

#nav-blog {
     padding-left:10px;
}

#nav-blog:hover {
    background: rgb(200, 200, 200) !important;
    color: #343434 !important;
}

#nav-group {
     padding-left:10px;
}

#nav-group:hover {
    background: rgb(200, 200, 200) !important;
    color: #343434 !important;
}

.open .input-dropdown-btn {
    background-color: #FF7D4F;
    color: #fff;
}
h1, h2, h3, h4, h5 {
    font-family: cursive;
    margin: 0;
    padding: 5px 0;
}
.icon-logo {
    /*Main site navbar full logo*/
    background-image: url(https://i.imgur.com/Q9VKmKA.png) !important;
}

.icon-logo-r {
    /*Main site navbar small logo*/
    background-image: url(https://i.imgur.com/i2fY9MN.png) !important;
    background-size: 30px 30px !important;
    background-position-x: 0px !important;
    background-position-y: center !important;
}

.big-logo {
    /*Download page logo*/
    background-image: url(https://i.imgur.com/i2fY9MN.png) !important;
    height: 80px !important;
}

.robloxLogo {
    /*Landing page small logo*/
    content: url("http://i.imgur.com/Hj502As.png");
    /*hides the new logo in the worst way possible idk*/
    background-image: url("https://i.imgur.com/i2fY9MN.png") !important;
    background-size: 167px 167px !important;
    background-position-x: left !important;
    background-position-y: center !important;
    width: 31px;
}

#MainLogoImage {
    /*Landing page big logo*/
    content: url("https://i.imgur.com/i2fY9MN.png") !important;
    margin-left: 20px !important;
}

#LogoAndSlogan .clearfix::after {
    /*Landing page slogan under the logo*/
    content: "Powering Imagination" !important;
    color: white !important;
    font-size: 35px !important;
    background-color: rgba(0, 0, 0, 0.7) !important;
    padding: 10px 25px !important;
    text-align: center !important;
    margin-top: -20px !important;
    display: inline-block !important;
}
.c-version .search-options .menu-link:hover,
.c-version .search-options .menu-link.active {
    color: #FF7D4F;
}
.avatar-back {
    background-image: url("http://i.imgur.com/sufNyPV.png");
}
.breadcrumb-container li.breadcrumb-filter {
    background-color: #FF7D4F;
}
.radio input[type="radio"]:checked + label::after {
    background-color: #FF7D4F;
}
.nav-pills > li.active > a,
.nav-pills > li.active > a:hover,
.nav-pills > li.active > a:focus {
    background-color: #FF7D4F;
}
div.small.dialog-message.dialog-message-content.dialog-triangle.ng-binding {
    color: #ffffff;
}
.tab.active {
    border-radius: 6px 6px 0px 0px;
    color: #ffffff;
}
.tab {
    background: #efefef;
    border-radius: 6px 6px 0px 0px;
    border: 0px;
}
.alert-info {
    background-color: #f5591a;
}
li.rbx-navbar-search-option.selected {
    box-shadow: 4px 0 0 0 rgb(255, 105, 50) inset !important;
}
a:link,
a:visited,
a:active {
    color: #1e2228;
}
div#CreateGroupBtn.btn-large.btn-primary {
    border: 0px;
    border-radius: 10px;
    height: 35px;
}
input#ctl00_cphRoblox_GroupWallPane_NewPostButton.btn-control.btn-control-large.GroupWallPostBtn.translate {
    position: static;
}
input#ctl00_cphRoblox_GroupSearchBar_SearchButton.group-search-button.translate,
input#ctl00_cphRoblox_GroupStatusPane_StatusSubmitButton.btn-control.btn-control-medium.translate {
    border: 0px;
    border-radius: 6px;
    color: #ffffff;
    padding: 12px;
    margin-left: 20px;
    line-height: 0px !important;
}
.btn-control.btn-control-medium.translate,
.group-search-button.translate,
.btn-control.btn-control-large {
    border: 0px;
    border-radius: 4px;
    color: #ffffff;
    background: rgb(255, 105, 50) !important;
}
button.btn-control-medium.btn-control-disabled {
    border: 0px;
    border-radius: 4px;
    color: rgb(100, 100, 100);
    padding: 2px;
    background: rgb(255, 105, 50) !important;
}
.btn-neutral,
.btn-neutral:link,
.btn-neutral:active,
.btn-neutral:visited {
    border-color: #FF7D4F;
    background-color: #FF4705;
    border-radius: 4px;
    background-image: url(https://www.roblox.com/imges/Buttons/StyleGuide/bg-btn-blue.png);
}
.btn-negative,
.btn-negative:link,
.btn-negative:active,
.btn-negative:visited {
    border-color: #ff0505;
    background-color: #FF7D4F;
    background-image: url(https://www.roblox.com/imags/Buttons/StyleGuide/bg-btn-gray.png);
    border-radius: 4px;
}
.content .SearchKeyword,
input#ctl00_cphRoblox_GroupStatusPane_StatusTextBox.translate,
textarea#ctl00_cphRoblox_GroupWallPane_NewPost.GroupWallPostText {
    border: 0px;
    height: 30px;
    background: #DCDADA;
    border-radius: 6px;
    padding-left: 10px;
}
div.section-content {
    border-radius: 6px;
}
textarea#ctl00_cphRoblox_GroupWallPane_NewPost.GroupWallPostText {
    background: #ffffff;
    margin-bottom: 10px;
    margin-right: 25px;
    width: 480px !important;
}
div#SearchControls {
    border: 0px;
    background: #efefef;
    height: 35px;
    border-radius: 6px;
}
#right-column .StandardBox {
    border-radius: 3px;
    border: 1px solid #eee;
    background: #eee;
    padding: 5px;
}
.btn-primary,
.btn-primary:link,
.btn-primary:active,
.btn-primary:visited {
    background-image: none !important;
}
div.GroupListContainer.StandardBox,
div.GroupControlsBox {
    border: 0px solid #ffffff !important;
    border-radius: 6px;
}
div.GroupControlsBox {
    padding-bottom: 10px;
}
.pill-toggle input:checked + label {
    background-color: #FF4705;
}
.btn-secondary-sm {
    background-color: #FF4705;
    border-color: rgba(0, 162, 255, 0);
}
.btn-primary,
.btn-primary:link,
.btn-primary:active,
.btn-primary:visited {
    background-color: #17990C;
    border-color: rgba(0, 162, 255, 0);
    border: 0px solid #ffffff !important;
    border-radius: 6px;
}
.dialog-container .dialog-message-container .dialog-message {
    background-color: #FF4705;
    border: 1px solid #FF4705;
}
.dialog-container .dialog-message-container.message-cluster-master .dialog-triangle:before,
.dialog-container .dialog-message-container.message-cluster-master .dialog-triangle:after {
    border-color: transparent transparent transparent #FF4705;
}
.StatusView .top {
    border-radius: 3px;
    background: #eee;
    padding: 5px;
    width: 317px;
}
.slide-switcher.groups {
    background-color: #FF4705;
}
.StatusView .bottom {
    background: url(https://i.imgur.com/iBPxeaK.png) no-repeat;
    padding: 5px;
    padding-right: 0;
}
.btn-control.btn-control-medium {
    border: 0px;
    border-radius: 4px;
    color: #ffffff;
    background: rgb(255, 105, 50) !important;
}
.btn-control.btn-control-medium {
    height: 21px;
    line-height: 21px;
    font-size: 12px;
    background-image: url(https://www.roblox.com/images/StyleGuide/btn-control-medium-tile.png);
}
.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
.rbx-tabs-horizontal .rbx-tab-heading:hover {
    box-shadow: 0 -4px 0 0 rgb(255, 105, 50) inset;
}
.icon-default-bc,
.icon-bc,
.icon-tbc,
.icon-obc {
    background-image: url(http://i.imgur.com/P4wtM9j.png);
}
div.small.dialog-message.dialog-message-content.dialog-triangle.ng-binding::before {
    border-color: transparent transparent transparent rgb(255, 105, 50);
}

.pagerbtns {
    background: url(https://i.imgur.com/pPwgj4B.png) no-repeat;
}
.icon-game,
.icon-default-friendstatus,
.icon-studio,
.icon-online {
    background-image: url(https://i.imgur.com/lKn0Hsu.png);
}

.CarouselPager .arrow-prev,
.CarouselPager .arrow-next {
    background: url(https://i.imgur.com/WqerIsw.png);
}
.CustomizeCharacterContainer .AssetName a,
.AttireCategory a.AttireCategorySelector,
.AttireCategory .AttireCategorySelector_Selected,
#ctl00_ctl00_cphRoblox_cphMyRobloxContent_cmdInvalidateThumbnails,
.FooterPager a,
.para-overflow-toggle .toggle-para,
.text-link,
.vlist .list-item .list-body .list-content a,
.text-link:link,
.vlist .list-item .list-body .list-content a:link,
.text-link:hover,
.vlist .list-item .list-body .list-content a:hover,
.text-link:visited,
.vlist .list-item .list-body .list-content a:visited,
.text-link:active,
.vlist .list-item .list-body .list-content a:active,
.text-name,
.text-name:link,
.text-name:hover,
.text-name:visited,
.text-name:active,
a.text-link,
.InventoryItemName,
#left-column .StandardBox a,
#mid-column .StandardBox a,
#right-column .StandardBox a,
tr.forum-post > .forum-content-background:first-child .normalTextSmallBold {
    color: #FF4705;
}
.rbx-tab-horizontal-submenu li.active {
    background-color: #FF4705;
}
.rbx-tab-horizontal-submenu li:hover {
    background-color: #FF4705;
}
.menu-vertical .menu-option:hover,
.menu-vertical .menu-option:focus,
.menu-vertical .menu-option.active .menu-secondary-option.active {
    box-shadow: 4px 0 0 0 #FF4705 inset;
}
.input-field:focus {
    box-shadow: none;
    border-color: #FF4705;
}
.voting-panel .users-vote .vote-details .vote-container .vote-percentage {
    background-color: #FF7D4F;
}
.voting-panel .users-vote .upvote span {
    color: #FF7D4F;
}
.voting-panel .users-vote .vote-details .vote-numbers .count-left {
    float: left;
    color: #FF7D4F;
}
.voting-panel .users-vote .vote-details .vote-numbers .count-right {
    float: right;
    color: #999999;
}
.voting-panel .users-vote .vote-details .vote-container .vote-background.has-votes {
    background-color: #999999;
}
.voting-panel .users-vote .downvote span {
    color: #999999;
}
.btn-secondary-md {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-transition: box-shadow 200ms ease-in-out;
    -o-transition: box-shadow 200ms ease-in-out;
    transition: box-shadow 200ms ease-in-out;
    border: 1px solid transparent;
    background-color: #FF4705;
    border-color: #FF4705;
    color: #fff;
    cursor: pointer;
    display: inline-block;
    font-weight: 400;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 9px 9px;
    font-size: 18px;
    line-height: 100%;
    border-radius: 3px;
    size: 70%
}

.btn-secondary-md:hover {
    background-color: #41f330;
    border-color: #41f330;
}
}
