css:
@-moz-document domain(stackoverflow.com) {
#chat-feature, #footer-sites, #left-sidebar, #newsletter-ad, #footer, .s-sidebarwidget, #content > #sidebar, #herobox, .s-hero, .ad, .ad-container, #mainbar #ignored-footer, #help-index .old-hero._orange._small._darken.mb24 .-container, .-container .-main .left-sidebar-toggle.js-left-sidebar-toggle,.inner-content > #sidebar, #answers > h2.bottom-notice { display: none; }
.container { margin: 0 !important; max-width: none !important; }
#content { max-width: none !important; width: 100% !important; padding: 25px; }
#content div[itemtype="http://schema.org/Question"] { width: 100%; }
#mainbar { width: 100% !important; }
.flush-left { margin-left: 0; }
#question-header > .grid--cell.fs-headline1.fl1 { cursor: text !important; }
#question-header > .grid--cell.fs-headline1.fl1 > .question-hyperlink { pointer-events: none; }
.suggested-edit, .suggested-edit table, .suggested-edit table .summary { width: 100% !important; }
#mainbar > .grid > .fs-headline1 { font-size: 1.4em !important; }
#mainbar > .grid > .fs-body3 { font-size: 1.1em !important; }
.top-bar .-container { max-width: 1264px !important; }
}
