css:
@-moz-document domain("azlyrics.com") {
body {
    font-family: Stalemate;
    font-size: 15px;
    font-style: italic;
    color: #333;
    text-shadow: 1px 1px #fff;
}
body {
    background-image: url(https://www.cjoint.com/doc/18_07/HGCnIecZvXE_3408318-sheet-music-wallpapers.jpg);
    background-size: cover;
    background-repeat: no-repeat;
    background-attachment: fixed;
}
.main-page {
    background-color: transparent;
    color: #555;
    text-shadow: 1px 1px #fff;
    padding-top: 0;
    padding-bottom: 7.5px;
}

/* header   */
.navbar-default {
    background-color: transparent;
    border-color: transparent;
}
.lboard-wrap,
.links-menu-wrap {
    display: none;
}
.lboard-wrap,
.links-menu-wrap {
    padding-top: 15px;
    padding-bottom: 15px;
    background-color: #669;
}
.pull-left {
    margin-left: -80px;
}
.btn-menu {
    background-color: transparent;
    color: #555;
    text-shadow: 1px 1px #fff;
    border-color: #c2851a;
    margin-left: 4px;
    margin-right: 3px;
    font-family: Stalemate;
    font-style: italic;
}
.btn-menu:hover {
    background-color: #c2851a;
    text-shadow: 1px 1px #fff;
    border-color: #c2851a;
    margin-left: 4px;
    margin-right: 3px;
    box-shadow: 1px 1px #fff
}

/* search */
.form-control {
    color: #555;
    background-color: transparent;
    border: 1px solid #c2851a;
    border-radius: 4px;
    box-shadow: 1px 1px #fff;
    text-shadow: 1px 1px #fff;
}
.form-control:focus {
    border-color: #c2851a;
    box-shadow: 1px 1px #c2851a;
    background-color: rgba(221, 221, 228, 0.3);
}
.btn-primary.active,
.btn-primary.focus,
.btn-primary:active,
.btn-primary:focus,
.btn-primary:hover,
.open > .dropdown-toggle.btn-primary {
    color: #fff;
    text-shadow: 1px 1px #fff;
    background-color: #c2851a;
    border-color: #c2851a;
}
.btn-primary {
    color: #555;
    text-shadow: 1px 1px #fff;
    background-color: transparent;
    border-color: #c2851a;
}

/*  home page  */
.col-lg-4 {
    width: 70%;
}
a:focus,
a:hover {
    color: #c2851a;
    text-shadow: 1px 1px #fff;
}
a {
    color: #333;
    font-weight: bold;
    text-shadow: 1px 1px #fff;
}
.navbar-default .navbar-nav > li > a {
    color: #333;
    text-shadow: 1px 1px #fff;
}
.navbar-default .navbar-nav > li > a:hover {
    color: #c2851a;
    text-shadow: 1px 1px #fff;
}
.navbar-footer {
    background-color: rgba(221, 221, 228, 0.3);
    color: #555;
    text-shadow: 1px 1px #fff;
}
.footer-wrap {
    background-color: rgba(221, 221, 228, 0.3);
    text-align: center;
}

/* lyrics page */
.btn-share {
    background-color: #c2851a;
    border: 1px solid #c2851a;
    font-family: Helvetica;
    padding: 0 2px;
}
.btn-share,
.sorting {
    margin-left: 15px;
    margin-right: auto;
}
.btn-share:active,
.btn-share:focus,
.btn-share:hover {
    background-color: #c2851a;
    border: 1px solid #fff;
}
.div-share h1 {
    color: #c2851a;
    text-shadow: 1px 1px #fff;
}
  /* footer */
.breadcrumb,
.panel {
    background-color: rgba(221, 221, 228, 0.3);
}
.alert-info {
    color: #c2851a;
    background-color: rgba(221, 221, 228, 0.3);
    border-color: #fff;
    text-shadow: 1px 1px #fff;
}
  /* submit */
.alert-warning {
    color: #555;
    background-color: rgba(221, 221, 228, 0.3);
    border-color: #faebcc;
    text-shadow: 1px 1px #fff;
}
.panel-info > .panel-heading {
    color: #c2851a;
    background-color: rgba(221, 221, 228, 0.3);
    border-color: #fff;
    text-shadow: 1px 1px #fff;
}
.table > tbody > tr.warning > td,
.table > tbody > tr.warning > th,
.table > tbody > tr > td.warning,
.table > tbody > tr > th.warning,
.table > tfoot > tr.warning > td,
.table > tfoot > tr.warning > th,
.table > tfoot > tr > td.warning,
.table > tfoot > tr > th.warning,
.table > thead > tr.warning > td,
.table > thead > tr.warning > th,
.table > thead > tr > td.warning,
.table > thead > tr > th.warning {
    background-color: rgba(221, 221, 228, 0.3);
}
}
