css:
@-moz-document domain("wikipedia.org") {
/* cyrillic-ext */
@font-face {
  font-family: 'Lora';
  font-style: normal;
  font-weight: 400;
  src: local('Lora Regular'), local('Lora-Regular'), url(https://fonts.gstatic.com/s/lora/v12/0QIvMX1D_JOuMwf7I_FMl_GW8g.woff2) format('woff2');
  unicode-range: U+0460-052F, U+1C80-1C88, U+20B4, U+2DE0-2DFF, U+A640-A69F, U+FE2E-FE2F;
}
/* cyrillic */
@font-face {
  font-family: 'Lora';
  font-style: normal;
  font-weight: 400;
  src: local('Lora Regular'), local('Lora-Regular'), url(https://fonts.gstatic.com/s/lora/v12/0QIvMX1D_JOuMw77I_FMl_GW8g.woff2) format('woff2');
  unicode-range: U+0400-045F, U+0490-0491, U+04B0-04B1, U+2116;
}
/* vietnamese */
@font-face {
  font-family: 'Lora';
  font-style: normal;
  font-weight: 400;
  src: local('Lora Regular'), local('Lora-Regular'), url(https://fonts.gstatic.com/s/lora/v12/0QIvMX1D_JOuMwX7I_FMl_GW8g.woff2) format('woff2');
  unicode-range: U+0102-0103, U+0110-0111, U+1EA0-1EF9, U+20AB;
}
/* latin-ext */
@font-face {
  font-family: 'Lora';
  font-style: normal;
  font-weight: 400;
  src: local('Lora Regular'), local('Lora-Regular'), url(https://fonts.gstatic.com/s/lora/v12/0QIvMX1D_JOuMwT7I_FMl_GW8g.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Lora';
  font-style: normal;
  font-weight: 400;
  src: local('Lora Regular'), local('Lora-Regular'), url(https://fonts.gstatic.com/s/lora/v12/0QIvMX1D_JOuMwr7I_FMl_E.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

body {
    font-family: 'Lora', serif;
    background-color: #FFF;
}
#mw-page-base {
    background: none;
}
.vectorTabs,
.vectorTabs span,
.vectorTabs ul,
.vectorTabs ul li {
    background-image: none;
}
#mw-head #left-navigation ul li a,
#mw-head #right-navigation ul li a {
    background-color: rgba(0, 0, 0, 0);
    color: rgba(0, 0, 0, 0.6);
    cursor: pointer;
    fill: rgba(0, 0, 0, 0.6);
    font-family: medium-content-sans-serif-font, "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Geneva, Arial, sans-serif;
    font-size: 15px;
    font-style: normal;
    font-weight: 400;
    height: auto;
    letter-spacing: 0.75px;
    line-height: 20px;
    text-align: left;
    text-decoration-color: rgba(0, 0, 0, 0.85);
    text-decoration-line: none;
    text-decoration-style: solid;
    text-rendering: optimizeLegibility;
    text-transform: uppercase;
    visibility: visible;
    white-space: nowrap;
    width: auto;
    transition: all 0.6s;
}

#mw-panel ul li a, h3{
     background-color: rgba(0, 0, 0, 0);
    color: rgba(0, 0, 0, 0.6);
    cursor: pointer;
    fill: rgba(0, 0, 0, 0.6);
    font-family: medium-content-sans-serif-font, "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Geneva, Arial, sans-serif;
    font-size: 0.8em;
    font-style: normal;
    font-weight: 400;
    height: auto;
    letter-spacing: 0.75px;
    line-height: 20px;
    text-align: left;
    text-decoration-color: rgba(0, 0, 0, 0.85);
    text-decoration-line: none;
    text-decoration-style: solid;
    text-rendering: optimizeLegibility;
    text-transform: uppercase;
    visibility: visible;
    white-space: nowrap;
    width: auto;
    transition: all 0.6s;
}

#mw-head #left-navigation ul li a:hover,
#mw-head #right-navigation ul li a:hover {
    color: rgba(0, 0, 0, 0.8);
}
h1 {
    font-size: 3em !important;
    font-weight: bold;
    border-bottom: 3px black solid;
}
.mw-body-content h2 {
    font-weight: bold;
    font-size: 2.1em;
    border-bottom: 2px black solid;
}
.mw-body-content h3 {
    font-weight: bold;
    font-size: 1.8em;
}
#content {
    max-width: 60%;
    border: none;
    margin-left: auto;
    margin-right: auto;
}

#content p {
    margin-top: 1em;
    margin-bottom: 2em;
}
.mw-body-content {
    font-size: 1.2em;
    line-height: 1.6;
}
.mw-body-content a {
    color: black;
    
    background: rgba(12,242,143,0.1);
    
}

#mw-panel .portal .body li a {
    color: #03a87c;
}
#mw-panel {
    opacity: 0.3;
    color: black;
    transition: all 0.8s;
}

#mw-panel:hover{
    opacity: 1;
}
.toc {
    border: none;
}
#content table {
    background-color: #f8f9fa;
    border: none;
}
#content table th {
    border: none;
}
#content .thumb {
    min-width: 30%;
}
#content .thumbinner {
    width: 100%;
    padding: 5px;
    border: none;
    background-color: #f8f9fa;
}
#content .thumbcaption {
    font-size: 0.84em;
}
#content ul li {
    margin-bottom: 1em;
}
#simpleSearch {
    padding-top: 5px;
    border: none;
    border-bottom: 1.5px black solid;
    background: none;
}
#simpleSearch #searchButton {}
#p-personal ul li a {
    color: #03a87c;
    font-family: medium-content-sans-serif-font, "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Geneva, Arial, sans-serif;
}
.mw-editsection, #footer {
    opacity: 0.1;
    transition: all 0.6s;
}
.mw-editsection:hover, #footer:hover {
    opacity: 1;
}
}
