css:
@-moz-document domain('gitlab.com') {

 div.js-contrib-calendar rect[fill="#ededed"] {
    background: #212426 !important;
    fill: #393f3f !important;
}
.avatar-circle, .avatar, .avatar-container {
	border-radius: 0px !important;
	border: none !important;
    background: none !important;
}
.header-user-avatar,
.avatar.avatar-inline.s24, .page-content-header .avatar.s24 {
	border-radius: 0 !important;
	border: 1px solid #444 !important;
}
.code.white, .code.white pre.code, .code.white .line_holder .line_content {
	color: #fff !important;
	background-color: #2b3030 !important;
}
.code.white .line_holder .diff-line-num.old,
.code.white .line_holder .diff-line-num.old,
.code.white .line_holder .line_content.old span.idiff {
	background-color: #380000 !important;
}
.code.white .line_holder .diff-line-num.old a {
	color: #ffffff !important;
}
.code.white .line_holder .diff-line-num.new,
.code.white .line_holder .line_content.new span.idiff {
	background-color: #002800 !important;
}
.code.white .line_holder .line_content.new {
	background-color: #181818 !important;
}
 tr.line_holder:hover .diff-line-num {
	background-color: #2b3030 !important;
	border: none;
	color: #fff !important;
}
 a::before {
    color: #393f3f !important;
}
 tr.line_holder:hover a::before {
    color: #fff !important;
}
.code.white .nt {
	color: #0075ff !important;
}
.code.white .na {
	color: #009000;
}
 .line_holder:hover .line_content.noteable_line,
.line_holder:hover .line_content.match {
	background-color: #181818 !important;
}
h1,
.group-nav-container .nav-controls,
.commit-box,
.branch-info.well-segment {
    border-color: #393f3f !important;
}
.project-repo-buttons .count-with-arrow .arrow::before, .group-buttons .count-with-arrow .arrow::before {
	border-right-color: #181818 !important;
}
.project-repo-buttons .count-with-arrow .arrow::after, .group-buttons .count-with-arrow .arrow::after {
	border-right-color: #181818 !important;
}
.disabled-comment {
	background-color: #212121 !important;
	border-color: transparent !important;
    border-radius: 0 !important;
    border-top: solid 1px #393f3f !important;
	padding: 20px 0 !important;
}
.btn-transparent {
    background: transparent !important;
}
.dismiss-icon,
.dismiss-icon * {
    box-shadow: none !important;
    text-shadow: none !important;
    color: #ff5529 !important;
    font-size: 2rem !important;
}
.js-autodevops-banner {
    border-color: #444 !important;
}
div.container-fluid:nth-child(5) {
    padding-top: 4px !important;
    padding-bottom: 1px !important;
}
div.container-fluid:nth-child(1) {
    padding-top: 6px !important;
    padding-bottom: 6px !important;
}
div.commit-detail.flex-list {
    padding-top: 7px !important;
}
div.commit-sha-group .label {
    border: dashed 1px #777 !important;
}
.commit-sha-group :first-child {
	border-radius: 4px;
}
div.avatar-cell a .avatar {
    padding: 0px !important;
    border-radius: 0 !important;
}
div.avatar-cell a .avatar {
    border: solid 1px #444 !important;
}
span.input-group-btn {
    background: transparent !important;
}
header.navbar {
    box-shadow: 0 0 2px -1px black !important;
}
.content-block {
    border-color: #444 !important;
}
span.star-count {
    background: #181818 !important;
    border-color: #181818 !important;
    border-right: solid 1px #212121 !important;
}
  
}
