css:
@-moz-document url-prefix("https://gogoanimes.co/"), url-prefix("https://www5.gogoanime.in/"), url-prefix("https://www05.gogoanimes.tv/"), url-prefix("https://www1.gogoanime.io/") {

a{
 font-size: 13px;   
}
.container {
  display: inline-block;
}
nav.menu_recent ul li {
  font-size: 13px;
  color: black;
}
nav.menu_recent ul li a {
  border-right: 80px solid #1b1b1b;
  border-radius: 5px;
}
nav.menu_recent ul li a:after {
  content: 'watched';
  position: absolute;
  color: #1b1b1b;
}
nav.menu_recent ul li a:visited,
nav.menu_recent ul li a:visited:hover {
  border-color: #ffc119;
}
nav.menu_recent ul li {
    font-size: 10px;
    color: black;
    height: 61px;
}
nav.menu_recent ul li:first-child {
    height: 44px;
}
}

@-moz-document url-prefix("https://gogoanimes.co/"), url-prefix("https://www5.gogoanime.in/"), url-prefix("https://www05.gogoanimes.tv/"), url-prefix("https://www1.gogoanime.io/") {
a{
 font-size: 13px;   
}
.container {
  display: inline-block;
}
ul.items li {
  font-size: 13px;
  color: black;
}
ul.items li a {
  border-right: 85px solid #1b1b1b;
  border-radius: 5px;
}
ul.items li a:after {
  content: 'watched';
  position: absolute;
  color: #1b1b1b;
}
ul.items li a:visited,
ul.items li a:visited:hover {
  border-color: #ffc119;
}
ul.items li .img a {
    width:146%;
}

ul.items li .img a:after {
    position: absolute;
    content:"Watched";
}
}

@-moz-document url-prefix("https://gogoanimes.co/"), url-prefix("https://www5.gogoanime.in/"), url-prefix("https://www05.gogoanimes.tv/"), url-prefix("https://www1.gogoanime.io/") {
.jw-controlbar {
    opacity: 0;
}

.jw-controlbar:hover {
    opacity: 1;
}
}
