css:
@-moz-document domain("office.com") {
/*GLOBAL*/

.fluent-background-color {
    background-color: #101010;
}

/*Titles*/

.section.section--fluent h2,
.section-header--flex .section-header__title.section-header__title--fluent {
    color: #dddddd;
}

.list-header__title.list-header__title--fluent {
    color: #cccccc;
}

/*Items*/

.list-item--shadowed:hover {
    background-color: #333333;
}

.expandable-list__item--bordered:first-child.expandable-list__item--fluent {
    border-color: #333333;
}

.expandable-list__item--bordered {
    border-color: #333333;
}

.ms-Callout-main {
    background-color: #212121;
}

.workload-item--fluent--provisioned:hover {
    background-color: #333333 !important;
}

/*Texts*/

.section--fluent .list-item__text--large {
    color: #cccccc;
}

.workload-item__name--fluent {
    color: #cccccc;
}

.list-item__text--small {
    color: #dddddd;
}

/*BUTTONS*/

.generic-button--fluent--secondary,
.generic-button--fluent--primary {
    background-color: #212121;
    color: #ffffff;
}

.generic-button--fluent--secondary:hover,
.generic-button--fluent--primary:hover {
    background-color: #333333;
    color: #ffffff;
}

.commanding-item:focus,
.commanding-item:hover {
    background-color: #333333;
}

.actions-menu.ms-Callout .menu-item,
.menu-item {
    background-color: #212121;
    color: #ffffff;
}

.menu-item:focus,
.menu-item:hover {
    background-color: #333333 !important;
}

.menu-item__title {
    color: #ffffff;
}

.o365cs-base .o365sx-neutral-secondary-font,
.o365cs-base .o365sx-neutral-secondary-hover-font:hover {
    color: white;
}

/*Office 365 navbar*/

.o365sx-navbar {
    background-color: #000000;
}

.o365sx-appName,
.o365sx-appName:hover {
    color: #dddddd;
}

.o365sx-waffle {
    background-color: #000000;
    color: #dddddd;
}

.o365cs-base .o365sx-button {
    color: #ffffff;
    background-color: #000000 !important;
}

.o365sx-neutral-lighterAlt-background,
.o365sx-neutral-lighterAlt-hover-background:hover {
    background-color: #212121;
}

.o365sx-neutral-dark-font,
.o365sx-neutral-dark-hover-font:hover {
    color: #cccccc;
}

.o365sx-neutral-secondary-font,
.o365sx-neutral-secondary-hover-font:hover {
    color: #dddddd;
}

.o365sx-button:hover {
    background-color: #212121;
    box-shadow: 0 -9px 15px 4px #000000;
}

#appLauncher {
    background-color: #212121;
}

#appLauncher > button {
    color: #ffffff;
    background-color: #212121 !important;
}
#appLauncher > button:hover {
    background-color: #333333 !important;
}

._3ChjdrgMKOROcCP2ukT_Bl {
    color: #dddddd !important;
}

._1A3uXjsN5xo_84FKeEt3-z {
    color: #ffffff;
}

.GluoxRaAop_OrXqwdKl8V:hover[href],
#appsModule > h2 {
    border: none;
    color: #ffffff;
}

#appsModule a {
    color: #ffffff;
}

#O365_MainLink_NavMenu {
    background: none;
    color: white;
}
#O365_MainLink_NavMenu:hover {
    background: #212121;
    color: white;
}

.o365cs-base .o365sx-neutral-lighterAlt-background,
.o365cs-base .o365sx-neutral-lighterAlt-hover-background:hover {
    background-color: #212121 !important;
}

.o365cs-base .o365sx-neutral-foreground-background,
.o365cs-base .o365sx-neutral-foreground-hover-background:hover {
    background: transparent;
}

.o365cs-base .o365sx-neutral-dark-font,
.o365cs-base .o365sx-neutral-dark-hover-font:hover {
    color: #ffffff;
}

a:focus,
a:hover {
    color: #cccccc;
}

.o365cs-base .o365sx-neutral-staticAccent-font,
.o365cs-base .o365sx-neutral-staticAccent-hover-font:hover {
    color: #cccccc;
}

}
