css:
@-moz-document url-prefix("https://twinery.org/questions") {
body {
    font-family: Verdana, Helvetica, sans-serif;
}
pre code {
    background: unset;
    font-family: Source Code Pro, Courier, monospace, sans-serif;
}
pre {
    background: #eee;
    border: 1px double #111;
}
.comment pre {
    background: #fff;
}
.qa-a-item-buttons,
.qa-c-item-buttons,
.qa-q-view-buttons {
    border-top: 1px solid #888;
}
button,
input[type=submit],
li.qa-page-links-item a,
li.qa-page-links-item span:not(.qa-page-ellipsis) {
    box-shadow: 2px 2px 4px #888;
}
button:active,
button:hover,
input[type=submit]:active,
input[type=submit]:hover,
li.qa-page-links-item a:active,
li.qa-page-links-item a:hover {
    box-shadow: 1px 1px 2px #888;
}
li.qa-page-links-item,
span.qa-page-links-label {
    margin-right: .5rem;
}
.qa-search button,
.qa-search button:active,
.qa-search button:hover,
.qa-search input[type=submit],
.qa-search input[type=submit]:active,
.qa-search input[type=submit]:hover,
.qa-voting button,
.qa-voting button:active,
.qa-voting button:hover,
.qa-voting input[type=submit],
.qa-voting input[type=submit]:active,
.qa-voting input[type=submit]:hover,
h1 button,
h1 button:active,
h1 button:hover,
h1 input[type=submit],
h1 input[type=submit]:active,
h1 input[type=submit]:hover,
input.qa-favorite-button,
input.qa-favorite-button:hover,
input.qa-favorite-button:active {
    box-shadow: unset;
}
.comment {
    box-shadow: 1px 1px 2px #888;
}
.qa-part-a-list,
.qa-part-q-view {
    box-shadow: 0px 0px 4px #888;
}
.qa-part-a-form {
    background: unset;
}
.cke_chrome {
    box-shadow: 1px 1px 2px #888;
}
.qa-part-form-activity,
.qa-part-form-password,
.qa-part-form-profile,
.qa-part-ranking,
.qa-q-list-item {
    box-shadow: 0 0 4px #888;
}
.qa-q-list-item {
    margin-top: 1rem;
}
.qa-voting {
    width: 65px;
    float: unset;
}
.qa-q-item-title {
    font-family: Ubuntu, Helvetica, sans-serif;
}
a.qa-tag-link {
    background: purple;
}
a.qa-tag-link:active,
a.qa-tag-link:hover {
    background: violet;
    font-weight: 700;
}
.qa-sidepanel {
    padding-left: .7rem;
}
.qa-a-count {
    background-color: #45c;
}
.qa-a-count-zero {
    background-color: #c45;
}
.qa-a-count-selected {
    background-color: #4c8;
}
h1, .qa-suggest-next, .qa-main-heading {
    background: #34a;
}
.qam-ask-mobile a.turquoise,
.qam-search-mobile.turquoise,
.qam-search.turquoise .qa-search {
    background-color: #3a7;
}
.qam-search.turquoise .qa-search-button {
    background-color: rgba(10, 10, 10, .3);
}
.qam-search.turquoise .qa-search-button:hover {
    background-color: rgba(255, 255, 255, .3);
}
.qam-ask-mobile a.turquoise:active,
.qam-ask-mobile a.turquoise:hover,
.qam-search-mobile.turquoise:active,
.qam-search-mobile.turquoise:hover {
    background-color: #3c7;
    text-shadow: 0 0 2px #fff;
}
.qa-page-links-item a:hover,
.qa-page-links-item a:focus {
    background: #45c;
}
.qa-vote-down-button {
    bottom: 11px;
}
.qa-vote-up-button:hover,
.qa-vote-up-button:focus,
.qa-vote-up-button:active {
    background-position: 0 -10.5px;
}
.qa-suggest-next {
     margin-top: 1rem;
}
}
