css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nixcraft.com"),
domain("cyberciti.biz")
  
  {
    
/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: #242529 !important;
}

select, button, div#page, div.entry-content div, div#multimedia_box, div.body_wrapper, li.forumbit_post div, div.wgo_subblock, div.navlinks, div.thread_controls div, div.userinfo, div.postdetails, div.posthead, div.postbody, div.floatcontainer, div.above_body

{
background: #242529 !important;
}

span

{
background-color: #242529 !important;
}

select, input, span, code, textarea, pre, label, div, dd

{
color: white !important;
}


/*links*/

a

{
color: #2BB24C !important;
}

a:visited

{
color: #3CE565 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}
    
  }
