@-moz-document url-prefix("https://m.weibo.cn/beta"), url-prefix("https://m.weibo.cn/profile/") {
.main-wrap {
    margin: 2.75rem 0 1.75rem!important;
    transition: all 0.4s ease;
}

.lite-topbar.main-top {
    width: auto;
    height: 2.75rem;
}
.lite-topbar .nav-search {
    left: 60%;
    right: 7.75rem;
}

.lite-topbar.main-top .nav-main {
    top: 0;
    right: 40%;
}



@media screen and (max-width: 768px) {

    .lite-topbar .nav-search {
        left: 0;
        right: 0;
    }

    .lite-topbar.main-top .nav-main {
        top: 2.5rem;
        right: 0;
    }

    .lite-topbar.main-top {
        width: auto;
        height: 5rem;
    }

    .main-wrap {
        margin: 5rem 0 1.75rem!important;
    }

    .lite-topbar .nav-left {
        top: auto!important;
        left: .75rem!important;
        right: auto!important;
    }

    .lite-topbar .nav-search {
        left: 2.75rem!important;
        right: 5.375rem!important;
    }
}






/*上面三个图标*/
.lite-topbar .nav-left {
    top: -.175rem;
    right: 5.75rem;
    left: auto
}

.lite-iconf-profile:hover:before {
    color: var(--blue);
}




.lite-topbar .nav-right .lite-iconf-msg {
    cursor: pointer;
}
.lite-topbar .nav-right .lite-iconf-msg:hover {
    color: var(--red);
}
.lite-topbar .nav-right .lite-iconf-releas {
    cursor: pointer;
}
.lite-topbar .nav-right .lite-iconf-releas:hover:before {
    color: var(--orange);
}


/**/
.wb-item-wrap {
    border-radius: 5px;
}

.wb-item {
    transition: all 0.4s ease;
}

.f-footer-ctrl {
    padding: .75rem .375rem .75rem 0;
}

.f-footer-ctrl h4 {
    cursor: pointer;
}

.f-footer-ctrl .lite-iconf-report:hover {
    color: var(--green);
}







/*转发*/
.f-footer-ctrl .lite-iconf-comments:hover {
    color: var(--blue);
}







/*评论*/
.f-footer-ctrl .lite-iconf-like:hover {
    color: var(--red);
}







/*赞*/
.f-weibo.card9 .weibo-main .media-b .m-auto-list {
    margin: auto;
    min-width: 100%;
}
.card9 .weibo-main .weibo-text {
    color: var(--secondary);
}

.f-weibo .weibo-rp .weibo-text {
    border-style: solid;
    border-width: 0 0 0 .275rem;
    border-color: rgba(110, 110, 110, 0.233);
    padding: 0 0 0 .375rem;
    color: var(--secondary);
}

.f-weibo .weibo-top .m-text-box {
    margin: 0 0 .15rem .5rem
}






/*用户id位置*/
.f-weibo.card9 .weibo-top .m-text-box h3 {
    font-size: .8rem;
}

.f-weibo .m-text-box h4 {
    font-size: .4rem;
}







/*方头像*/
.m-avatar-box .m-img-box img {
    border-radius: 5px;
    /*
    border-width: 1px;
    border-style: solid;
    border-color: rgba(110, 110, 110, 0.233);
    */
}

.card9 > .card-wrap {
    padding-top: .85rem;
}

.wb-item-wrap .card9.card {
    margin: 0;
}

#app > div.main-wrap > div.lite-topbar.main-top > div.nav-main > div > div.m-box-col.inner-box > div.lite-nav-sublist.m-col-4 > ul > li > span {
    transition: all 0.15s ease;
}

#app > div.main-wrap > div.lite-topbar.main-top > div.nav-main > div > div.m-box-col.inner-box > div.lite-nav-sublist.m-col-4 > ul > li > span:hover {
    background-color: #ddd;
}



/*nav 除第一个文字*/
#app > div.main-wrap > div.lite-topbar.main-top > div.nav-main > div > div.m-box-col.inner-box > div > div > ul > li.item_li > span {
    transition: all 0.15s ease;
}
#app > div.main-wrap > div.lite-topbar.main-top > div.nav-main > div > div.m-box-col.inner-box > div > div > ul > li.item_li > span:hover {
    color: var(--gray-dark);
}


/*nav 第一个文字*/
#app > div.main-wrap > div.lite-topbar.main-top > div.nav-main > div > div.m-box-col.inner-box > div > div > ul > li.item_li.cur > span:nth-child(1) {
    transition: all 0.15s ease;
    color: var(--gray-dark)!important;
}

#app > div.main-wrap > div.lite-topbar.main-top > div.nav-main > div > div.m-box-col.inner-box > div > div > ul > li.item_li.cur > span:nth-child(1):hover {
    color: #000;
}

.lite-topbar .nav-main .nav_item .item_li.cur span em {
    display: none;
}
#app > div.main-wrap > div.lite-topbar.main-top > div.nav-main > div > div.m-box-col.inner-box > div > div > ul > li.item_li.cur {
    display: inline-block;
    vertical-align: middle;
    /*
    error
    
    transform: perspective(1px) translateZ(0);
    */
    box-shadow: 0 0 1px rgba(0, 0, 0, 0);
    position: relative;
    overflow: hidden;
}
#app > div.main-wrap > div.lite-topbar.main-top > div.nav-main > div > div.m-box-col.inner-box > div > div > ul > li.item_li.cur:before {
    content: "";
    position: absolute;
    z-index: -1;
    top: 81%;
    left: 31%;
    right: 31%;

    bottom: -.5rem;
    background: #282f3c;
    height: 2px;

    -webkit-transition-property: left, right;
    transition-property: left, right;
    -webkit-transition-duration: 0.3s;
    transition-duration: 0.3s;
    -webkit-transition-timing-function: ease-out;
    transition-timing-function: ease-out;
}
#app > div.main-wrap > div.lite-topbar.main-top > div.nav-main > div > div.m-box-col.inner-box > div > div > ul > li.item_li.cur:hover:before,
#app > div.main-wrap > div.lite-topbar.main-top > div.nav-main > div > div.m-box-col.inner-box > div > div > ul > li.item_li.cur:focus:before,
#app > div.main-wrap > div.lite-topbar.main-top > div.nav-main > div > div.m-box-col.inner-box > div > div > ul > li.item_li.cur:active:before {
    left: 0;
    right: 0;
}



/*刷新*/
.refresh-btn {
    font-size: 1.125rem!important;
    border-radius: 5px!important;
}


.m-search div {
    cursor: text;
}

.lite-topbar .nav-main .nav-plus .m-font {
    cursor: pointer;
}


/*图片圆角*/
.f-weibo .f-bg-img {
    border-radius: 10px!important;
}
.f-weibo .m-img-box {
    background-color: rgb(0, 0, 0, 0);
}


/*视频圆角*/
.mwb-video .mwbv-info-bar {
    display: none!important;
}
.mwb-video {
    background-color: rgb(0, 0, 0, 0)!important;
}
.mwb-video .m-img-box {
    border-radius: 10px!important;
}
}

