@-moz-document url-prefix("https://m.weibo.cn/profile/") {
/*
m.weibo.cn/profile/
*/
.profile-header .prf-handle .bar-btn .m-btn-lite-white:after {
    border-radius: 15px;
}
#app > div:nth-child(1) > div.profile-header.lite-bot-line > div.prf-handle.m-box > div.bar-btn.m-box-col > a {
    transition: all 0.3s ease;
}
#app > div:nth-child(1) > div.profile-header.lite-bot-line > div.prf-handle.m-box > div.bar-btn.m-box-col > a:hover {
    color: var(--blue);
}
.profile-header .prf-topbar .nav-right:hover {
    color: #0056b3;
    text-decoration: underline;
}

.profile-header .prf-handle .dm-btn:after {
    border-radius: 10px;
    transition: all 0.3s ease;
}
#app > div:nth-child(1) > div.profile-header.lite-bot-line > div.prf-handle.m-box {
    transition: all 0.3s ease;
}
}

@-moz-document url-prefix("https://m.weibo.cn/beta"), url-prefix("https://m.weibo.cn/profile/") {
/*!
 * Bootstrap v4.1.0 (https://getbootstrap.com/)
 * Copyright 2011-2018 The Bootstrap Authors
 * Copyright 2011-2018 Twitter, Inc.
 * Licensed under MIT (https://github.com/twbs/bootstrap/blob/master/LICENSE)
 */
:root {
    --blue: #007bff;
    --indigo: #6610f2;
    --purple: #6f42c1;
    --pink: #e83e8c;
    --red: #dc3545;
    --orange: #fd7e14;
    --yellow: #ffc107;
    --green: #28a745;
    --teal: #20c997;
    --cyan: #17a2b8;
    --white: #fff;
    --gray: #6c757d;
    --gray-dark: #343a40;
    --primary: #007bff;
    --secondary: #6c757d;
    --success: #28a745;
    --info: #17a2b8;
    --warning: #ffc107;
    --danger: #dc3545;
    --light: #f8f9fa;
    --dark: #343a40;
    --breakpoint-xs: 0;
    --breakpoint-sm: 576px;
    --breakpoint-md: 768px;
    --breakpoint-lg: 992px;
    --breakpoint-xl: 1200px;
    --font-family-sans-serif: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    --font-family-monospace: SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
}



html {
    font-family: sans-serif;
}

article,
aside,
dialog,
figcaption,
figure,
footer,
header,
hgroup,
main,
nav,
section {
    display: block;
}


a {
    color: #007bff;
    text-decoration: none;
    background-color: transparent;
    -webkit-text-decoration-skip: objects;
}

a:hover {
    color: #0056b3;
    text-decoration: underline;
}

a:not([href]):not([tabindex]) {
    color: inherit;
    text-decoration: none;
}

a:not([href]):not([tabindex]):hover,
a:not([href]):not([tabindex]):focus {
    color: inherit;
    text-decoration: none;
}

a:not([href]):not([tabindex]):focus {
    outline: 0;
}


img {
    vertical-align: middle;
    border-style: none;
}
}

@-moz-document url-prefix("https://m.weibo.cn/compose/"), url-prefix("https://m.weibo.cn/message/") {
.m-reles-nr span textarea {
    font-family: "Microsoft Yahei", "PingFang SC", "Source Han Sans CN", -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    font-size: 0.9rem;
    font-style: normal;
}
#app > div.chat-page-wrap > div.lite-page-editor > div > div > div > div.textarea-box > textarea {
    font-family: "Microsoft Yahei", "PingFang SC", "Source Han Sans CN", -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    font-size: 0.9rem;
    font-style: normal;
    max-width: 100%!important;
    min-width: 5rem!important;
    min-height: 3rem!important;
}
html {
    overflow-x: hidden;
    overflow-y: auto;
}
}
