css:
@-moz-document url-prefix("http://lpf2.ro/") {
body {
    font-family: Segoe UI, "Helvetica Neue", Helvetica, Arial, sans-serif !important;
    color: #ddd;
    background-color: #222 !important;
}
.rightmenu {
    background: #222;
}
.rightmenu .colleft {
    color: #ddd;
    background: #222;
}
a {
    color: #ddd;
}
a:hover {
    color: #fff;
    background: none;
}

#header {
	clear: both;
	float: left;
	width: 100%;
	background: #433;
}

#etape {
	background: #111;
}

.captabel {
	color: #eee;
	background-color: #666;
}
.grouptable {
    border-width: 0px;
    background-color: #444;
}
.grouptablegri {
    border-width: 0px;
    background-color: #333;
}
.grouptableliga2 {
    border-width: 0px;
    background-color: #533;
}
.grouptableleft {
    border-width: 0px;
}
.grouptableright {
	border-width: 0px;
}

.col1 table {
	border: 3px solid #555;
	box-shadow: none;
}

#social {
	background-color: #222;
	border-radius: 0px;
	border: 0px;
	box-shadow: none;
}

.titlu {
	color: #ddd;
}

span.question {
	background-color: #444;
	font-weight: bold;
}

img.flag {
	box-shadow: none;
}

.col1 img {
	border-radius: 0px;
	border: none;
	box-shadow: none;
}

.tstgalben {
	background-color: #440;
}

.txtrosu {
	font-family: Verdana,Arial;
	font-size: 10pt;
	color: #fff;
}

.tstrosu {
	background-color: #533;
}

.tstverde {
	background-color: #353;
}

.txtverdegros {
	font-family: none;
	color: #4d6;
}

.txtrosugros {
	font-family: none;
	color: #d46;
}

.txtnormb {
	font-family: none;
}
}
