css:
/*
    
    Theme Name: How-To-Type Dark Surface

    Creator: Adam-J-Frost.
    
    Updated: 2020/01/06

    Created: 2018/07/30
    
    Description:

    This is a Dark Style for How-To-Type.com.

    Hope you like it and thanks for using it!

*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("how-to-type.com") {
body {
    background: #0d0d0d;
}
a:focus,
a:hover {
    color: #007fff;
}
#header {
    background: #0d0d0d;
    background-image: none;
    border-bottom: 1px solid #2e2e2e;
}
#features {
    background-color: #1e1e1e;
    font-family: Courier New, Arial, Helvetica, sans-serif;
    padding-bottom: 2em;
}
#splash {
    background: #1e1e1e;
    height: 150px;
    padding-top: 40px;
}
#splash h1 {
    text-shadow: 0 0 0;
}
#logo a {
    color: #ffffff;
}
#logo a:hover {
    color: #007fff;
}
#logo h1 {
    color: #fff;
    text-shadow: 0 0 0;
}
#logo h2 {
    color: #ffffff;
}
.lesson {
    background: #1a1a1a;
    color: #fff;
    box-shadow: 0 0 0;
}
.lesson a {
    color: #007fff;
}
.quote {
    font-family: Courier;
    background: #1a1a1a linear-gradient(#3a3a3a, #2a2a2a);
    border: 1px solid #2e2e2e;
    border-radius: 0;
}
div.result {
    background: #1a1a1a linear-gradient(#3a3a3a, #2a2a2a);
    border: 1px solid #2e2e2e;
    border-radius: 0;
}
.cursor {
    background: #007fff;
}
.correct {
    color: #007fff;
}
.large.stat,
.large.stat .bottom,
.large.stat.errors .details {
    color: #007fff;
}
.button {
    background: #1a1a1a;
    border: 1px solid #2e2e2e;
    color: #ffffff;
    border-radius: 2px;
    box-shadow: 0 0 0;
}
.button:hover {
    background: #2a2a2a;
    border: 1px solid #2e2e2e;
    color: #ffffff;
    border-radius: 2px;
    box-shadow: 0 0 0;
}
a:hover .button {
    background: #007fff;
    color: #ffffff;
    text-decoration: none;
    transform: scale(1.1);
    box-shadow: 0 0px 15px #007fff;
}
.lesson-list ul a {
    background: #1a1a1a;
    color: #ffffff;
}
.lesson-list a:hover {
    background: #007fff;
    color: #ffffff;
    box-shadow: 0 0px 10px #007fff;
}
.lesson-list a {
    background: #007fff;
    color: #fff;
}
.choice {
    background-color: #1a1a1a;
    border: 1px solid #2e2e2e;
    border-radius: 2px;
    color: #ffffff;
    padding: 5px;
    margin: 20px;
    margin-right: 20px;
    margin-bottom: 20px;
    clear: both;
    float: right;
}
#footer {
    background-image: none;
    height: 75px !important;
}
div#wrapper {
    padding-bottom: 75px;
    margin-bottom: 50px;
}
#footer p {
    margin: 0;
    padding: 3px;
    padding-right: 5em;
    line-height: normal;
    font-size: 10px;
    text-transform: lowercase;
    text-align: center;
}
#octopus_side_img {
    display: none;
}
}
