css:
@-moz-document domain("tureng.com"), domain("termbank.com") {
.tureng-navbar, .tureng-index > .row:first-child, .tureng-index-content-area .tureng-index-content, .dropdown-menu, .tureng-searchresults-col-left, .tureng-searchform-container-container .affix, .tureng-content-col-left {
    background-color: #141d26;
}
div[style*="background-color: #efefef;"] {
    background-color: #141d26!important;
}

.tureng-searchform-container-container .affix {
    box-shadow: 0 1px 1px 0 #131313;
}

.table-striped > tbody > tr:nth-child(2n+1) {
    background-color: #141d26;
}

body {
    background-color: #1B2836;
    color:#8798A5;
}

.searchResultsTable th {
    background-color: #131313 !important;
}

.tureng-searchform-textbox, .btn-dictionary-selector, .btn-searchareaicon {
    background-color: #1B2836;
    color:#8798A5;
    border:1px solid #131313;
}

.btn-dictionary-selector {
    border-left:none;
}

div[style*="background-color:#efefef;"]{
    display: none!important;
}

.dropdown-menu > li > a {
    color:#8798A5;
}

.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
    color: #8798A5;
    background-color: #1B2836;
}

.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top: 1px solid #131313;
}

div[id*="div-gpt-ad"], div[id*="crt-"] {
    display: none!important;
}

.table-striped > tbody > tr:nth-child(2n) > td {
    background-color: #1B2836;
}

.searchResultsTable a {
    color: #c2ccd1 !important;
}

.table-hover > tbody > tr:hover > td {
    background-color: #131313 !important;
}

.searchResultsTable .rc4 {
    color: #7b7878;
}

.btn-default {
    background-color: #1b2836;
    border-color: #131313;
}

.row a {
    color: #7b7878;
}

.row a:hover {
    color: #7b7878;
}

.btn-default:hover, .btn-default:focus, .btn-default.focus, .btn-default:active, .btn-default.active, .open > .dropdown-toggle.btn-default {
    background-color: #131313;
    border-color: #131313;
}

#crossDictionaryTabs .active a {
    background-color: #131313;
}

#crossDictionaryTabs a:hover {
    background-color: #131313;
}

#crossDictionaryTabs a, footer a {
    color: #7b7878;
}

footer a:hover {
    color: #c2ccd1;
}

#crossDictionaryTabs a {
    background-color: #141d26;
}

.modal-header {
    border-bottom: 1px solid #131313;
}

.modal-content {
    background-color: #1b2836;
}

.modal-footer {
    border-top: 1px solid #131313;
}

.btn-default, .btn-default:hover {
    color: #c2ccd1;
}

.external-search-item-box > a {
    background-color: #141d26;
}

.external-search-item-box > a:hover {
    background-color: #131313;
}

.book-list-item:hover {
    background-color: #131313;
}

.tureng-index-logo img {
    display: none;
}

.btn:hover, .btn:focus, .btn.focus {
    color: #c2ccd1;
}


.tureng-navbar a img {
    background: url(data:image/png;base64,) no-repeat left center;
padding: 37px 0 0 0;
background-size: contain;
width: 25px;
}

.btn-searchareaicon:hover, .btn-searchareaicon:focus, .btn-searchareaicon.focus, .btn-searchareaicon:active, .btn-searchareaicon.active {

    background-color: #131313;
}

.btn-vkeyboard {
    background: #1B2836 url("data:image/png;base64,") no-repeat center!important;
    border-left: none;
    color: transparent;
}

.btn-clearsearch {
    background: #1B2836 url("data:image/png;base64,") no-repeat center!important;
    color: transparent;
    border-left: none;
}

.btn-turengsearch {
    border-color: #131313;
}

.ui-autocomplete {
    background-color: #141d26;
    box-shadow: 0 2px 6px 0 #131313;
}

.ui-menu .ui-state-focus, .ui-menu .ui-state-active {
    background-color: #131313;
}

.helperArea {
    background-color: #141d26;
    border-right: transparent!important;
    border-bottom: 1px solid #131313;
}

#history li a {
    color: #c2ccd1;
}

.tureng-singleblock-content {
    background-color: #141d26;
}

.ui-state-default {
    background: #131313;
}


}
