css:
@-moz-document domain("lolmasters.net") {
body.light-theme {
	color: #AFAFAF !important;
	background-color: #212121;
}

body.light-theme td, 
body.light-theme th, 
body.light-theme .btn{
	color: #CFCFCF !important;
}

body.light-theme .card-panel {
	background-color: #414141;
}

body.light-theme .card-panel2{
	background-color: #515151;
}

body.light-theme .top-nav {
	background-color: #37474F;
}

body.light-theme thead{
	background-color: #37474F;
}

body.light-theme table.bordered {
    border: 1px solid #515151 !important;
}

body.light-theme table.bordered tr {
    border-bottom: 1px solid #515151 !important;
}

body.light-theme td{
	background-color: #414141;
}

body.light-theme .lolm-skin-palette-1{
	background-color: #37474F;
}

body.light-theme .lolm-skin-palette-2{
	background-color: #515151;
}

body.light-theme .lolm-skin-palette-3-fg{
	color: #AFAFAF;
}

body.light-theme .role-image {
	filter: invert(100%);
}

body.light-theme .spell-cell-palette{
	background-color: #AAAAAA;
}

body.light-theme .btnchoice{
	color: #CFCFCF !important;
	background-color: #616161 !important;
}

body.light-theme .btnactive{
	color: #CFCFCF !important;
	background-color: #37474F !important;
}

body.light-theme .repeatedHeader{
	/*#78909C*/
	background-color: #37474F !important;
}

body.light-theme .modal, 
body.light-theme .modal-footer{
	background-color: #212121 !important;
}

body.light-theme .tabs{
	background-color: #414141 !important;
}

body.light-theme .tabs .tab a{
	color: #AFAFAF !important;
}

body.light-theme .tabs .tab a.active{
	color: #313131 !important;
}

.switch label .lever, .switch {
    display: none;
    visibility: hidden;
}
}

@-moz-document domain("lolmasters.net") {
body.light-theme .modal, body.light-theme .modal-footer{
    visibility: hidden;
}
}
