css:
@-moz-document domain("celsoazevedo.com") {
body,
.content,
.sticky,
.title a,
.post-content a,
.info,
.info a,
.barfixed {
    background: #101010;
    color: #e5e5e5;
}

.listapks li {
    padding: 3px 4px
}
.listapks li:nth-child(even) {
    background: #1a1a1a;
}
hr {
    border: 0;
    height: 0;
    border-bottom: 2px solid
}
.fixes a,
.devs a,
.devspost a {
    display: inline-block;
    color: #e5e5e5;
    text-decoration: none;
    padding: 5px;
    background-color: #1a1a1a;
    border-radius: 5px;
    text-shadow: 1px 1px #000;
    margin-bottom: 3px
}
.footer a {
    color: #e5e5e5;
    text-decoration: none;
    padding: 5px;
    background-color: #1a1a1a;
    border-radius: 5px;
    text-shadow: 1px 1px #000
}

.devinfo span {
    background: #101010;
}
}
