css:
@-moz-document domain("speedhunters.com") {
body, #fs-navbar {
  background: #111 !important;
}

section.top-nav .menu-strip .menu-left {
  padding-top: 3px;
  padding-bottom: 7px;
}

.dotted {
  border-color: #555 !important;
}

p, h1, h2, h3, h4, dl, .fs-navbar-number,.homepage-entry-title, .story-date, .ajax-title, .s-logos h3, .sh-post-chapter-break {
  color: white !important;
}

.fs-navbar-download, .fs-navbar-close {
  filter: invert(1);
}

.top-bar-section ul li>a, .home, .blog, .sticky-nav, .card-hero, .card-hero .with-image .story-meta, .card .meta, .card-second .meta, .row, .top-bar.chapter-nav, .chapter-content, .inline-mpu-container {
  background: black !important;
}

.menu-strip.row {
  background: #333 !important;
}

section[class*='featured-'] .home-filters .active a {
  color: gray !important;
}

.de_comments .de-select.de-select-filter dt, .de_comments .de-select.de-select-filter dd {
  background-color: #222 !important;
  border-color: #222 !important;
}

.loader-ball-scale>div {
  background-color: #ed6964 !important;
}

.decomments-comment-body::before {
   background-color: #333 !important;
}

.decomments-autor-name {
  color: #888 !important;
}

.round-wrap {
  background: #fff !important;
}

.comment, .decomments-comment-list.decomments-avatar-right, blockquote {
  background: #000 !important;
}
}
