css:
@-moz-document domain("jandan.net") {
/*
设置背景色字体
*/
body,
html {
	background-color: /*[[background-color]]*/;
	font-family: "Microsoft YaHei", "Calibri", "Tahoma", "Arial";
    font-size: /*[[font-size]]*/;
}

input {
	background: gainsboro;
}

#content .title {
	background-color: /*[[background-color]]*/;
}

.tucao-content {
	background: gainsboro;
}

/*
隐藏备案信息
*/
div#footer {
	display: none;
}

div.jandan-vote {
	font-size: 16px;
}

div.jandan-vote > span {
	font-size: 16px;
}

div.jandan-vote > span > a {
	font-size: 16px;
}

#sidebar,
#tucao-gg {
	display: none;
}
}
