@-moz-document regexp("chrome-extension://apmmpaebfobifelkijhaljbmpcgbjbdo/edit.html.*") {
/* ------------ Body ------------- */
body {
  background: #101010 !important;
  color: #CCC !important;
}

#header {
  background: #202020 !important;
  border-right: 1px solid #303030 !important;
}

#sections > div:not(:first-of-type) {
  border-color: #202020 !important;
}
/* --------- Help Popups --------- */

#help-popup {
  background: #303030 !important;
}

.title {
  background: #404040 !important;
}
/* --------- Input Bars ---------- */

#name,
input,
select {
  background: #303030 !important;
  border: 1px solid #404040 !important;
  color: #CCC !important;
}

.style-contributor {
  background: #202020 !important;
  border-color: #303030 !important;
}
/* ----------- Buttons ----------- */
  /* --------- Header ---------- */

#save-button,
#beautify,
#cancel-button,
#from-mozilla,
#to-mozilla {
  background: #303030 !important;
  border: 1px solid #404040 !important;
  color: #CCC !important;
}

#save-button:hover,
#beautify:hover,
#cancel-button:hover,
#from-mozilla:hover,
#to-mozilla:hover {
  background: #404040 !important;
  border-color: #505050 !important;
  color: #CCC !important;
}

#save-button:disabled {
  color: #999 !important;
}

#save-button:disabled:hover {
  background: #303030 !important;
  border-color: #404040 !important;
}
  /* ---------- Body ----------- */

.remove-section,
.add-section,
.clone-section,
.move-section-up,
.move-section-down,
.beautify-section,
.test-regexp,
.remove-applies-to,
.add-applies-to {
  background: #202020 !important;
  border: 1px solid #303030 !important;
  color: #CCC !important;
}

.remove-section:hover,
.add-section:hover,
.clone-section:hover,
.move-section-up:hover,
.move-section-down:hover,
.beautify-section:hover,
.test-regexp:hover,
.remove-applies-to:hover,
.add-applies-to:hover {
  background: #303030 !important;
  border-color: #404040 !important;
  color: #CCC !important;
}
}

@-moz-document regexp("chrome-extension://apmmpaebfobifelkijhaljbmpcgbjbdo/manage.html") {
/* ------------ Body ------------- */
body {
  background: #101010 !important;
  color: #CCC !important;
}

#header {
  background: #202020 !important;
  border-right: 1px solid #303030 !important;
}

a {
  color: #CCC !important;
}

/* a:hover {
  color: #888888 !important;
} */

#installed::after {
  background: transparent !important;
}

[style-id] {
  border-color: #202020 !important;
}



/* --------- Filters ---------- */

/* #filters label:hover, #filters .filter-selection:hover{
  background-color: none !important;
} */


.filter-selection select{
  background: #202020 !important;
  color: #CCC !important;
}

.filter-selection select:hover {
  background: #303030 !important;
  color: #CCC !important;
}

/* --------- Input Bars ---------- */

#search {
  background: #303030 !important;
  border: 1px solid #404040 !important;
  color: #CCC !important;
}

 #manage\.newUI\.sort {
  background: #303030 !important;
  border: 1px solid #404040 !important;  
  color: #CCC !important;
}

#manage\.newUI\.targets {
  background-color: #303030 !important;
  border: 1px solid #404040 !important;  
  color: #CCC !important;
}

/* ----------- Buttons ----------- */
  /* --------- Header ---------- */

#check-all-updates,
#add-style-label,
#manage-options-button,
#manage-shortcuts-button,
#find-editor-styles > button,
#file-all-styles,
#unfile-all-styles, 
#apply-all-updates {
  background: #303030 !important;
  border: 1px solid #404040 !important;
  color: #CCC !important;
}

#check-all-updates:hover,
#add-style-label:hover,
#manage-options-button:hover,
#manage-shortcuts-button:hover,
#find-editor-styles > button:hover,
#file-all-styles:hover,
#unfile-all-styles:hover, 
#apply-all-updates:hover {
  background: #404040 !important;
  border-color:  #505050 !important;
  color: #CCC !important;
}

#backup-buttons > div > button, #backup-buttons > div > div {
  background-color: #303030 !important;
  border: 1px solid #404040 !important;
  color: #CCC !important;
}

#backup-buttons .dropdown-content a:hover {
  background-color: #404040 !important;
  border: 1px solid #505050 !important;
  color: #CCC !important;
}


/* ---------- Body ----------- */

.style-edit-link button,
.enable,
.disable,
.delete,
.check-update, .update {
  background: #202020 !important;
  border: 1px solid #303030 !important;
  color: #CCC !important;
}

.style-edit-link button:hover,
.enable:hover,
.disable:hover,
.delete:hover,
.check-update:hover, .update:hover {
  background: #303030 !important;
  border-color: #404040 !important;
  color: #CCC !important;
}

/* #style-18 > h2 > div > input  */
input[type="checkbox"] {
    background-color: #303030 !important;
    border: 1px solid #404040 !important;
}

/* install update ribbon */
.newUI .can-update:not([data-details$="locally edited"]) .update:after {
  background: #303030 !important;
  border: 1px solid #404040 !important;
  color: #CCC !important;
}

/* update installed ribbon */
.newUI .updater-icons > :not(.check-update):after {
    background-color: #303030 !important;
    border: 1px solid #404040 !important;
    animation: fadeout 10s;
}
}

