*, table.table0 tbody tr td, a.user-2, input, body#food-search-results p.fields input.text, input.text, select.select, textarea.textarea {
	transition: .2s ease;
	font-family: SF display, Roboto;
    letter-spacing: -.5px;
}

/*remove mfp logo*/
div#logo a {
	opacity: 0;
}

/*hide useless underarmour branding bars and other junk*/
nav.globalTopNav-2GCw3, .uacf-logo-wrapper, #navTop li.last  {
	display: none;
}
/*reduce headers for added screen real estate*/
.header-wrap.clearfix {
	position: fixed;
	background: white;
	width: 100%;
	z-index: 1;
	opacity: 0;
	margin-top: -40px;
}

.header-wrap.clearfix:hover {
	opacity: .9;
	margin-top: -10px;
	z-index: 1!important;
}

#header #nav-bg {
	margin-top: 0px;
}

div#header {
	margin-top: -75px;
}

div#header:hover {
	margin-top: 0;
}
.header-wrap.clearfix:hover div#header {
	margin-top: 0;
}
/*end of header shit*/

/*beginning of food diary shit*/

table {
	width: fit-content;
}

#main {
	float: none;
	margin-left: auto;
	margin-right: auto;
}

div#content {
	padding-top: 0px;
}

div.food_container.container {
	padding-left: 10px;
}
/*don't preface date with 'food diary for'*/
div.diary h1 {
	display: none;
}

div.diary {
    border-bottom: 3px solid /*#ed2f6b*/ white
}
table.table0 tbody tr td {
	border-color: transparent;
}

/*the add food and quick tools links*/
body#food-diary table.table0 tbody tr.bottom td a {
	opacity: 0;
	letter-spacing: 0;
}


body#food-diary table.table0 tbody tr.bottom td a:hover {
	opacity: 1;
}

/*meal titles*/
body#food-diary table.table0 tbody tr.meal_header td.first {
    font-weight: 900;
    border-top: 4px solid white!important;
    text-transform: uppercase;
    color: #ed2f6b;
}

body#food-diary table.table0 tbody tr.meal_header td.first::after {
    content: ':'
}

body#food-diary table.table0 tbody tr td.first {
	font-weight: 00;
    font-size: 15px;
}

body#food-diary table.table0 tbody tr td {
	font-size: 15px;
	padding: 8px 3px;
}

td.delete a {
	color: #ed2f6b!important;
    opacity: .2;
}

#diary-table > tbody > tr > td.delete {
	background: transparent!important;
	border: none;
}

#date_controls .date>time {
	font-size: 22px;
	font-weight: 600;
    text-transform: uppercase;
}

#food-diary #diary-table .nutrient-column, #exercise-diary #diary-table .nutrient-column {
	font-weight: 400;
	font-size: 11px;
    max-width: 20px;
}

#complete_day {
    text-align: left;
    padding-left: 10px;
}

a.button.complete-this-day-button {
	background-image: none;
	border: none;
	border-radius: 0px;
	font-size: 16px;
	font-weight: 800!important;
	text-transform: uppercase;
	letter-spacing: 0;
	background-color: #ed2f6b;
}

/*hide water stuff at bottom of page*/
.block.water-notes-v2 {
	opacity: 0;
}

/*larger font for add food list items*/
body#food-add table.table0 tbody td {
	font-size: 16px;
	letter-spacing: 0;
}

