css:
@-moz-document url-prefix("https://yts.am"), url-prefix("https://yts.gs"), url-prefix("https://yts.ag"), url-prefix("https://yts.lt"), url-prefix("https://yts.pm"), url-prefix("https://yts.mx") {
body { background: #1b2836; color: #8798A5; }
.bg-black {background-color: #141d26;}
.nav-bar { background: #141D26; border-bottom: 1px solid #0C1218 }
.content-dark { background: #1B2836; }
#background-image { background: red !important; }
#background-overlay { background: linear-gradient(to bottom, rgba(20, 29, 38, 0.93) 0, rgba(27, 40, 54, 0.86) 100%); }
footer { background: #141D26; border-top: 1px solid #0C1218 }
#quick-search-input { border: 1px solid #0c1218; border-radius: 0; background: #1b2836 url(https://yts.am/assets/images/website/icon-search.svg) no-repeat 10px 11px; }
.home-content #popular-downloads h2, .home-content .home-movies h2 { border-bottom: 1px solid rgb(12, 18, 24); color: #8798A5; }
.browse-movie-tags a { background: #141d26; }
.non-touch .browse-movie-link:hover { border: 2px solid #6ac045; }
.browse-movie-link { border: 2px solid #141d26; }
.browse-content { background-color: #1b2836; }
#main-search-fields input, .selects-container select { background-color: #141D26; }
ul.tsc_paginationA06 li a, .pagination-bordered, #movie-info p a, .quality-tags { background: #141D26; border: 1px solid #0C1218 }
ul.tsc_paginationA06 li.unavailable { border: 1px solid #0C1218 }
#movie-tech-specs .tech-spec-info, #movie-tech-specs .selected, #comments .comment, #comments #no-comments, #comments #load-comments, #comments-box, #info-page form .contact { background-color: #141D26; }
#movie-tech-specs .tech-quality { padding: 10px 20px; }
figcaption .rating { position: absolute; top: 0px; color: #8798A5; font-size: 11px; background: #141d26; text-align: end; border-right: 4px solid transparent; }
figcaption .rating::before { content: "\e607"; font-family: icomoon; padding: 0 4px 0 0; }
figcaption span.icon-star { display: none; }
.hidden-xs.hidden-sm { opacity: 1; }
figcaption .button-green-download2-big, figcaption .button-green-download-big { display: none; }
figure figcaption h4:not(.rating) { font-size: 11px; top: 116px; background: #141d2680; }
figcaption .rating.red { background: #7e110d; color: #8798a5; }
figcaption .rating.green { background: #184A02; color: #8798a5; }
figcaption .rating.yellow { background: #774C00; color: #8798a5; }
.button-green-download2-big { background: #141d26; }
#movie-poster .bookmark { color: #8798a5!important; }
#movie-poster .bookmarked { color: #6ac045!important; }
.notice-flash { background-color: #1b2836; color: #6ac045; border-bottom: 1px solid #141d26; }
#movie-poster img { border: 5px solid #141d26; }
#movie-related a, #info-page .recent-downloads a { border: 4px solid #141d26; }
.browse-movie-year { position: absolute; top: 0px; z-index: 99; padding: 2px 0 0 2px; font-size: 12px; color: #8798a5; }
.browse-movie-year::after { content: "\e603"; font-family: icomoon; padding: 0 0 0 3px; font-size: 11px; }
.browse-movie-tags a { margin-top: 5px; padding: 1px 3px; }
.browse-movie-title { padding: 0 0 0 4px; color: #8798a5; }
.browse-movie-title:hover { background: #0c1218; transition: 0.3s all; }
.gray-bg { background-color: #1b2836; }
#comments-box textarea { background: #1b2836; }
.line, #movie-sub-info .list-cast, #movie-tech-specs .tech-spec-element div { border-bottom: 1px solid #141d26; }
#comments-box input.pull-right { background: #1b2836; padding: 5px; }
#comments-box textarea { background: #1b2836; width: 100% !important; resize: vertical !important; }
#movie-info h1, #movie-info h2, #mobile-movie-info h1, #mobile-movie-info h2, #info-page h1 { color: #8798a5; }
#movie-info .hidden-xs h1 { font-size: 28px; margin: 0; }
#movie-info .hidden-xs h2 { font-size: 13px; display: contents; color: #919191; }
.modal-container { background-color: #1b2836; color: #8798a5; }
.container.home-content .hidden-xs:nth-child(1), .addthis_bar.addthis_bar_bottom.at-topBottom-animateIn, .hidden-xs.hidden-sm .ibox-bordered, #at4-share { display: none !important; }
.modal-content .quality-size { color: #8798A5; }
.modal-content .modal-torrent { border-right: 1px solid #141d26; }
figcaption.hidden-xs { display: block !important; }
.container.hidden-md.hidden-lg { background: #141d26; }
#comments span.report-btn, #comments span.delete-btn { color: #8798A5; }
#mobile-search-input input, .admin-search input { background-color: #141d26; border: 2px solid #10171f; }
.form-control:focus { box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(0, 0, 0, 0.6); }
#mobile-search-input {background: #10171f;border-bottom: 1px solid #14151c;}

}
