    color: #999894;
}
.page-header .weather span.last {
    color: #81a0bb;
}
#weather-wrapper > a:hover, .page-header .weather span.last:hover {
    color: #d9d8d1;
}
.page-header .icn-w {
    background-image: url(https://i.imgur.com/ScZ1CLB.png);
}
.page-header .sun, .page-header .clear, .page-header .hot {
    background-position: 0 0;
}
.page-header .ptcloudy {
    background-position: -40px 0;
}
.page-header .mscloudy {
    background-position: -80px 0;
}
