}
/* borders below top */
 .bdrgr2, a.link-bdrgr2, a.linkro-bdrgr2:hover, a:hover .linkro-bdrgr2 {
    border: 1px solid #333;
}
/* ---logos and other images--- */
 #logo {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(https://i.imgur.com/juSTiFM.png) no-repeat;
    width: 350px;
    height: 66px;
    padding-left: 350px;
}
.newsnews .masthead {
    background-image: url(https://i.imgur.com/E53eHjL.png);
}
.masthead {
    height: 91px;
    background-position: 100%;
    background-repeat: no-repeat;
    position: relative;
}
div.home.item.html_snippet_module > img {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(https://i.imgur.com/sLx3NFy.png) no-repeat;
