    color: #b7b6b0;
}
.nav-secondary, .page-header .nav-secondary, .bdrgr2, a.link-bdrgr2, a.linkro-bdrgr2:hover, a:hover .linkro-bdrgr2 {
    border: 0;
}
.nav-secondary li, .page-header .nav-secondary li {
    border-left: 1px solid #e8f3ff;
}
.money-mini-navigation-button {
    background-color: #892688 !important;
    color: #d9d8d1 !important;
}
/* curves */
 .tl, .tr, .bl, .br {
    visibility: hidden
}
div.page-header.bdrgr2 > ul > li > .link-wocc a, div.page-header.bdrgr2 > ul > li > .linkro-wocc a:hover {
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
/* ---beta sidebar--- */

/* searchbar */
