css:
@-moz-document domain("askamanager.org"), domain("dumbingofage.com"), domain("captainawkward.com"), domain("dearcoquette.com") {
/* level 1 */
/*darkred orange gold green blue purple violet */
#commentlist > li.comment,
li.comment.depth-1,
ul#post-list > li.post,

#commentlist > li.comment > ol > li.comment > ol > li.comment > ol > li.comment > ol > li.comment > ol > li.comment > ol > li.comment > ol > li.comment
{
    border-left: 1px solid #1565C0 !important;
}
/* level 2 */
#commentlist > li.comment > ol > li.comment,
ul#post-list > li.post > ul.children > li.post,
li.comment.depth-2
{
    border-left: 1px solid #4527A0 !important;
}
/* level 3 */
#commentlist > li.comment > ol > li.comment > ol > li.comment,
ul#post-list > li.post > ul.children > li.post > ul.children > li.post,
li.comment.depth-3
{
    border-left: 1px solid #AA00FF !important;
}
/* level 4 */
#commentlist > li.comment > ol > li.comment > ol > li.comment > ol > li.comment,
ul#post-list > li.post > ul.children > li.post > ul.children > li.post > ul.children > li.post,
li.comment.depth-4
{
    border-left: 1px solid #C62828 !important;
}
/* level 5 */
#commentlist > li.comment > ol > li.comment > ol > li.comment > ol > li.comment > ol > li.comment,
ul#post-list > li.post > ul.children > li.post > ul.children > li.post > ul.children > li.post > ul.children > li.post,
li.comment.depth-5
{
    border-left: 1px solid #FF8F00 !important;
}
/* level 6 */
#commentlist > li.comment > ol > li.comment > ol > li.comment > ol > li.comment > ol > li.comment > ol > li.comment,
ul#post-list > li.post > ul.children > li.post > ul.children > li.post > ul.children > li.post > ul.children > li.post > ul.children > li.post,
li.comment.depth-6
{
    border-left: 1px solid #FBC02D !important;
}
/* level 7 */
#commentlist > li.comment > ol > li.comment > ol > li.comment > ol > li.comment > ol > li.comment > ol > li.comment > ol > li.comment,
ul#post-list > li.post > ul.children > li.post > ul.children > li.post > ul.children > li.post > ul.children > li.post > ul.children > li.post > ul.children > li.post,
li.comment.depth-7
{
    border-left: 1px solid #43A047 !important;
}

/* Avatars. */
.comment-content, .comment-author.vcard, .comment-meta
{
    padding-left: 15px;
}
.comment-author-avatar
{
    display: none;
}
}

@-moz-document domain("askamanager.org") {
/* Remove existing stuff */
article.comment
{
    border: 0 !important;
}
}

@-moz-document domain("captainawkward.com") {
/* Captain Awkward specifically */
article.comment
{
    padding-left: 5px;
}

}

@-moz-document domain("dumbingofage.com") {
/* DOA specifically */
img.avatar, div.comment-avatar
{
    display: none;
}
div.comment-content
{
    margin-left: 2px;
}
div.comment-author.vcard
{
    padding-left: 0;
}
div.comment-text
{
    padding-left: 0;
    margin-left: -5px;
}
li.comment > ul.children > li.comment
{
    margin: 10px 0 0 40px;
}
}

@-moz-document domain("reddit.com") {
/* Reddit specifically. */

/* level 1 */
.jDSCcP > div:nth-child(1) .threadline,
.jDSCcP > div:nth-child(8) .threadline {
    border-color: #1565C0;
}

/* level 2 */
.jDSCcP > div:nth-child(2) .threadline,
.jDSCcP > div:nth-child(9) .threadline {
    border-color: #4527A0;
}

/* level 3 */
.jDSCcP > div:nth-child(3) .threadline,
.jDSCcP > div:nth-child(10) .threadline {
    border-color: #AA00FF;
}

/* level 4 */
.jDSCcP > div:nth-child(4) .threadline {
    border-color: #C62828;
}

/* level 5 */
.jDSCcP > div:nth-child(5) .threadline {
    border-color: #FF8F00;
}

/* level 6 */
.jDSCcP > div:nth-child(6) .threadline {
    border-color: #FBC02D;
}

/* level 7 */
.jDSCcP > div:nth-child(7) .threadline {
    border-color: #43A047;
}
blockquote {
    border-left-width: 2px !important;
    border-image: linear-gradient(to bottom, #63A4EE, #8467DA, #D37AFF, #E68484, #FFC67A, #FDE4A5, #90D093) 1 100%;
}
}

@-moz-document domain("reddit.com"), domain("metafilter.com"), domain("arstechnica.com") {
/* Blockquotes */

blockquote,
.quotetitle,
.quotecontent
{
    border-left-width: 2px !important;
    border-image: linear-gradient(to bottom, #63A4EE, #8467DA, #D37AFF, #E68484, #FFC67A, #FDE4A5, #90D093) 1 100%;
}

}
