/*-----gray-----*/ 
body , .feed_dropdown--darker_background {
    background-color: #333333;
}

.song_body , .annotation_sidebar_unit , .annotation_sidebar_arrow:before , .horizontal_section--white_background , .square_input , .quick_search--header , .mini_card , .full_width_button , .white_container , .mini_article_card {
        background-color: #444;
}

.mini_article_card-thumbnail:before {
    background: linear-gradient(to right,#434343,transparent);
}

.quick_search--header {
    box-shadow: 0 0 3px #000000 inset;
}

.bordered_media_container {
    background-color: #434343;
    border: .15rem solid #5a5a5a;
}

.question--bottom_border, .question--top_border {
    border-color: #5a5a5a;
}

.metadata_unit--table_row {
    border-top: 1px solid #5A5A5A;
}

.chart_row , .metadata_with_icon-link , .feed_dropdown-header--bottom_border {
    border-bottom: 1px solid #5a5a5a ;
}

.square_input {
    border: .15rem solid #5a5a5a;
}

.tooltip {
    background: #444;
    border: .15rem solid #5a5a5a;
}

.chart_row--large_border {
    border-top: .15rem solid #fff;
}

.chart_row-number_container-number--square {
    border: .15rem solid #fff;
}

.text_label--button.text_label--button {
    color: #fff;
    border-color: #fff;
}

.chart_row:hover , .mini_card:not(.mini_card--transparent):hover , .full_width_button:hover , .vertical_album_card:hover , .mini_article_card:hover {
    background-color: #565656;
}

.home_featured_latest_story-title:hover {
    color: #99a7ee;
}

.rich_text_formatting blockquote:before {
    color:#bfbfbf;
}

.voting-button , .voting-total , .square_button--transparent , .square_button--facebook , .square_button--twitter {
    border-color: transparent !important;
}

.windows .gray_container {
    background-color:rgba(97, 97, 97, 0.35);
}

.footer--secondary a {
    color: #99a7ee !important;
}

.song_media_controls-controls--docked {
    background: linear-gradient(to bottom, #444 80%,rgba(255,255,255,0));
}

/*-----lyrics selection-----*/
.windows .referent:not(.referent--green):not(.referent--red):not(.referent--highlighted):not(.referent--image) {
    background-color: #5f5f5f;
    box-shadow: 0.01em 0 0 #5f5f5f, -0.01em 0 0 #5f5f5f;
}

.referent--yellow.referent--highlighted {
    color: #333333;
}

