css:
@-moz-document domain("facebook.com")
{
    ::selection{
	    background: #49BCAB;
	    color: #fff;
        }
        
    body.fbIndex { 
        background: url('https://images.unsplash.com/photo-1501696461415-6bd6660c6742?ixlib=rb-0.3.5&ixid=eyJhcHBfaWQiOjEyMDd9&s=defe121eb958a24b9e2c4f2041897339&auto=format&fit=crop&w=1336&q=80')right center fixed !important; 
        -webkit-background-size: cover; 
        -moz-background-size: 100% !important; 
        background-repeat: no-repeat !important;
        background-size: cover !important;
        }

    .loggedout_menubar_container { background: transparent !important; }
    .fbIndex #globalContainer #dropmenu_container,
    .fbIndex #globalContainer #content,
    .fbIndex #globalContainer #pageFooter { display: none !important }


    .fbIndex .loggedout_menubar_container {
        position: fixed !important;
        width: 420px !important;
        height: 82px !important;
        min-width: 0 !important;
        z-index: -1 !important;
        bottom:1%;
    }

    #facebook ._-kb a{
        color:#888d91 !important;
        font-size: 8px !important;
        letter-spacing: 2px;
        text-transform:uppercase;
    }
	
	#facebook time > span{
        color:#888d91 !important;
        font-size: 6px !important;
        letter-spacing: 2px;
        text-transform:uppercase;
    }

	#facebook ._-kb span >._1vp5{
		color:#fff !important;
        font-size: 8px !important;
        letter-spacing: 2px;
        text-transform:uppercase !important;
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif

    }
	#facebook ._-kb span{
        color:#fff !important;
        font-family: Muli, sans-serif; 
    }
	
	#facebook ._-kb p{
        color:#fff !important;
        font-family: Muli, sans-serif; 
    }

	.menu_login_container .html7magic label{
        color:#fff !important;
        font-size: 10px !important;
    }

	._3l-q{
		background-color: #2c2c36 !important;
    }
	._44bj{
		color:#fff !important;
    }

    .fbIndex .loggedout_menubar { width: auto !important }
    .fbIndex .loggedout_menubar_container .lfloat,
    .fbIndex .loggedout_menubar_container .rfloat { float: none !important }
    .fbIndex .loggedout_menubar_container .lfloat img,
    .fbIndex .loggedout_menubar_container .rfloat #login_form table { display: block !important; margin: 0 auto !important }

    .fbIndex .loggedout_menubar_container .lfloat img { display: block; margin: -60px auto 20px !important; }

    ._5a-- { display: none !important }


    #SetAsHomepage_Callout {
        display: none;
    }

    div._53jh {
        background: transparent !important;
        border-bottom: none !important;
        min-height: 42px !important;
    }

    i.fb_logo.img{
	    display:none;
    }
 
    body.UIPage_LoggedOut.fbIndex input, .UIPage_LoggedOut.fbIndex select{
	    background-color: transparent !important;
        color: #fff !important;
        border: 1px solid rgb(255,255,255) !important;
        font-size: 10px !important;
    }
    label#loginbutton > input{
	    border: none !important;
    }
    label#loginbutton{
	    display:none;
    }
    input{
	    background: transparent !important;
    }
}
