css:
@-moz-document domain("fdb.pl"), domain("www.fdb.pl") {
/* -------------------------------------------------- */
/* -------------------------------------------------- */
/*               fdb.pl - ciemny motyw                */
/*      Autor: CML99 (https://fdb.pl/users/cml99)     */
/*                      1.08.2018                     */
/* -------------------------------------------------- */
/* -------------------------------------------------- */

* {
    font-family: Arial;
}

.content {
    background-color: #111 !important;
    color: #aaa !important;    
}

.section {
    color: #ccc !important;        
}

.btn-xs {
    background-color: #222 !important;
    color: #aaa !important;
    border-color: #666 !important;
    font-weight: normal !important;
}
.btn-xs:hover {
    color: #ff871e !important;
    border-color: #ff871e !important;
}

body {
    background-color: #111 !important;
    border-color: #666 !important;    
    color: #ccc !important;
}

a {
    color: #ccc !important;
}
a:hover {
    color: #ff871e !important;
}

#query {
    background: #333 !important;
    border-color: #666 !important;
    color: #aaa !important;
}

.navbar-nav > li > a.navbar-featured:hover, .nav-link.text-primary.font-weight-bold:hover {
    color: #fff !important;
}
.navbar-nav > li > a.navbar-featured, .nav-link.text-primary.font-weight-bold {
    color: #ff871e !important;
}

.navbar-brand:hover {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}

.dropdown-menu {
    background-color: #333 !important;
    border-color: #666 !important;
}
.dropdown-menu > li > a {
    color: #ccc !important;
}
.dropdown-menu > li > a:hover {
    color: #ff871e !important;
    background-color: #222 !important;    
}
.dropdown-menu .divider {
    background-color: #666 !important;
}
.dropdown-item:hover {
    background-color: #222 !important;        
}

.media-bubble .media-top, #legacy .media-top {
    background-color: #333 !important;
    border-color: #666 !important;
    color: #888;
}

.media-list-bordered .media {
    border-color: #666 !important;
}

.media-bubble .media-comments, #legacy .media-comments {
    border-color: #666 !important;
    background-color: #222 !important;
    font-weight: normal !important;
}

.media-bubble-container {
    border-color: #666 !important;
    background-color: #222 !important;
}

.media-heading {
    border-color: #666 !important;
}

.media-bubble .media-bubble-container .arrow, #legacy .media-bubble-container .arrow {
    border-right-color: #666 !important;
}

code {
    color: #ff871e;
    background-color: #333 !important;
}

#legacy #container {
    background-color: #222 !important;
    color: #ccc !important;
}

#legacy .forum {
    border-color: #666 !important;
    background-color: #333 !important;
    font-weight: normal !important;
}

h1, h2, h3, h4, h5, h6, h7 {
    color: #ccc !important;
}

#legacy .posts .header, #legacy .post .content, #legacy .topic, #legacy .post-content blockquote, #legacy .pagination em, #legacy .pagination a, #legacy .pagination span, .list-group-item {
    border-color: #666 !important;
}

#legacy strong {
    color: #bbb !important;
}

.bg-warning {
    background-color: #ff871e !important;
    color: #000 !important;
}
.alert-link {
    color: #000 !important;
}
.alert-link:hover {
    color: #000 !important;    
    text-decoration: underline !important;
}

.form-control {
    border-color: #666 !important;
    background-color: #333 !important;
    color: #aaa !important;
}

#legacy #posts-paginate {
    border-color: #666 !important;
    background: #333 url(https://forum.fdb.pl/assets/icons/arrow-up-fd40acf1c12516dae28fda394d92314b3f5619f3c60d102e8c8274b76d0c6a78.png) no-repeat 5px 50% !important;
}

.btn-default {
    border-color: #666 !important;
    background-color: #333 !important;    
    color: #aaa !important;    
}
.btn-default:hover {
    border-color: #ff871e !important;
    background-color: #333 !important;    
    color: #ff871e !important;    
}

.quiz-option {
    background-color: #333 !important;
}

#legacy .pagination .current {
    color: #ff871e !important;
}

.btn.d-block.btn-sm.btn-light {
    background-color: #333;
    border-color: #666;
}
.btn.d-block.btn-sm.btn-light:hover {
    background-color: #222;
}

.d-none {
    color: #ddd !important;
}

.list-group-item {
    background-color: #333 !important;
    border-color: #666;
}

.btn.btn-light.btn-sm.mt-1 {
    background-color: #222 !important;
    color: #aaa !important;
    border-color: #666;    
}
.btn.btn-light.btn-sm.mt-1:hover {
    color: #ff871e !important;
    border-color: #ff871e;    
}

.bg-light {
    background-color: #222 !important;
}

.btn.btn-primary.btn-sm {
    color: #000 !important;
}

.badge {
    background-color: #000 !important;
}

.pager li a {
    background-color: #333 !important;
    border-color: #666 !important;
}

.pagination > li a {
    background-color: #333 !important;
    border-color: #666 !important;    
}
.pagination > li a:hover {
    background-color: #222 !important;   
}

.pagination .page-item.active a {
    background-color: #ff871e !important;
    color: #000 !important;
}

/* .svg-inline--fa.fa-w-18:hover, .svg-inline--fa.fa-w-16:hover, .svg-inline--fa.fa-w-14:hover, */ 
.svg-inline--fa:hover {
    color: #ff871e !important;
}

.text-light a:hover {
    color: #ff871e !important;
}

.input-group-addon {
    border-color: #666 !important;
    background-color: #222 !important;
}

.nav-bordered > li > a {
    border-color: #666 !important;
}
.nav-justified > li a:hover {
    background-color: #333 !important;
}
.nav-bordered > .active > a {
    background-color: #333 !important;
}

.nav-tabs {
    border-color: #666 !important;
}
.nav-tabs > li.active > a {
    background-color: #333 !important;
}
.nav-tabs > li.open > a {
    background-color: #333 !important;
}

.btn-primary {
    color: #000 !important;
}
.btn-primary:hover {
    color: #fff !important;
}

.btn-facebook.btn-lg:hover {
    color: #fff !important;
}

hr {
    border-color: #666 !important;
}

.select2-selection {
    background: #333 !important;
    border-color: #666 !important;
    color: #aaa !important;
}

.select2-selection__rendered {
    color: #aaa !important;
}

.select2-container--bootstrap .select2-dropdown {
    border-color: #666 !important;
}

.select2-search--dropdown, .select2-results__options {
    background-color: #333 !important;
}

.select2-search__field {
    background-color: #333 !important;    
    color: #aaa !important;
    border-color: #666 !important;
}

.select2-container--bootstrap .select2-results__option[aria-selected="true"] {
    background-color: #333 !important;
    color: #ccc !important;
}
.select2-container--bootstrap .select2-results__option[aria-selected="true"]:hover {
    background-color: #ff871e !important;
    color: #fff !important;
}

.bg-success {
    background-color: #333 !important;
}

.block.p-a-1.bg-faded.normal {
    background-color: #333 !important;
}

.text-success {
    color: #5ed261 !important;
}
.bg-success {
    background-color: #333 !important;    
    box-shadow: 0px 0px 0px 1px #3c763d inset !important;
}

.text-danger {
    color: #ff5b57;
}
.bg-danger {
    background-color: #333 !important;
    box-shadow: 0px 0px 0px 1px #a94442 inset !important;    
}

.bg-faded.p-a-1, .bg-faded.p-a-2  {
    background-color: #333 !important;
}

#soulmate li.focus a .text-muted {
    color: #aaa !important;
}

.popover-content {
    background-color: #333 !important;
    border-color: #666 !important;
}

.alert-warning {
    background-color: #333 !important;
    border-color: #666 !important;
    color: #ffcb73 !important;
}

.pointer th {
    background-color: #333 !important;
    border-color: #666 !important;
}

.btn-vote-widget {
    color: #333 !important;
}

#content-form table tr, #content-form table td, #content-form .invalid {
    background-color: #222 !important;
    border-color: #666 !important;
}

.modal-content, .modal-header, .modal-body, .modal-footer {
    background-color: #333 !important;
    border-color: #666 !important;
}

.close {
    color: #fff !important;
}

td, th, tr {
    border-color: #666 !important;
}

.btn-outline-primary, .btn-primary-outline {
    background-color: #222 !important;
}

.thumbnail-overlay-caption {
    background-color: #333 !important;
}

.well-bordered {
    border-color: #666 !important;    
}

.well {
    background-color: #222 !important;
}

.bg-faded.p-a-05 {
    background-color: #222 !important;
}

.p-x-3 {
    background-color: #333 !important;
}

.label-primary {
    color: #fff !important;
}
.label-primary:hover {
    color: #000 !important;
}

.label-success {
    color: #fff !important;
    background-color: #4c8c4c !important;
}

.tiles .tile .tile-title-background {
    color: #fff !important;
}
.tiles .tile .tile-title-background:hover {
    color: #ff871e !important;
}

.adaptive-image-container img {
    transition: transform .5s;
}
.adaptive-image-container img:hover {
    transform: scale(1.25);
}

#post_content {
    background-color: #333 !important;
    border-color: #666 !important;
    border-style: solid !important;
    color: #ccc !important;
}

.submit {
    background-color: #333 !important;
    color: #ccc !important;
    border-color: #666 !important;
    border-style: solid !important;
    border-radius: 2px !important;
}
.submit:hover {
    background-color: #333 !important;
    color: #ff871e !important;
    border-color: #ff871e !important;
    border-style: solid !important;
    border-radius: 2px !important;
}

.spinner .spinner-content {
    color: #ccc !important;
    background-color: #222 !important;
}

.well-md.bg-warning.m-b-1.text-center.hidden-xs a {
    color: #000 !important;
}

.btn.btn-lg.btn-primary.p-x-3 {
    background-color: #ff871e !important;
    color: #fff !important;
    border-color: #ff871e !important;
}
.btn.btn-lg.btn-primary.p-x-3:hover {
    color: #000 !important;
}

.svg-inline--fa.fa-w-16 {
    color: #aaa !important;
}
}
