css:
@-moz-document url-prefix("https://www.gamingonlinux.com/") {


:root
{
	--bkgd0: #312020 !important;
	

}  
html, #newtab-window
  {
    background: var(--bkgd0)  !important;
  }



  *
  {
   
      background-color: rgb(0, 0, 0) !important;
       
  }

  
  
a, a
  {
    color: #964005 !important;
   
  }

  a:hover,  a:visited:hover,  span[onclick]:hover, div[onclick]:hover,   [role="menuitem"]:hover
  {
    color: #e93d3d !important;
    text-shadow: 0 0 5px rgba(149, 22, 58, .9) !important;
  }

  a:visited
  {
    color: #a4a4a4 !important;
  }

  a.highlight, a.active,  .selected,  [href="#"]
  {
    color: #DDD !important;
    font-weight: bold !important;
  }
/*Import Reset */
  body
  {
    background: #26292c;
    color: #b3aa98;
  }

/*/* Header */
  #wrapper #header
  {
    background: #26292c;
    height: 65px;
  }

  #wrapper #header #logo-res a
  {
    color: #079cb4;
    float: left;
  }

  #navigation ul li > a
  {
    border-left: 1px solid #26292c;
    border-right: 1px solid #26292c;
    color: #079cb4;
    display: inherit;
    float: left;
    font-size: 18px;
    font-weight: bold;
    margin-left: -1px;
    padding: 21px;
    white-space: nowrap;
  }



/* Articles */
  .article
  {
    background: #26292c;
    margin: 0 0 9px 0;
  }

  .box .body
  {
    background: #26292c;
    padding: 10px;
  }

  .box .body.list ul li
  {
    border-bottom: 1px solid #5a5c5d;
    margin: 0;
    padding: 10px;
  }

  .comment .comment-meta
  {
    background: #37383a;
    border-top: 1px solid #26292c;
    border-bottom: 1px solid #26292c;
    padding: 7px 14px;
    margin-bottom: 7px;
    position: relative;
  }

  .comment .comment-meta .comment-actions li a
  {
    background: #818488;
    float: left;
    padding: 5px;
    -webkit-border-radius: 3px;
    -khtml-border-radius: 3px;
    -moz-border-radius: 3px;
    -o-border-radius: 3px;
    border-radius: 3px;
  }

  .comment .comment-social li a
  {
    background: #079cb4;
    padding: 3px;
    margin-right: 3px;
    margin-bottom: 3px;
    -webkit-border-radius: 3px;
    -khtml-border-radius: 3px;
    -moz-border-radius: 3px;
    -o-border-radius: 3px;
    border-radius: 3px;
    display: inline-block;
  }

  div.announce:nth-of-type(odd)
  {
    background: #26292c;
    margin-bottom: 7px;
    color: #fff;
  }

  div.announce:nth-of-type(even)
  {
    background: #26292c;
    margin-bottom: 7px;
    color: #fff;
  }

  .dark div.announce:nth-of-type(odd)
  {
    background: #609FA9;
    margin-bottom: 7px;
    color: #000;
  }

  .dark div.announce:nth-of-type(even)
  {
    background: #99CCFF;
    margin-bottom: 7px;
    color: #000;
  }

  .announce a
  {
    color: #323232;
    font-style: italic;
  }

  .announce a:link
  {
    color: #ff0000;
    font-style: italic;
  }

  .dark .announce a
  {
    color: #191919;
    font-style: italic;
  }

  .dark .announce a:link
  {
    color: #191919;
    font-style: italic;
  }

  .dark svg.svggraph rect
  {
    fill: #000;
    stroke: grey;
  }

  svg.svggraph text
  {
    font-family: "Open Sans", "Source Sans Pro", "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, sans-serif;
  }

  .dark svg.svggraph text
  {
    fill: #eee;
    font-family: "Open Sans", "Source Sans Pro", "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, sans-serif;
  }

  .dark svg.svggraph path
  {
    stroke: grey;
  }

  .dark .breadcrumb
  {
    background: #2E2E2E;
  }

  .dark .collapse_header
  {
    background: #000;
  }

  .vote_done
  {
    color: white;
    background-color: green;
  }

  .dark .vote_done
  {
    color: white;
    background-color: green;
  }

  .dark #wrap strong
  {
    color: black;
  }

  .patreon
  {
    margin-top: 14px;
    line-height: 30px;
  }

  .preview_pm
  {
    display: none;
  }






/* Footer */
  #footer
  {
    background: #26292c;
    color: #e6db74;
    overflow: hidden;
    clear: both;
  }

  #footer #social ul li
  {
    background: #26292c;
    float: left;
    margin-right: 1px;
    margin-bottom: 1px;
    padding: 0;
  }

  #footer #about a
  {
    color: #e6db74;
  }

  .box .head
  {
    background: #37383a;
    color: #079cb4;
    font-weight: bold;
    padding: 7px 10px;
  }

  .article.individual.no-image .info
  {
    padding: 0px 0px 27px 170px;
    background-repeat: no-repeat;
    background-position: left bottom;
    background-color: #838383;
    border: none;
    min-height: 150px;
  }

  .article.individual.no-image .info .bubble
  {
    background: #4f5045;
    padding: 7px;
    -webkit-border-radius: 3px;
    -khtml-border-radius: 3px;
    -moz-border-radius: 3px;
    -ms-border-radius: 3px;
    -o-border-radius: 3px;
    border-radius: 3px;
  }

  .article .info .desc
  {
    border-top: 1px solid #5a5c5d;
    border-bottom: 1px solid #5a5c5d;
    padding: 7px 0;
    margin: 7px 0;
  }
}
