css:
@-moz-document domain('dynasty-scans.com')
{
body {
    background: #111!important;
    color: #e8e8e8!important;
}

div#content, .popover-content, .span4 a, .toolbar {
    background: #383838!important;
    color: #d9d7d7!important;
}

a, b, .doujins {
    color: #eeeeee!important;
    text-decoration: none!important;
}
/*forum links*/
p a {
    color: #007aff!important;
}

p a:visited {
    color: #a849ff!important;
}

.forum_topic .bulleted {
    font-size: 12px;
}

/**/

.caption br {
    line-height: 25px!important;
}

.caption {
    color: #999!important;
}
.alert-info, #news b, #news a, .views_count b, .posts_count b, .views_count, .posts_count {
    color: black!important;
}

/*quotes*/
blockquote {
    background: #2d2d2d!important;
    border-left: 10px solid #ccc;
    padding: 0px 0px 0px 10px;
}

blockquote p {
    color: #999;
    padding-bottom: 5px;
    padding-top: 5px;
}
/**/

a:hover, div.title:hover a.subject, .thumbnail:hover div.title {
    text-decoration:underline!important;
}

dt {
    font-size: 16px;
    color: white;
    text-decoration: underline;
}

.tag-list a:visited, a.name:visited, a.subject:visited {
    color: #999!important;
    text-decoration: underline!important;
}

dd {
    line-height: 20px;
}

.tag-list a:visited:hover, .tag-list a:hover, a.subject:hover, .table-link:hover {
    border-radius: 15px;
    padding: 0px 10px 0px 10px;
    -moz-transition: .05s ease;
    -webkit-transition: .05s ease;
    -o-transition: .05s ease;
}

.tags .chapter-list dd:hover, .added .chapter-list dd:hover{
    background:#004C99!important;
    border-radius: 15px;
    padding: 0px 0px 0px 30px!important;
    -moz-transition: .05s ease;
    -webkit-transition: .05s ease;
    -o-transition: .05s ease;
}


/*tabs and pills*/
/*chapters, groupings, oneshots*/
.nav-tabs a {
    background: #383838!important;
}

.nav-tabs li a {
    border-bottom: 0px solid white;
    display: inline-block;
    margin-right: 1px;
}

.nav-tabs li:active {
    background: #383838!important;
}

.nav-tabs li:hover {
    padding-bottom: 1px;
    padding-left: 1px;
    padding-top: 1px;
    display: inline-block;
    margin-right: 1px;
}

.nav-tabs li.active:hover {
    padding: 0px;
    display: inline-block;
    margin-right: 0px;
}
/**/
/*Pagination*/
.pagination a {
    background: #383838!important;
}

.pagination li.active a, .nav-pills li.active:hover a, .pagination li.active:hover a{
    background: #003366!important;
}

.pagination li:hover a {
    background: rgb(0,76,153)!important;
    color: #d7d7d7!important;
    text-decoration: none!important;
}
/**/
/*homepage and thumbnails*/
.span2 a.thumbnail {
    display: inline-block;
    margin: 0px;
    border: 1px solid white!important;
}

.span2 .thumbnail:hover {
    border: 1px solid #003366!important;
    background: #383838!important;
}

.chapter:hover, .nav a, #reader .page {
    text-decoration: none!important;
}

/*buttons*/
.label, .btn-small, .btn-mini, .btn{
    background: #d7d7d7!important;
    color: black!important;
}

.btn-small:hover {
    display: inline-block;
    margin-left: 2px!important;
    padding-bottom: 3px;
    padding-top: 3px;
    padding-left: 13px;
}

.btn-group .btn-small:hover {
    display: inline-block;
    margin-left: -2px!important;
    
}

a.btn-small.active {
    background: #aaa!important;
    pointer-events: none;
}

.btn-toolbar .btn:hover, #new_forum_post .btn:hover, .btn-primary:hover {
    display: inline-block;
    margin-left: 0px!important;
    padding: 5px 14px 5px 12px; 
}

.btn-mini:hover {
    display: inline-block!important;
    padding: 1px 7px 1px 7px!important;
    margin: -1px;
}

#resize.btn.btn-mini:hover {
    padding: 1px 8px 1px 7px!important;
}

.span2 .btn-mini:hover {
    background: #aaa!important;
    color: black!important;
    margin: 0px;
}

#image-actions .btn-mini:hover {
    margin: 0px;
}

#tag-actions .btn-mini:hover {
    display:inline-block;
    margin: 0px 0px 0px -1px;
}

.label:hover {
    background: #aaa!important;
    text-decoration: none!important;
}


#toggle_images_search:hover, .btn:hover, .dropdown-menu:hover li a:hover, .nav-list a:hover, .dropdown-toggle:hover{
    background: rgb(0,76,153)!important;
    color: #d7d7d7!important;
    text-decoration: none!important;
    border: none;
}

.nav-tabs li:hover a, .nav-pills li:hover a {
    background: rgb(0,76,153)!important;
    color: #d7d7d7!important;
    text-decoration: none!important;
    border: none;
}


/*important tags*/
.label-important:hover, .label-info:hover {
    background: #d7d7d7!important;
    color: black!important;
}

#image.thumbnail {
    background: #111!important;
    border: none;
}

.media.thumbnail, .open .dropdown-menu {
    background: #383838!important;
}

.title {
    color: white!important;
}

/*rows*/
.forum_topic:nth-child(odd), tbody tr:nth-child(odd) td, .forum_post .info, .form-actions {
    background-color: #2d2d2d!important;
}

tbody tr:nth-child(even) {
    background: #383838!important;
    border-bottom: 1px solid white!important;
}

tr:hover td, tr:nth-child(odd):hover td {
    background: rgb(0,76,153)!important;
}

}
